#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 3, -3);
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.xz *= Rot(-m.x*6.2831);
    return ro;
}

float GetDist(vec3 p) {
    float d = length(p + 0.*thc(2., (p.x + p.y + p.z) + iTime)) - 1.5;

    float pd =min( abs(p.x-p.y), abs(p.z - p.x));
    pd = min(pd, abs(p.y-p.z));

    p = erot(p, normalize(vec3(1)), -0.8 * iTime);
    float e = 2. * pi / 3.;
    p = erot(p, normalize(2. - cos(iTime + (0.5 + 0.5 * thc(4.,0.25 * iTime)) * vec3(0,e,-e))*p), pi + 0. * iTime);
    //p *= 1.5 + 0.5 * cos(iTime);
    pd = abs(p.x + p.y + p.z)-0.1;
    float a = 2. * pi / 6.;
  //  p.xy *= Rot(a);
   // p.yz *= Rot(a);
   // p.zx *= Rot(a);

    float sc = 16.;
    float o = 1. * (p.y + p.x + p.z);
    float d2 = cos(sc * (p.x - p.y) + o)
             + cos(sc * (p.y - p.z) + o)
             + cos(sc * (p.z - p.x) + o);
    //d += 0.1 * d2;
    //d = 0.1 * d2 + pd;//0.1* pd * pd;
    d = max(pd + smoothstep(0.,1., 3.5-dot(p,p)) * 0.12 * d2, length(p) - 2.);
    return 0.1 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    if(d<MAX_DIST) {
        vec3 p = ro + rd * d;
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(dif);

        vec3 e = vec3(0.5);
        col *= pal(length(p) * 0.25 + 1.2,e,e,e,0.5*vec3(0,1,2)/3.);

        col += 0.01 * (n-1.);

        float fresnel = pow(1.+dot(rdIn, nExit), 5.);
        //col += fresnel;
        float fresnel2 = pow(1.+dot(rd, n), 5.); 
       // col *= 0.5 + 0.5 * fresnel2;
    }

    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

