mat4 rotX(float angle)
{
    mat4 rotMatrix = mat4
    (
        vec4( 1.0,        0.0,        0.0, 0.0),
        vec4( 0.0, cos(angle), sin(angle), 0.0),
        vec4( 0.0,-sin(angle), cos(angle), 0.0),
        vec4( 0.0,        0.0,        0.0, 1.0)
    );

    return rotMatrix; 
}

