/*
SHADEF**K

a (barely) esoteric language for shaders

You're given a few basic commands, a pointer, and a single memory.

U,D,L,R: Moves pointer by one pixel up,down,left,right respetively.

INC,DEC: Increment/Decrement cell by one.
RED: Read value at current cell.

MEM: shortcut for the memory value.
MS: sets memory to a specific value.

C(a): will only work if the given value is equal to the memory

RE: will repeat as much as the value in memory, you can use I if you want.

B: will exit the repeat.

good luck :)
*/

#define U ptr.y++;
#define D ptr.y--;
#define R ptr.x++;
#define L ptr.x--;
#define INC if(ivec2(POS) == ptr) {COL += vec4(vec3(1.0/255.0),0.0);}
#define DEC if(ivec2(POS) == ptr) {COL -= vec4(vec3(1.0/255.0),0.0);}
#define RED memory = int(COL.r*255.0);
#define MEM memory
#define MS(a) memory = a;
#define C(a) if(memory == a)
#define RE for(int i = 0;i<memory;i++)
#define B break;

void mainImage( out vec4 COL, in vec2 POS )
{
    int memory;
    POS /= 10.0;
    ivec2 ptr = ivec2(iResolution.xy)/20;
    COL = vec4(0.0);

    //CODE GOES HERE:

    MS(10) RE{
        MS(10) RE{
            MS(255)
            RE { INC }
            D
            MS(10)
        }
        RE { U }
        L
    }
}

#define PI 3.14159265359
#define Amplitude 0.21
#define Wavelength 4.0
#define Speed 0.31
#define Warm_c vec3(1.0,0.21,0.07)
#define Hot_c vec3(0.9,1.0,0.1)

float random (vec2 uv) 
{
    return fract(sin(dot(uv.xy,vec2(12.9898,78.233)))*43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy,
          ps = 1. / iResolution.xy;


    uv.xy += vec2(-0.5, 0.3);
    uv = abs(uv);
    uv.x *= 5.;

    float K = 14. * PI / Wavelength;
    uv.x += cos(uv.y + iTime + abs(random(uv) * 0.09)) * 0.6;

    uv.x -=  Amplitude                         * sin(K * (uv.y  - Speed * (iTime * 4.1)));
    uv.y += (Amplitude     *(random(uv) * 0.6))  * cos(K * (uv.x  + Speed * (iTime * 8.1)));
    uv.x -= (Amplitude * 1. *(random(uv) * 0.7))  * sin(K * (uv.y  - Speed * (iTime * 2.1)));
    uv.x *= uv.y;   

    float d = 0.0;                
    d = length(abs(uv)-.5);
    float corner = 1. - floor(d * 7.) * d;
    d = fract(d * 7.);

    vec3 color = mix(Warm_c,Hot_c,corner);

    fragColor =  vec4(color + corner , 1);
}

#define VIG 0.4
#define IPO 0.1

vec3 tex2D(sampler2D _tex, vec2 _p) {
  vec3 col = texture(_tex, _p).xyz;
  if (0.5 < abs(_p.x - 0.5)) {
    col = vec3(0.1);
  }
  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

  vec2 uv = fragCoord / iResolution.xy;
  vec3 col = tex2D(iChannel0, uv);
  vec3 vig = vec3(1);

  float b = 1. - VIG;

  vig -= vec3(smoothstep(VIG,0.0, uv.x) + IPO);
  vig -= vec3(smoothstep(b,1.0, uv.x) + IPO);

  vig -= vec3(smoothstep(VIG,0.0, uv.y) + IPO);
  vig -= vec3(smoothstep(b,1.0, uv.y) + IPO);

  col *= vig;
  fragColor = vec4(col, 1.0);
}

