//1498 unique stars made of 7500 stitches, well 4/5ths of 1498 anyway
#define DIV 1499.
//50 stitches per frame
#define PTS 50.
#define R iResolution

const float pi2=6.283185;
float rand(vec2 U){return fract(sin(dot(U,vec2(113.,117.)))*412.143);}
vec4 shad(vec2 U){U/=R.xy;vec2 su=sin(U*5.);return vec4(.8)+vec4(.4,.2,.1,0.)*sin((U.x+U.y*1.6)*5.+pi2*su.x*su.y);}
vec4 BG(vec2 U){//background texture
  vec4 c=vec4(0.01,.03,.06,.0)*(sin(mod(U.x,pi2))*sin(mod(U.y,pi2))+1.)*(0.5+0.5*rand(U));
  return c*shad(U);
}
float tube(vec2 pa, vec2 ba){return length(pa-ba*clamp(dot(pa,ba)/dot(ba,ba),.25,.75));}
vec3 cmap(float a){return abs(vec3(sin(a),sin(a+1.),sin(a+1.7)));}
void mainImage(out vec4 O, in vec2 U){
  vec2 u=floor(U);
  bool bLogic=(u.y<1. && u.x<3.);u=U;//3 state pixels
  float iF=max(0.,float(iFrame)-1.);//setup figure, start step
  float steps=ceil(DIV*5./PTS)*PTS,start=iF*PTS,fig=1.+floor(start/steps);
  start=mod(start,steps);
  //L0,L1 mnx,mxx,mny,mxy for centering and scaling, 2 xyxy last positions
  vec4 L0=texture(iChannel0,vec2(.5)/R.xy);//load logic
  vec4 L1=texture(iChannel0,vec2(1.5,.5)/R.xy);
  vec4 L2=texture(iChannel0,vec2(2.5,.5)/R.xy);
  if(iF==0.){//first frame
    O=BG(U);L0=vec4(-23,23,-23,23);L1=vec4(0);L2=vec4(0.,R.y*.2,0.,0.);
  }else if(start==0.){//new figure
    float s=-.5*R.y/(L1.w-L1.z);
    L2=vec4((L1.y+L1.x)*s,(L1.w+L1.z)*s,0.,0.);L0=L1;L1=vec4(0);
    O=((U.x>R.x/2.)&&(mod(fig,5.)!=1.)?texture(iChannel0,U/R.xy-vec2(.5,0.)):BG(U));
  }else O=texture(iChannel0,U/R.xy);
  if(mod(fig,5.)>.5){if(U.x>R.x/2.)return;U-=R.xy*vec2(.25,.5);}
  else U=(U-R.xy*.5)*1.5;
  float scl=.45*R.x/(L0.w-L0.z),a,b,b2;//scale the star to the screen
  vec2 oL=L2.xy;//previous point for line drawing
  for(float i=start;i<start+PTS;i+=1.){//formula: 2*pi*fig/DIV*(i*i*i+i/5)
    a=mod(i,DIV);  //since this is an angle multiple of 2pi
    a=mod(mod(i,DIV)*a,DIV);//you can use mod by the divisor to toss big values early
    a=mod(mod(i,DIV)*a,DIV);b2=a;//save for offscreen calc of next fig center, extents
    a=mod(fig*a,DIV);b=mod(fig*i,DIV*5.);
    b=pi2*fract((a+b*.2)/DIV);//now a+b (i*i*i+i/5) are not so far apart
    //b=pi2*fig*i/DIV*(i*i+.2);//uncomment to see how quickly floats break
    //b=pi2*fract(i*fract(fig*fract((i*i+.2)/DIV)));//still way off!
    L2.xy+=vec2(cos(b),sin(b))*scl;//now I want to integrate this sum but so far
    float d=tube(U-L2.xy,oL-L2.xy);//I can only get a spiral
    if(!bLogic)O.rgb=max(O.rgb*smoothstep(0.,2.5,d),cmap(pi2*i/DIV)*smoothstep(1.,0.,d)*shad(u).rgb);
    oL=L2.xy;//save last point
    if(bLogic){//next fig run offscreen to capture the center and extents
      b=mod((fig+1.)*i,DIV*5.);
      a=mod((fig+1.)*b2,DIV);
      a=pi2*fract((a+b*.2)/DIV);
      L2.zw+=vec2(cos(a),sin(a));
      L1.xz=min(L1.xz,L2.zw);
      L1.yw=max(L1.yw,L2.zw);
    }
  }
  if(bLogic){if(u.x==0.5)O=L0;else if(u.x==1.5)O=L1;else if(u.x==2.5)O=L2;}//save logic
}

