#define bps 4.
#define pi 3.14159
int N[16]=int[16](0,5,4,7,7,2,4,0,7,5,4,2,7,5,4,0);
float scale(float note){//throws out dissonant tones 
 float n2=mod(note,12.);
 if((n2==1.)||(n2==3.)||(n2==6.)||(n2==8.)||(n2==11.))note=-100.;
 return note;
}
// note number to frequency  from https://www.shadertoy.com/view/ldfSW2 
float ntof(int n0){
 float n=scale(float(n0));
 return (n>0.)?440.0 * pow(2.0, (float(n) - 67.0) / 12.0):0.0;
}
vec2 rot(vec2 v, float a) {return cos(a)*v+sin(a)*vec2(v.y,-v.x);}
vec2 I(int n, float t, vec3 p, vec4 e, vec4 d){
  float bt=t,t2,t3,t4,f=ntof(n);
  if(f==0.)return vec2(0);
  t-=t*d.y*sin((d.w*pi+t)*pi*d.z);t3=t*d.x;
  t2=fract(t*p.y*f);t4=fract(t3*p.y*f);
  t=fract(t*f);t3=fract(t3*f);
  float amp=abs(1.+cos(bt*e.z*pi)*e.w)*exp(-bt*e.y)*(1.0-exp(-bt*e.x))*min(1.,100.-bt*100.);
  return amp*rot(vec2(sin(pow(t,p.x)*pi*2.+pi*2.*p.z*sin(pow(t2,p.x)*pi*2.)),
    sin(pow(t3,p.x)*pi*2.+pi*2.*p.z*sin(pow(t4,p.x)*pi*2.))),float(n)+bt*pi*2.);
}
int pat(float bt){return int(bt)%(mod(bt,32.)<16.?8:16);}
vec2 mainSound(int samp, float time){
  float tim=time*bps;if(tim>128. && tim<256.)tim=tim-128.;
  float bt=floor(tim),t=fract(tim);t+=0.01*sin(pi*pow(t,15.));
  int n64=N[pat(bt/64.)]-(mod(bt/128.,2.)<1.?0:7);
  int n8=n64+N[pat(bt/8.)],n1=n8+N[pat(bt)],n2=n8+N[pat(bt/2.)];
  float fo=pow(1.-t,20.0);
  vec2 v=I(n1+48,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,1.0,6.,t*.8),vec4(1.014,0.005,1.,.5))*.5;
  t=fract(tim/2.);
  fo=pow(1.-t,20.0);
  v+=I(n2+48,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,1.,12.,t*.8),vec4(1.014,0.005,1.,.5));
  v+=I(n2+54,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,3.,18.1,t*.8),vec4(1.008,0.0051,1.,.5));
  t=fract(tim/8.);
  fo=pow(1.-t,20.0);
  v+=I(n8+48,t,vec3(1.-fo*.5,.5,fo),vec4(10.,1.0,24.,t*.4),vec4(1.004,0.005,1.,.5));
  v+=.5*I(n8+63,t,vec3(1.-fo*.5,.5,fo),vec4(10.,1.0,24.,t*.8),vec4(1.014,0.005,1.,.5));
  return v*.05;
}

