#define THIS_CHANNEL     iChannel1
#define CAMERA_CHANNEL   iChannel0

#define CALC_MARGINAL  0.05
#define PRINT_MARGINAL 0.15

/* Frame variables */
#define EDGE    0
#define AVERAGE 1
#define RADIUS  2

/* Frame varible macros */
#define SAVE(value)        fragColor = vec4(0.0, 0.0, 0.0, (value))
#define LOAD(variable, x)  texelFetch(THIS_CHANNEL, ivec2((x), variable), 0).a

/* Colors */
#define LINE_COLOR  vec4(1.0, 0.0, 0.0, 0.2)
#define GRAPH_COLOR vec4(0.0, 0.0, 1.0, 0.4)


#define MATRIX_SIZE 5
vec4 camera_value(float x, float y)
{
    int dx ,dy;
    vec4 sum = vec4(0.0);
    int count = 0;

    for (dx = 0; dx < MATRIX_SIZE; dx++) {
        for (dy = 0; dy < MATRIX_SIZE; dy++) {
            sum += texture(CAMERA_CHANNEL, vec2(x + (float(dx) + 0.5) / iResolution.x, y + (float(dy) + 0.5) / iResolution.y));
            count++;
        }
    }


    return sum / float(count);
}

#define EDGE_WINDOW_SIZE 8
float find_edge(int x_int)
{
    float diff;
    float x, y;
    int i;

    float pre_y;
    float post_y;

    vec3 pre_color_sum;
    vec3 post_color_sum;


    /* Calculate initial calues for sums */
    x = float(x_int) / iResolution.x;

    /* Calculate initial sums */
    pre_color_sum  = vec3(0.0);
    post_color_sum = vec3(0.0);
    for (i = EDGE_WINDOW_SIZE; i < EDGE_WINDOW_SIZE + EDGE_WINDOW_SIZE; i++) {
        vec4 color;

        /* Calculate pre average */
        pre_y = 1.0 - float(i + 1) / iResolution.y;
        color = camera_value(x, pre_y);
        pre_color_sum += color.rgb;

        /* Calculate post average */
        post_y = 1.0 + float(i) / iResolution.y;
        color = camera_value(x, post_y);
        post_color_sum += color.rgb;
    }

    /* Find out edge */
    int y_int;
    for (y_int = int(iResolution.y) - (EDGE_WINDOW_SIZE + 1); y_int >= 0 ; y_int--) {
        x = float(x_int) / iResolution.x;
        y = float(y_int) / iResolution.y;

        float edge_value;
        vec4 color;

        /* Update pre/post sums */ 
        pre_y = float(y_int) / iResolution.y;
        color = camera_value(x, pre_y);
        pre_color_sum  -= color.rgb;
        post_color_sum += color.rgb;

        pre_y = float(y_int) / iResolution.y - float(EDGE_WINDOW_SIZE) / iResolution.y;
        color = camera_value(x, pre_y);
        pre_color_sum += color.rgb;

        post_y = float(y_int) / iResolution.y + float(EDGE_WINDOW_SIZE) / iResolution.y;
        color = camera_value(x, post_y);
        post_color_sum -= color.rgb;


#if 0
        vec3 color_diff = abs(pre_color_sum - post_color_sum);
        float diff = color_diff.r + color_diff.g + color_diff.b;
#define DIFF_TRESHOLD (float(EDGE_WINDOW_SIZE) / 8.0)
        if (diff > DIFF_TRESHOLD) {
//            break;
        }

        if (color_diff.r > DIFF_TRESHOLD) {
            break;
        }
        if (color_diff.g > DIFF_TRESHOLD) {
            break;
        }
        if (color_diff.b > DIFF_TRESHOLD) {
            break;
        }
#else
        float pre_luma  = dot(pre_color_sum.rgb,  vec3(0.299, 0.587, 0.114));
        float post_luma = dot(post_color_sum.rgb, vec3(0.299, 0.587, 0.114));
        vec3 color_diff = abs(pre_color_sum - post_color_sum);
        float diff = abs(pre_luma - post_luma);

        if (diff > float(EDGE_WINDOW_SIZE) / 8.0) {
            break;
        }
#endif
    }

    return float(y_int);
}

#define AVERAGE_WINDOW int(iResolution.x / 8.0)
//#define AVERAGE_WINDOW 1
float calc_average(int x)
{
    int i;
    float sum = 0.0;
    int count  = 0;
    for (i = 0; i < AVERAGE_WINDOW; i += 1) {
        sum += LOAD(EDGE, x + (i - AVERAGE_WINDOW/2));
        count++;
    }

    /* Return average value */
    return sum / float(count);
}


#define RADIUS_WINDOW   int(iResolution.x / 12.0)
#define RADIUS_SPACING  int(iResolution.x / 40.0)
#define RADIUS_SCALE    2.0
float calc_radius(int x)
{
    vec2 first = vec2(float(x - RADIUS_WINDOW), LOAD(AVERAGE, x - RADIUS_WINDOW));
    vec2 mid   = vec2(float(x),                 LOAD(AVERAGE, x));
    vec2 last  = vec2(float(x + RADIUS_WINDOW), LOAD(AVERAGE, x + RADIUS_WINDOW));

    float result = 0.0;
    result += (first.y - mid.y) / (mid.x  - first.x);
    result += (last.y  - mid.y) / (last.x - mid.x);

    result = (last.x - first.x) / (result * 2.0);

    if (result < 0.0) {
        return -result * RADIUS_SCALE / iResolution.y;
    }

    return 10000.0;
}


#define CALC_FRAME_RATE 3
void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    ivec2 ifragCoord = ivec2(fragCoord - 0.5);
    vec2 uv = fragCoord.xy / iResolution.xy;

    float mouse_x = iMouse.x/iResolution.x;
    if (mouse_x > 0.5) {
        /* Keep unchanged */
        fragColor = texture(THIS_CHANNEL, uv);
        return;
    }

    if (uv.x > CALC_MARGINAL && uv.x < 1.0 - CALC_MARGINAL) {

        if (iFrame % CALC_FRAME_RATE == 0) {
            /* Findout border place */
            if (ifragCoord.y == EDGE) {
                float y;

                /* Detect border */
                y = find_edge(ifragCoord.x);

                /* Save border value */
                SAVE(y);

                return;
            } 

            /* Calculate averages */
            if (ifragCoord.y == AVERAGE) {
                float average;

                average = calc_average(ifragCoord.x);

                /* Save average value */
                SAVE(average);

                return;
            }

            /* Calculate radius */
            if (ifragCoord.y == RADIUS && (ifragCoord.x % RADIUS_SPACING) == 0) {
                #define RADIUS_AVERAGE_WINDOW int(iResolution.x / 80.0)
//                #define RADIUS_AVERAGE_WINDOW 1
                int i;

                float radius_sum = 0.0;
                for (i = 0; i < RADIUS_AVERAGE_WINDOW; i++) {
                    radius_sum += calc_radius(ifragCoord.x + (i - RADIUS_AVERAGE_WINDOW/2));
                }

                float radius;
                radius = radius_sum / float(RADIUS_AVERAGE_WINDOW);

                /* Save average value */
                SAVE(radius);

                return;
            }
        }

        if (ifragCoord.y == EDGE) {
            /* Keep unchanged */
            fragColor = texture(THIS_CHANNEL, uv);
            return;
        }
        if (ifragCoord.y == AVERAGE) {
            /* Keep unchanged */
            fragColor = texture(THIS_CHANNEL, uv);
            return;
        }
        if (ifragCoord.y == RADIUS && (ifragCoord.x % RADIUS_SPACING) == 0) {
            /* Keep unchanged */
            fragColor = texture(THIS_CHANNEL, uv);
            return;
        }

        if (uv.x > PRINT_MARGINAL && uv.x < 1.0 - PRINT_MARGINAL) {
            /* Print radius */
            if ((ifragCoord.x % RADIUS_SPACING) == 0) {
                /* Get border value */
                int radius_y = int(LOAD(RADIUS, ifragCoord.x));
                if (radius_y > ifragCoord.y) {
                    fragColor = mix(GRAPH_COLOR, texture(CAMERA_CHANNEL, uv), GRAPH_COLOR.a);
                    return;
                }
            }

            /* Print edge */
            int average_y = int(LOAD(AVERAGE, ifragCoord.x));
            if (average_y > ifragCoord.y) {
                if (average_y - ifragCoord.y > 7) {
                    fragColor = texture(CAMERA_CHANNEL, uv);
                } else {
                    fragColor = mix(LINE_COLOR, texture(CAMERA_CHANNEL, uv), LINE_COLOR.a);
                }
                return;
            }
        }

        fragColor = texture(CAMERA_CHANNEL, uv);
    } else {
        fragColor = texture(CAMERA_CHANNEL, uv);
    }
}

