void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = texelFetch( iChannel0, ivec2(fragCoord),0 ).xyz;
    vec3 picked_col = texelFetch( iChannel0, ivec2(iMouse.xy),0 ).xyz;
    if (col == picked_col){
        vec2 edge_point = floor(fragCoord); //a point on the edge of the same area as this pixel
        vec2 click_edge_point = floor(iMouse.xy); //a point on the edge of the same area as the last click
        //trace from the coord of this pixel to the edge of the color
        //count edge of screen as edge
        while (texelFetch( iChannel0, ivec2(edge_point) + ivec2(0,1),0 ).xyz == col){
            if (edge_point.y + 1.0 < iResolution.y) {
                edge_point += vec2(0,1);
            } else {
                break;
            }
        }
        //trace from the coord of the last click to the edge of the color
        //count edge of screen as edge
        while (texelFetch( iChannel0, ivec2(click_edge_point) + ivec2(0,1),0 ).xyz == col){
            if (click_edge_point.y + 1.0 < iResolution.y) {
                click_edge_point += vec2(0,1);
            } else {
                break;
            }
        }
        //follow edge from edge_point to click_edge_point
        //if it reaches click_edge_point change col
        //if it wraps back to edge_point exit
        //treat screen edge as edge
    }

    //outline (for testing)
    if (col == picked_col){
        vec3 x_pos_col = texelFetch( iChannel0, ivec2(fragCoord) + ivec2(1,0),0 ).xyz;
        vec3 x_neg_col = texelFetch( iChannel0, ivec2(fragCoord) + ivec2(-1,0),0 ).xyz;
        vec3 y_pos_col = texelFetch( iChannel0, ivec2(fragCoord) + ivec2(0,1),0 ).xyz;
        vec3 y_neg_col = texelFetch( iChannel0, ivec2(fragCoord) + ivec2(0,-1),0 ).xyz;
        if (x_pos_col + x_neg_col + y_pos_col + y_neg_col != picked_col*4.0){
            col = vec3(0);
        }
        //follow edge
    }
    fragColor = vec4(col,1.0);
}

