#define MAX_PATTERN_LENGTH 10


// We define the patterns here
// This version of GL doesn't allow for lists of lists, so we have to do it this way.
const int[] v_edges_0 = int[](1, 1, 0, 1, 0, 0, 1, 0);
const int[] h_edges_0 = int[](1, 0, 0, 1, 0, 1);

const int[] v_edges_1 = int[](1, 1, 0, 0);
const int[] h_edges_1 = int[](1, 1, 0);

const int[] v_edges_2 = int[](1, 1, 0, 0, 1, 0, 0);
const int[] h_edges_2 = int[](1, 1, 0, 0, 1, 0, 0);

const int[] v_edges_3 = int[](1, 1, 1, 0, 0, 0);
const int[] h_edges_3 = int[](1, 1, 0);

// Integer modulo (%) in glsl is a joke! This one works properly.
int intmod(int a, int m) {
	int res = a - (a/m)*m;
	return res < 0 ? res + m : res;
}

// Is the low vertical edge on the cell (x,y) on?
bool hitomezashi_edge_v(int x, int y, int pattern)
{
	// Fetch the right pattern. Default 0.
	int v_edge = v_edges_0[intmod(x, v_edges_0.length())];
	if (pattern == 1) v_edge = v_edges_1[intmod(x, v_edges_1.length())];
    if (pattern == 2) v_edge = v_edges_2[intmod(x, v_edges_2.length())];
    if (pattern == 3) v_edge = v_edges_3[intmod(x, v_edges_3.length())];
    return intmod(v_edge + y, 2) != 1;
}

// Is the low horizontal edge on the cell (x,y) on?
bool hitomezashi_edge_h(int x, int y, int pattern)
{
	// Fetch the right pattern. Default 0.
	int h_edge = h_edges_0[intmod(y, h_edges_0.length())];
	if (pattern == 1) h_edge = h_edges_1[intmod(y, h_edges_1.length())];
    if (pattern == 2) h_edge = h_edges_2[intmod(y, h_edges_2.length())];
    if (pattern == 3) h_edge = h_edges_3[intmod(y, h_edges_3.length())];
    return intmod(h_edge + x, 2) != 1;
}

// The SDF for a hitomezashi pattern with a given cell size.
float hitomezashi_SDF(float cell_size, vec2 coord, int pattern)
{
// Work out what cell we're in, and whereabouts in it we are.
    vec2 coord_ = coord/cell_size,
         cell_f = fract(coord_);
	int  cell_x = int(floor(coord_.x)),
		 cell_y = int(floor(coord_.y));

    // Every corner is populated, so the distance is bounded above
    // by the distances to each of the corners. We will reduce it from here.
    float dist = min(
        min( length(vec2(0.0, 0.0) - cell_f), length(vec2(0.0, 1.0) - cell_f) ),
        min( length(vec2(1.0, 0.0) - cell_f), length(vec2(1.0, 1.0) - cell_f) )  );

    // Fetch +ve or -ve for every vertical/horizontal edge:
    if (hitomezashi_edge_v(cell_x + 0, cell_y + 0, pattern)) dist = min(dist,       cell_f.x);
    if (hitomezashi_edge_v(cell_x + 1, cell_y + 0, pattern)) dist = min(dist, 1.0 - cell_f.x);
    if (hitomezashi_edge_h(cell_x + 0, cell_y + 0, pattern)) dist = min(dist,       cell_f.y);
    if (hitomezashi_edge_h(cell_x + 0, cell_y + 1, pattern)) dist = min(dist, 1.0 - cell_f.y);

	// We're done!
    return dist * cell_size;
}

// Check if a given square has the same color as the (0,0) square.
bool hitomezashi_color(float cell_size, vec2 coord, int pattern)
{
	// Fetch the right pattern's measurements. Default 0.
	int v_edges = v_edges_0.length();
	int h_edges = h_edges_0.length();
	if (pattern == 1)
	{
		v_edges = v_edges_1.length();
		h_edges = h_edges_1.length();
	}
    if (pattern == 2)
	{
		v_edges = v_edges_2.length();
		h_edges = h_edges_2.length();
	}
    if (pattern == 3)
	{
		v_edges = v_edges_3.length();
		h_edges = h_edges_3.length();
	}

    // Some hitomezashi patterns need to be repeated to tile the colors correctly.
	# define X_REPEATS 2
    # define Y_REPEATS 2
	vec2 coord_ = coord/cell_size;
	int cell_x = intmod(int(floor(coord_.x)), X_REPEATS * v_edges),
		cell_y = intmod(int(floor(coord_.y)), Y_REPEATS * h_edges);

	bool same = true;

    // We count the number of bold edges we cross going right
	int i;
	for (i = 0; i < X_REPEATS * MAX_PATTERN_LENGTH; i++)
	{
		if (i == cell_x) break;
		if (hitomezashi_edge_v(i+1, 0, pattern)) same = !same;
	}

	// We count the number of bold edges we cross going up
	for (int j = 0; j < Y_REPEATS * MAX_PATTERN_LENGTH; j++)
	{
		if (j == cell_y) break;
		if (hitomezashi_edge_h(i, j+1, pattern)) same = !same;
	}

	return same;
}

vec3 hitomezashi_image(float cell_size, vec2 coord, int pattern,
                       vec3 tile1, vec3 tile2, vec3 grouting)
{
    vec3 color = hitomezashi_color(cell_size, coord, pattern) ? tile1 : tile2;
    float b = smoothstep(1.5, 2.5, hitomezashi_SDF(cell_size, coord, pattern));
    color = mix(color, grouting, 1.0 - b);
    return color;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	// I've chosen some colours I think are nice :)
    vec3 p0 = hitomezashi_image(25.0, fragCoord + 40.0*iTime*vec2(1.0, 1.0), 0,
        vec3(0.75, 0.7, 1.0), vec3(1.0), vec3(0.0));
    vec3 p1 = hitomezashi_image(90.0, fragCoord + 80.0*iTime*vec2(1.0,-1.0), 1,
        vec3(1.0, 0.8, 0.8), vec3(0, 0.3, 0.85), vec3(1.0));
    vec3 p2 = hitomezashi_image(50.0, fragCoord + 50.0*iTime*vec2(1.25,0.5), 2,
        vec3(fragCoord.x/iResolution.x), vec3(1.0 - fragCoord.x/iResolution.x), vec3(1.0));
    float wobble = sin(5.0*fragCoord.x/iResolution.x);
    vec3 p3 = hitomezashi_image(45.0, fragCoord + 20.0*iTime*vec2(0.5,-1.5), 3,
        vec3(0.95, 0.95, 0.9), vec3(0.8, 0.7, 0.5-0.1*wobble), vec3(0.4 - 0.2*wobble));
    vec3 pattern_img[] = vec3[](p0, p1, p2, p3);


    // Cycle every 8 seconds
    float transition_timing = iTime/8.0;
    int cycle = intmod(int(transition_timing), pattern_img.length());
    int next_cycle = intmod(cycle + 1, pattern_img.length());
    float f = fract(transition_timing);

    vec3 color = mix(pattern_img[cycle], pattern_img[next_cycle], smoothstep(0.9, 1.0, f));
    fragColor = vec4(color, 1.0);
}

// WebGL2 cleaned-up version of "Viscous Fingering" by cornusammonis. https://shadertoy.com/view/Xst3Dj

void mainImage(out vec4 O, vec2 u) {
        vec2 U = u;
        O =  normalize(T()); 

        // Before Fabrice suggestion I had chosen values:
        //O =  O/vec4(2,4,4,1) + .5*O.z;

        // now I boost the blue and dial back the red and green
        O =  O/vec4(1.3,1.3,0.75,1) + 0.4 *O.z; // 28.1 fps 
}

