// data
// in [x,y,z,w]

// (remember that 32 bit float may be 31 bit float on some GPUs)
// x pos 0xfffff data 0xfff (uint)
// y pos 0xfffff data 0xfff (uint)
// z speed 0xfffff f data 0xff (uint)
// w speed 0xfffff f data 0xff (uint)

// data 0xf color id 0xfffff id 
// 0xffff timer (unused, used only to fade start particles)


//particle movement logic in particle_data() function


int max_lines;
int max_particles; // per line because id 0xfffff max

bool is_reset();
vec4 particle_data(ivec2 ipx);
float noise3( in vec3 x );
float noise2( in vec2 p);
vec4 loadval(ivec2 io) {return loadvalg(io, iResolution.xy);}

vec4 getV(in vec2 p,float max_pos,float max_posy){
    if (p.x < 0.001 || p.y < 0.001) return vec4(0);
    if(ivec2(p)==ivec2(iResolution.xy)-1) return vec4(0);
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    float p1=decodeval_pos(floatBitsToUint(tval.x))*max_pos;
    float p2=decodeval_pos(floatBitsToUint(tval.y))*max_posy;
    float v1=decodeval_vel(floatBitsToUint(tval.z));
    float v2=decodeval_vel(floatBitsToUint(tval.w));
    return vec4(p1,p2,v1,v2);
}

uvec2 extra_dat_pos(vec2 p){
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    return uvec2(floatBitsToUint(tval.x)&0xfffu,floatBitsToUint(tval.y)&0xfffu);
}

uvec2 extra_dat_vel(vec2 p){
    vec4 tval=texelFetch( iChannel0, ivec2(p), 0 );
    return uvec2(floatBitsToUint(tval.z)&0xffu,floatBitsToUint(tval.w)&0xffu);
}

// get saved unique ID
uvec2 get_id(vec2 p){
    uvec2 v1=extra_dat_pos(p);
    uint iret=(v1[0]<<12)|(v1[1]<<0);
    return uvec2(iret&0xfffffu,iret>>20);
}

uint get_timer(vec2 p){
    uvec2 v1=extra_dat_vel(p);
    uint iret=(v1[0]<<8)|(v1[1]<<0);
    return iret;
}

uvec2 save_id(uint id, uint color_id){
    id=id&0xfffffu;
    id+=color_id<<20;
    uint a=(id>>12)&0xfffu;
    uint b=(id>>0)&0xfffu;
    return uvec2(a,b);
}

uvec2 save_timer(uint timer){
    uint a=(timer>>8)&0xffu;
    uint b=(timer>>0)&0xffu;
    return uvec2(a,b);
}

bool destr(int id, int max_pos){
    int sx=id%max_pos;
    int sy=id/max_pos;
    return particle_data(ivec2(sx,sy)).w<-0.5;
}

vec2 pack_pos(vec2 pos,uvec2 extra_val, float max_pos, float max_posy){
    uint p1=uint((pos.x/max_pos)*float(0xfffff));
    uint p2=uint((pos.y/max_posy)*float(0xfffff));
    float px=uintBitsToFloat(encodeval_pos(uvec2(p1,extra_val.x)));
    float py=uintBitsToFloat(encodeval_pos(uvec2(p2,extra_val.y)));
    return vec2(px,py);
}

vec2 pack_vel(vec2 vel,uvec2 extra_val){
    float vx=uintBitsToFloat(encodeval_vel(vel.x,extra_val.x));
    float vy=uintBitsToFloat(encodeval_vel(vel.y,extra_val.y));
    return vec2(vx,vy);
}

// save everything to pixel color
vec4 save_all(vec2 pos, vec2 vel, uint id, uint timer, uint color_id){
    uvec2 extra_data_pos_tid=save_id(id, color_id);
    uvec2 extra_data_vel_tid=save_timer(timer);
    float max_pos=iResolution.x;
    float max_posy=iResolution.y;
    vec2 pos_ret=pack_pos(pos,extra_data_pos_tid,max_pos,max_posy);
    vec2 vel_ret=pack_vel(vel,extra_data_vel_tid);
    return vec4(pos_ret,vel_ret);
}

float rand(vec2 co){
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

bool is_reset(){
    ivec2 tres = ivec2(texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).xy);
    if(tres==ivec2(iResolution.xy))return false;
    return true;
}

vec2 get_target_pos(int id, int rsz){
    int sx=id%rsz;
    int sy=id/rsz;
    return particle_data(ivec2(sx,sy)).xy;
}

void sim_step( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    vec2 middle = fragCoord;
    uint self_id=0u;
    uint color_id=0u;
    uint timer=0u;
    int fade_frame = max(iFrame-abs(int(texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).w))-fade_time/100,0);
    int timer_spn_l = timer_spn+max((fade_time*4-fade_frame)*100,0);
    float max_pos=iResolution.x;
    float max_posy=iResolution.y;
    int sz = int(min(max_posy,max_pos)*0.25); // use 0.25 of screen square size
    int rsz = min(sz,max_particles); // limit max id of texture because 0xfffff max id
    if (iFrame < 1 || is_reset()) 
    {
        ivec2 ipx = ivec2(fragCoord);
        if ((ipx.x + ipx.y) %2 == 0 && ipx.y % 2 == 0){

            //init

            ivec2 mid = (ipx-ivec2(iResolution.xy/2.)+ivec2(iResolution.xy/2.)%2)/2;
            int ix = 0;int iy = 0;
            if(mid.x<0&&mid.y>0){
                ix=1;iy=(((max_lines))%2);
                mid.x=abs(mid.x);
                mid.y=(max_lines/2+0)-mid.y-iy+(((max_lines))%2);
            }

            if(mid.x>=0&&mid.y>=0&&mid.x<rsz&&mid.y<max_lines/2+(((max_lines))%2)){
                mid.y+=ix*(max_lines/2)+iy;
                uint id=uint(mid.x+mid.y*rsz);
                ivec2 tidp=ivec2(int(id)%rsz,int(id)/rsz);
                vec2 pos=middle;
                vec2 vel=vec2(0.);
                timer=uint(particle_data(tidp).z);
                color_id=0x0u;
                fragColor = save_all(pos,vel,id,timer,color_id);
                return;
            }
        }
    } else {
        vec4 v = vec4(0.); 
        vec2 lp=vec2(-10.);
        bool br=false;
        for (int x=-1; x<=1; x++) {
            if(br)break;
            for (int y=-1; y<=1; y++) {
                vec2 np = fragCoord + vec2(x,y);
                vec4 p = getV(np,max_pos,max_posy);
                //found ball for transition
                if(trunc(middle) == trunc(p.xy)){
                    v = p;
                    lp=np;
                    br=true;
                    break;
                }
            }
        }


        // movement calculations
        if (br){
            uvec2 tmpid = get_id(trunc(lp.xy));
            self_id=tmpid.x;
            color_id=tmpid.y;
            if(destr(int(self_id),rsz)){fragColor = vec4(0.);return;}

            timer=get_timer(trunc(lp.xy));
            //if(timer>=uint(fade_time)){fragColor=vec4(0.);return;}
            timer+=1u;
            vec2 dr = vec2(0);

            // collision checks
            float stress = 0.0;
            for (int x=-2; x<=2; x++) {
                for (int y=-2; y<=2; y++) {
                    if (x !=0 || y != 0) 
                    {
                        vec4 p = getV(fragCoord + vec2(x,y),max_pos,max_posy);
                        if (p.x > 0.0) {
                            vec2 d2 = v.xy - p.xy;
                            float l = length(d2);
                            float f = BALL_D - l;
                            if (l >= 0.001* BALL_SIZE &&  f > 0.0) {
                                float f2 = f / (BALL_D);
                                f2 *= BALL_D;
                                vec2 force_part = E_FORCE * normalize(d2)*f2;
                                stress += abs(force_part.x)+abs(force_part.y);
                                dr += force_part;
                            }
                        }
                    }
                }
            }

            dr += M *max(stress, 1.0) * vec2(0.008) * VEL_LIMIT; // bad fix for jittering

            // movement calculation
            vec2 pos = v.xy;
            float damp_k = length(dr)>0.001? DAMP_K : 1.0; // don't apply damping to freely flying balls
            vec2 gravity = loadval(ivec2(0,1)).xy*maxG;
            dr += gravity * M; // gravity

            vec2 tdir = -pos+get_target_pos(int(self_id),rsz)*loadval(ivec2(1,0)).xy;
            tdir = tdir/loadval(ivec2(1,0)).xy;
            if(length(tdir)>0.0001)tdir=normalize(tdir);else tdir=vec2(0.);
            vec2 dr2 = clamp(length(tdir),0.,1.)*tdir * maxPf * M; // target pos


            vec2 vel = damp_k * v.zw + dr / M + dr2 / M ;
            vel = clamp(vel, vec2(-1.0), vec2(1.0));

            vec2 dpos = vel * VEL_LIMIT;
            pos += dpos;
            v.xy = pos;
            v.xy = clamp(v.xy,vec2(BALL_SIZE *(1.0 + sin(pos.y)*0.1),BALL_SIZE),iResolution.xy-vec2(BALL_SIZE));

            if(iFrame%timer_spn_l==0){self_id+=1u;}
            if((self_id%uint(rsz))==0u){fragColor = vec4(0.);return;}

            v=save_all(v.xy,vel,self_id,timer,color_id);
            fragColor = v; 
        } else {
        ivec2 ipx = ivec2(fragCoord);
        if ((iFrame%(timer_spn_l/1)==0)&&(ipx.x + ipx.y) %2 == 0 && ipx.y % 2 == 0){

            ivec2 mid = (ipx-ivec2(iResolution.xy/2.)+ivec2(iResolution.xy/2.)%2)/2;
            if(max_lines>11){mid.x+=(ipx.y%3)*((iFrame%2));mid.y+=max_lines/4;}
            int ix = 0;int iy = 0;
            if(mid.x<0&&mid.y>0){
                ix=1;iy=(((max_lines))%2);
                mid.x=abs(mid.x);
                mid.y=(max_lines/2+0)-mid.y-iy+(((max_lines))%2);
            }
            if(max_lines>11){mid.y+=(iFrame%4);}
            mid.x+=-int(0.25*radius*min(iResolution.y,iResolution.x));

            if(mid.x==1&&mid.y>=0&&mid.x<rsz&&mid.y<max_lines/2+(((max_lines))%2)){
                mid.y+=ix*(max_lines/2)+iy;
                uint id=uint(mid.x+mid.y*rsz);
                ivec2 tidp=ivec2(int(id)%rsz,int(id)/rsz);
                vec2 pos=middle;
                vec2 vel=vec2(0.);
                timer=uint(fade_time);
                color_id = uint((iFrame/(timer_spn_l*20))%0xf);
                fragColor = save_all(pos,vel,id,timer,color_id);
                return;
            }
        } else fragColor = vec4(0.);
        }
    }
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
#ifdef set_max_lines
    max_lines = set_max_lines;
#else
    max_lines = int(2.+sqrt(min(iResolution.x,iResolution.y)));
#endif
    max_particles= 0xfffff/max_lines;
    ivec2 ipx = ivec2(fragCoord);
    if(ipx==ivec2(iResolution.xy)-1){
        float lc = texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).z;
        float imz=iMouse.z;
        //imz=-1.;
        float c=is_reset()?iTime:(imz>0.?(lc>=0.?-iTime:lc):abs(lc));
        float ld = abs(texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).w);
        float d=is_reset()?float(iFrame):min(-1.*ld,-1.);
        fragColor=vec4(iResolution.xy,c,d);
        return;
    }
    sim_step(fragColor, fragCoord);
}


// return vec4(a,b,c,d)
// a b - target pos
// c - init timer (max 0xffff)
// d - destroy -1 (to fix real time ID resize)

// in ivec2 ipx is particle id in vec2(column,row)

vec4 particle_data(ivec2 ipx){

    float max_pos=iResolution.x;
    float max_posy=iResolution.y;
    int sz1 = int(min(max_posy,max_pos)*0.25);
    int rsz = min(sz1,max_particles);


    if(ipx.x>=rsz||ipx.y>=rsz)return vec4(-1.);

    int self_id = ipx.x+ipx.y*rsz;
    vec2 pos = vec2(0.);
    float timer_save = 0.; //used only on init particles

    vec2 tuv = vec2(ipx)/float(rsz)-0.5;

    self_id = (ipx.x+ipx.y*rsz);


    vec2 ttuv = toPolar(tuv*2.);
    ivec2 ipfx = ivec2((ttuv+vec2(0.5,0.))*float(rsz));

    float d=float(self_id);

    float amount = float(max_lines);
    float len = float(rsz);
    float fsize = float((len)*(amount));

    float res = iResolution.x/iResolution.y;

    float segt = floor(float(self_id)/len);
    if(segt<amount){
    float ts = 0.1285*len;

    //float segId = ts+mod(mod(float(self_id),len)+float(iFrame/30),len);
    float segId = ts+mod(float(self_id),len);//self_id+iFrame/30

    float dlen = mod(float(self_id),len)/len;

    // do not multiply timers in code, change here (because fract)
    float timer2 = fract(max(iTime-15.,0.)*0.018);
    //timer2=0.6;
    float timer0 = fract(iTime*0.02);
    float timer01 = fract(iTime*0.0435);
    //timer0=0.0;
    //timer01=0.0;
    float timer3 = fract(iTime*0.01);
    float timer4 = fract(iTime*0.007);
    float timer5 = fract(iTime*0.015);
    //timer3=0.0;
    //timer4=0.0;
    //timer5=0.0;

    float s = (0.1+0.33*pow( 4.0*timer2*(1.0-timer2), 100.))*noise2(vec2(1., 12.*(1.+segId)/len));
    vec3 p = vec3(40.*float(self_id)/fsize , 1.,  2.*segId/len);
    float tfl = mix((2.*dlen),1.*noise3(p),0.5*pow(4.0*timer5*(1.0-timer5),2.));
    vec2 tpos = vec2(cos(float(self_id)/fsize * PI * 2.)*(radius+(0.5665-0.13*pow(dlen,2.))*dlen),
                     sin(float(self_id)/fsize * PI * 2.)*(radius+(0.5665-0.13*pow(dlen,2.))*1./res*dlen)*res);

    float mouse_timer = texelFetch(iChannel0,ivec2(iResolution.xy)-1,0).z;
    if(mouse_timer<0.)
    {
        mouse_timer=iTime-abs(mouse_timer);
        float mouse_timer2=smoothstep(7.,10.,mouse_timer);
        mouse_timer=1.-smoothstep(0.,1.,mouse_timer);
        float ttmr = clamp((dlen-(radius*4.))*1000.,0.,1.);
        tpos*=0.135+(0.865*ttmr)*mouse_timer+(0.865*(1.-ttmr));
        vec2 iml =(iMouse.xy/iResolution.xy-0.5);
        float lim = length(iml);
        iml+=0.0001*(1.-abs(sign(iml)));
        tpos+=ttmr*mouse_timer2*(1.-mouse_timer)*min(lim,0.45)*normalize(iml);
    }

    tpos += vec2(cos((tfl+timer01) * PI*4.) * (1.-0.7313*pow(dlen,2.)) , sin((tfl+timer01) * PI*4.) * (1.-0.7313*pow(dlen,2.)) *res) * segId/len * s;


    float tt = pow( 4.0*timer3*(1.0-timer3), 3.);
    tpos += 0.09*(max(pow( 4.0*timer4*(1.0-timer4), 2.)-tt*10.,0.))*vec2(cos((float(self_id)/fsize+timer0) * PI*4.) * (1.-0.87313*pow(dlen,2.)) , sin((float(self_id)/fsize+timer0) * PI*4.) * (1.-0.87313*pow(dlen,2.)) *res)*(-0.062+segId/len);


    tpos += tt*.5*tpos*vec2(cos(5.*PI*segId/len),sin(5.*PI*segId/len));
    pos=(vec2(tpos)+0.5);

    }
    if(segt>=amount)d=-1.;

    vec4 ret=vec4(pos,timer_save,d);
    return ret;
}

float hash3(vec3 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

float hash2(vec2 p)
{
    p  = fract( p*0.3183099+.1 );
	p *= 17.0;
    return -1.+2.*fract( p.x*p.y*(p.x+p.y) );
}

float noise3( in vec3 x )
{
    vec3 i = floor(x);
    vec3 f = fract(x);
    f = f*f*(3.0-2.0*f);

    return mix(mix(mix( hash3(i+vec3(0,0,0)), 
                        hash3(i+vec3(1,0,0)),f.x),
                   mix( hash3(i+vec3(0,1,0)), 
                        hash3(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( hash3(i+vec3(0,0,1)), 
                        hash3(i+vec3(1,0,1)),f.x),
                   mix( hash3(i+vec3(0,1,1)), 
                        hash3(i+vec3(1,1,1)),f.x),f.y),f.z);
}


float noise2( in vec2 p)
{
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( hash2( i + vec2(0.0,0.0) ), 
                     hash2( i + vec2(1.0,0.0) ), u.x),
                mix( hash2( i + vec2(0.0,1.0) ), 
