//
// Playing with Voronoi smooth distance
//
//  (basically redoing https://www.shadertoy.com/view/ldl3W8 on my own)
//
////////////////////////////////////////////////////////////////////////

#define ISO_PHASE 5.    // to animate the iso-lines
#define SMOOTH_MIN_PHASE 5.

// see https://www.shadertoy.com/view/4djSRW
vec2 hash2(vec2 p) {
  vec3 p3 = fract(vec3(p.xyx) * vec3(.1021, .1013, .0977));
  p3 += dot(p3, p3.yzx + 33.33);
  return fract((p3.xx + p3.yz) * p3.zy);
}

////////////////////////////////////////////////////////////////////////
// distance from origin to segment [p1, p2]
float SegmentDistance(vec2 p1, vec2 p2) {
  vec2 dir = p2 - p1;
  float norm = 1. / dot(dir, dir);
  float frac = clamp(norm * dot(dir, -p1), 0.0, 1.0);
  return length(p1 + frac * dir);
}

// distance from origin to (p1+p2)/2 along (p1->p2)
float CellDistance(vec2 p1, vec2 p2) {
  vec2 mid = 0.5 * (p1 + p2);
  vec2 dir = normalize(p2 - p1);
  return dot(mid, dir);
}

////////////////////////////////////////////////////////////////////////

vec2 GetCenter(vec2 id, float theta) {
  vec2 phi = 6.283185 * hash2(id) + theta;
  return 0.5 + 0.5 * sin(phi);
}

float VoronoiDistance(vec2 p, float t, int n, out vec2 p1) {
  vec2 cell = floor(p);
  vec2 sub_pos = fract(p);
  float d1 = 1.e6, d2 = 1.e7;
  vec2 p2;
  for (int j = -n; j <= n; ++j) {
    for (int i = -n; i <= n; ++i) {
      vec2 offset = vec2(float(i), float(j));
      vec2 center = offset + GetCenter(cell + offset, t) - sub_pos;
      float d = dot(center, center);
      if (d < d1) {
        d2 = d1;
        d1 = d;
        p2 = p1;
        p1 = center;
      } else if (d < d2) {
        d2 = d;
        p2 = center;
      }
    }
  }
  return SegmentDistance(p1, p2);
}

// polynomial smooth min
float smin(float a, float b, float k) {
  float h = max(k - abs(a - b), 0.0) / k;
  return min(a, b) - h * h * k * 0.25;
}

float VoronoiMin(vec2 p, float t, int n, vec2 p1, float k) {
  vec2 cell = floor(p);
  vec2 sub_pos = fract(p);
  float d_min = 1.e6;
  for (int j = -n; j <= n; ++j) {
    for (int i = -n; i <= n; ++i) {
      vec2 offset = vec2(float(i), float(j));
      vec2 p = offset + GetCenter(cell + offset, t) - sub_pos;
      if (dot(p - p1, p - p1) > 0.001) {  // not the immediate center?  
        d_min = smin(CellDistance(p1, p), d_min, k);
      }
    }
  }
  return d_min;
}

////////////////////////////////////////////////////////////////////////

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 uv = 6. * fragCoord / iResolution.y;
  float theta = iTime * 1.2;
  float blend =
      mix(0.01, 0.38, 0.5 + 0.5 * sin(SMOOTH_MIN_PHASE * theta));
  vec2 p1;
  float d12 = VoronoiDistance(uv, theta, 1, p1);
  float d = VoronoiMin(uv, theta, 2, p1, blend);

  vec3 col = vec3(0.);
  if (true) {  // draw a line p2 -> p1
    col = mix(vec3(0.2, 0.2, 0.7), col, smoothstep(0.00, 0.06, d12));
  }
  if (true) {  // draw iso-lines
    float iso_d = d * (0.5 + 0.5 * sin(60. * d + iTime * ISO_PHASE));
    float amp = 0.5 + 0.5 * sin(2. * theta * 0.5);
    col = mix(col, amp*vec3(0.76, 0.45, 0.96), smoothstep(0.0, 0.3, iso_d));
  }
  if (true) {  // draw cell borders
    col = mix(vec3(0.35, 0.65, 0.20), col, smoothstep(0.00, 0.02, d));
  }
  if (true) {  // draw centers
    float d = dot(p1, p1);
    col = mix(vec3(0.97, 0.8, 0.3), col, smoothstep(0.002, 0.005, d));
  }
  fragColor = vec4(col, 1.0);
}

// Caosdoar 2017 (caosdoar@gmail.com)

#define PI          (3.1415926535897932384626433832795)
#define HALF_PI     (1.5707963267948966192313216916398)
#define TWO_PI      (6.283185307179586476925286766559)


// Spherical to cartesian coordinates
vec3 sphe2cart(vec2 p)
{
    vec2 s = sin(p);
    vec2 c = cos(p);
    return vec3(c.x*s.y, c.y, s.x*s.y);
}

vec2 cart2sphe(vec3 p)
{
    p *= inversesqrt(dot(p, p));
    return vec2(atan(p.z, p.x), acos(p.y));
}

// Ray-sphere intersection
float sphere(vec3 o, vec3 l, vec3 c, float r2)
{
    const float eps = 0.0001;

    float a_ = dot(l,l);
	vec3 p = o - c;
    float b_ = 2.0 * dot(l,p);
    float c_ = dot(p,p) - r2;

    float a = b_*b_-4.0*a_*c_;
    if (a < 0.0) return -1.0;
    a = sqrt(a);
    float t2 = -b_ + a;
    if (t2 < eps) return -1.0;
    float t1 = -b_ - a;
    float result = (t1 > eps && t1 < t2) ? t1 : t2;
    return result * 0.5 / a_;
}

int rotl17(int v) { return (v << 17) | (v >> 15); }

int hash(int x, int y, int z, int seed)
{
    int h = seed * 374761393;
    h = (x ^ h) * 374761393;
    h = (y ^ h) * 374761393;
    h = (z ^ h) * 374761393;
    h = (h ^ rotl17(h)) * 2246822519;
    return h;
}

float noise(vec3 p, int seed)
{
    return float(hash(int(p.x), int(p.y), int(p.z), seed)) / 4294967296.0;
}

float vnoise(vec3 p, int seed)
{
    vec3 p0 = floor(p);
    vec3 s = p - p0;
    //s = s * s * (3.0 - 2.0 * s);
    s = s * s * s * (s * (s * 6.0 - 15.0) + 10.0);

    return mix(
        mix(
        	mix(noise(p0, seed), noise(p0 + vec3(1.0, 0.0, 0.0), seed), s.x),
        	mix(noise(p0 + vec3(0.0, 1.0, 0.0), seed), noise(p0 + vec3(1.0, 1.0, 0.0), seed), s.x),
        	s.y),
        mix(
        	mix(noise(p0 + vec3(0.0, 0.0, 1.0), seed), noise(p0 + vec3(1.0, 0.0, 1.0), seed), s.x),
        	mix(noise(p0 + vec3(0.0, 1.0, 1.0), seed), noise(p0 + vec3(1.0), seed), s.x),
        	s.y),
        s.z);
}

vec3 grad(vec3 p, int seed)
{
    return vec3(noise(p, seed), noise(p, seed + 1), noise(p, seed + 2));
}

// by: Iñigo Quilez (with small modifications for custom hash)
// https://www.shadertoy.com/view/4dffRH
float gnoise(in vec3 x, int seed)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    #if 1
    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);
