    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);
    #endif    

    // gradients
    vec3 ga = grad( p+vec3(0.0,0.0,0.0), seed );
    vec3 gb = grad( p+vec3(1.0,0.0,0.0), seed );
    vec3 gc = grad( p+vec3(0.0,1.0,0.0), seed );
    vec3 gd = grad( p+vec3(1.0,1.0,0.0), seed );
    vec3 ge = grad( p+vec3(0.0,0.0,1.0), seed );
	vec3 gf = grad( p+vec3(1.0,0.0,1.0), seed );
    vec3 gg = grad( p+vec3(0.0,1.0,1.0), seed );
    vec3 gh = grad( p+vec3(1.0,1.0,1.0), seed );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return 
        va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + 
        u.x*u.y*(va-vb-vc+vd) + 
        u.y*u.z*(va-vc-ve+vg) + 
        u.z*u.x*(va-vb-ve+vf) + 
        (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z;
}

// by: Iñigo Quilez (with small modifications for custom hash)
// https://www.shadertoy.com/view/4dffRH
vec4 gnoised(in vec3 x, int seed)
{
    // grid
    vec3 p = floor(x);
    vec3 w = fract(x);

    #if 1
    // quintic interpolant
    vec3 u = w*w*w*(w*(w*6.0-15.0)+10.0);
    vec3 du = 30.0*w*w*(w*(w-2.0)+1.0);
    vec3 iu = 1.0 - u;
    #else
    // cubic interpolant
    vec3 u = w*w*(3.0-2.0*w);
    vec3 du = 6.0*w*(1.0-w);
    #endif    

    // gradients
    vec3 ga = grad( p+vec3(0.0,0.0,0.0), seed );
    vec3 gb = grad( p+vec3(1.0,0.0,0.0), seed );
    vec3 gc = grad( p+vec3(0.0,1.0,0.0), seed );
    vec3 gd = grad( p+vec3(1.0,1.0,0.0), seed );
    vec3 ge = grad( p+vec3(0.0,0.0,1.0), seed );
	vec3 gf = grad( p+vec3(1.0,0.0,1.0), seed );
    vec3 gg = grad( p+vec3(0.0,1.0,1.0), seed );
    vec3 gh = grad( p+vec3(1.0,1.0,1.0), seed );

    // projections
    float va = dot( ga, w-vec3(0.0,0.0,0.0) );
    float vb = dot( gb, w-vec3(1.0,0.0,0.0) );
    float vc = dot( gc, w-vec3(0.0,1.0,0.0) );
    float vd = dot( gd, w-vec3(1.0,1.0,0.0) );
    float ve = dot( ge, w-vec3(0.0,0.0,1.0) );
    float vf = dot( gf, w-vec3(1.0,0.0,1.0) );
    float vg = dot( gg, w-vec3(0.0,1.0,1.0) );
    float vh = dot( gh, w-vec3(1.0,1.0,1.0) );

    // interpolations
    return vec4( va + u.x*(vb-va) + u.y*(vc-va) + u.z*(ve-va) + u.x*u.y*(va-vb-vc+vd) + u.y*u.z*(va-vc-ve+vg) + u.z*u.x*(va-vb-ve+vf) + (-va+vb+vc-vd+ve-vf-vg+vh)*u.x*u.y*u.z,    // value
                 ga + u.x*(gb-ga) + u.y*(gc-ga) + u.z*(ge-ga) + u.x*u.y*(ga-gb-gc+gd) + u.y*u.z*(ga-gc-ge+gg) + u.z*u.x*(ga-gb-ge+gf) + (-ga+gb+gc-gd+ge-gf-gg+gh)*u.x*u.y*u.z +   // derivatives
                 du * (vec3(vb,vc,ve) - va + u.yzx*vec3(va-vb-vc+vd,va-vc-ve+vg,va-vb-ve+vf) + u.zxy*vec3(va-vb-ve+vf,va-vb-vc+vd,va-vc-ve+vg) + u.yzx*u.zxy*(-va+vb+vc-vd+ve-vf-vg+vh) ));
}

// Curl-noise 
// Reference
// https://www.cct.lsu.edu/~fharhad/ganbatte/siggraph2007/CD2/content/papers/046-bridson.pdf
vec3 curlnoise(in vec3 p, int seed)
{
    vec4 x = gnoised(p, seed);
    vec4 y = gnoised(p, seed+3);
    vec4 z = gnoised(p, seed+6);
    return vec3(z.z - x.w, x.w - z.y, y.y - x.z);
}

vec3 noise_offset(vec3 p, float f, float s)
{
    p += curlnoise(p * f, 1) * s;
    p += curlnoise(p * f, 1) * s;
    p += curlnoise(p * f, 1) * s;
    return p;
}


float y_equator(float y)
{
    return pow(abs(y), 1.0) * sign(y);
}

float y_noise(float y)
{
    y += texture(iChannel0, vec2(y * 0.05, 0.0)).x * 0.08;
    y += texture(iChannel0, vec2(y * 0.1, 0.0)).x * 0.04;
    y += texture(iChannel0, vec2(y * 0.2, 0.0)).x * 0.02;
    return y;
}

vec4 swirl(vec3 p, vec3 sc, float sr)
{
    float t = max(0.0, 1.0 - distance(p, sc) / sr);
    float t2 = smoothstep(0.0, 1.0, t);
    float theta = t2 * 10.0;
    vec4 q1 = vec4(0.0, p.x, p.y, p.z);
    float theta_half = theta * 0.5;
    float s = sin(theta_half);
    float c = cos(theta_half);
    vec4 q2 = vec4(c, s * sc.x, s * sc.y, s * sc.z);
    vec4 q2i = vec4(q2.x, -q2.y, -q2.z, -q2.w);
    vec4 qa = vec4(
        q1.x*q2.x-q1.y*q2.y-q1.z*q2.z-q1.w*q2.w,
        q1.x*q2.y+q1.y*q2.x-q1.z*q2.w+q1.w*q2.z,
        q1.x*q2.z+q1.y*q2.w+q1.z*q2.x-q1.w*q2.y,
        q1.x*q2.w-q1.y*q2.z+q1.z*q2.y+q1.w*q2.x);
    vec4 q3 = vec4(
        q2i.x*qa.x-q2i.y*qa.y-q2i.z*qa.z-q2i.w*qa.w,
        q2i.x*qa.y+q2i.y*qa.x-q2i.z*qa.w+q2i.w*qa.z,
        q2i.x*qa.z+q2i.y*qa.w+q2i.z*qa.x-q2i.w*qa.y,
        q2i.x*qa.w-q2i.y*qa.z+q2i.z*qa.y+q2i.w*qa.x);
    return vec4(q3.yzw, t2);
    //return vec3(t);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv -= vec2(0.5);
    uv.x *= iResolution.x / iResolution.y;

#if 1
    vec3 cc = vec3(0.0,0.0,3.6);
    float cam_speed = 0.1;
    vec3 co = 3.6 * sphe2cart(vec2(cam_speed*iTime, 3.14159*0.5+0.5*sin(cam_speed*iTime))) + cc;
    vec3 cf = normalize(cc-co);
    vec3 cr = normalize(cross(cf, vec3(0.0,1.0,0.0)));
    vec3 cu = normalize(cross(cr, cf));
#else
    vec3 cc = vec3(0.0,0.0,3.6);
    vec3 co = vec3(0.0,2.6,3.6);
    vec3 cf = normalize(cc-co);
    vec3 cr = normalize(cross(cf, vec3(0.0,0.0,1.0)));
    vec3 cu = normalize(cross(cr, cf));
#endif
    uv *= 3.14159 * 0.25;

    vec3 rd = normalize(cr*uv.x + cu*uv.y + cf);

    vec3 so = vec3(0.0,0.0,3.6);
    float r = 1.0;
    float r2 = r*r;
    float t = sphere(co, rd, so, r2);
    if (t > 0.0)
    {
        vec3 p = co + t * rd;
        vec3 pc = p - so;

        // Bands
        vec2 scoord_clean = cart2sphe(pc);
        float y_clean = scoord_clean.y / PI * 2.0 - 1.0;
        y_clean = y_equator(y_clean);
        y_clean = y_noise(y_clean);
        float w0_clean = sin((y_clean * HALF_PI + HALF_PI / 4.0) * 12.0f);

        // Swirls
        float row_count = 2.0;
        float row = round(scoord_clean.y * row_count);
        vec3 row_rand = texture(iChannel0, vec2(row / row_count, 0.35)).rgb;
        float swirl_size = 0.3 + 0.7 * row_rand.y;
        float swirl_start = fract(row_rand.x * 10.0f);
        float swirl_offset = fract(iTime * (row_rand.z * 0.9 + 0.1) * 0.03);
        vec2 swirlc_sph = vec2((swirl_start + swirl_offset) * TWO_PI, (row + 0.5 * (1.0 - swirl_size) * (row_rand.z * 2.0 - 1.0)) / row_count);
        vec3 swirlc = sphe2cart(swirlc_sph);
        vec4 swirl_data = swirl(pc, swirlc, 0.9 * swirl_size * 0.5 / row_count);
        //float h = texture(iChannel0, cart2sphe(pc + swirl_data.xyz) / PI).r;
        float swirl_out = 1.0 - swirl_data.w;
        pc = swirl_data.xyz;

        vec3 pc1 = noise_offset(pc, 50.0, 0.002);
        vec3 pc2 = noise_offset(pc, 5.0, 0.02);
        pc1 += (pc2 - pc) * 0.2;
        pc2 += (pc1 - pc) * 0.5;

        pc2 = mix(pc2, swirl_data.xyz, swirl_data.w);

        vec2 scoord = cart2sphe(pc1);
        float y = scoord.y / PI * 2.0 - 1.0;
        y = y_equator(y);
        y = y_noise(y);

        float band = (y * HALF_PI + HALF_PI / 4.0) * 12.0f;
        float w0 = sin(band);
        float w1 = smoothstep(-1.0, 1.0, w0) * 2.0 - 1.0;
        float w2 = smoothstep(-1.0, 1.0, w1) * 2.0 - 1.0;

        // band force
        float a = sin((scoord.x + w0 * 0.25) * 10.0);
        vec3 pn = pc2 * 7.0;
        pn += swirl_out * gnoise(pc * 20.0, 1) * 0.2;
        vec3 ptan = cross(vec3(0.0,1.0,0.0), normalize(pc));
        pn += swirl_out * ptan * w0_clean * 2.0;

        //pn *= 4.0;
        float b = gnoise(pn, 1) + 0.6 * gnoise(pn * 2.0, 2) + 0.5 * gnoise(pn * 4.0, 3) + 0.4 * gnoise(pn * 8.0, 4);
        //float c = mix(w2, 0.75 * w0_clean + 0.4 * b, abs(w0_clean));
        float c = w2 - w0_clean * (b * 0.5 + 0.5);

        float swirl_height_dir = sign(row_rand.z - 0.5);
        c += 0.5 * swirl_data.w * swirl_height_dir;
        //c = clamp(c, -1.0, 1.0) - 0.2 * swirl_data.w * swirl_height_dir * b;
        float swirl_inner = max(0.0, swirl_data.w);
        //swirl_inner = pow(swirl_inner, 0.5);
        c = mix(c, swirl_height_dir * b, swirl_inner);
        //float a = gnoise(pc * 10.0, 1);



        fragColor = vec4(vec3(c * 0.5 + 0.5), 1.0);
        //fragColor = vec4(vec3(swirl_data.w), 1.0);
    }
    else
    {
        fragColor = vec4(vec3(0.1), 1.0);
    }
}

