vec4 sphere(vec4 data, vec4 color, vec3 camOrigin, float camScale, vec2 centerCoord, vec4 lightPos, float ambient)
{
    vec3 ro = camOrigin + vec3(camScale*(centerCoord).xy,0);
    vec3 rd = vec3(0.0,0.0,-1.0);
    if(length(ro.xy - data.xy) > data.w){
        return vec4(0);
    }

    vec3 toCenter = data.xyz - ro;
    float t0 = dot(rd, toCenter);

    float d = pow(-pow(length(toCenter),2.0)+pow(t0,2.0), .5);

    if(t0<0.0){
        return vec4(0);

    }

    float thc = pow(pow(d,2.0)+pow(data.w,2.0), .5);

    vec3 point = (t0 - thc)*rd+ro;

    vec3 normal = (data.xyz - point);
    normal = normal/length(normal);

    vec3 lr = point - lightPos.xyz;

    float lightDist = length(lr)*.1;

    lr = lr/(lightDist*lightDist+1.0);

    float light = dot(lr, normal)*lightPos.w/lightDist;
    light = max(light, 0.0);


    return vec4((light)*color.xyz + ambient * color.xyz,1.0);



}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float ambient = .03;

    vec4 sp = vec4(0.0,0.0,-60.0,iResolution.x/5.0);
    vec4 color = vec4(0.1,.70,1.0,1.0);
    vec3 camOrigin = vec3(0.0);
    float camscale = 1.0;
    vec2 center = fragCoord - iResolution.xy/2.0;
    vec4 light = vec4(4500.0,4500.0,-300.0,100000.0);

    float lr = 10000.0;
    float lightSpeed = .5;


    vec3 lightPos = vec3(0, sin(iTime*lightSpeed)*lr, cos(iTime*lightSpeed)*lr);
    lightPos += sp.xyz;
    light = vec4(lightPos, light.w);

    fragColor = sphere(sp,color,camOrigin,camscale,center,light, ambient);
}

// naive implementation of the 'hexrosa' aperiodic tiling
// see shane's radial tiling truchet for a better implementation:
//   https://www.shadertoy.com/view/NlcBRs

#define PI 3.141592653589793
const int nfold = 7;
const float hexH = 1.;
const int hexL = 7;
vec2 center = vec2(0.);
float initR = 0.;
const float angle1 = 1. / float(nfold) * PI * 2.;
const float angle2 = PI * 0.5 - angle1 / 2.;
const float sidelength = hexH / (2. * cos(0.5 * angle1) + 1.);
const float hexW = 2. * sidelength * sin(0.5 * angle1);
const float offX = hexW / 2.;
const float offY = sidelength + ((hexH - sidelength) / 2.);


float hexSDF(in vec2 position) {
    position /= vec2(sqrt(3.0), 1.5); // magic numbers
    position.y -= 0.5;
    position.x -= fract(floor(position.y) * 0.5);
    position = abs(fract(position) - 0.5);
    return abs(1. - max(position.x * tan(angle2) * 1.15 + position.y, position.x / hexW * 1.15)) * sqrt(3.) * 0.5; // magic numbers
}

float drawHex(in vec2 position, in float size) {
    // position *= vec2(hexH,hexW)/hexH;
    float sdf = hexSDF(position);
    if (position.x < -0.5) sdf = 0.;
    if (position.x > 0.5) sdf = 0.;
    if (position.y < -0.5) sdf = 0.;
    if (position.y > 0.5) sdf = 0.;
    return smoothstep(size-0.002,size+0.002,sdf);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.*fragCoord - iResolution.xy ) / iResolution.y;

    vec3 color = vec3(0.);
    float dt = iTime * 0.5;

    #define LOOP_METHOD 0
    #define GRID_METHOD 1
    #define USE_METHOD LOOP_METHOD
    #if USE_METHOD == LOOP_METHOD    
    //the hex rosa is made of 2 sets of nfold# of what I call 'branches'. a branch is a single triangular array of 'seeds' drawn as 'hexes' (base hexagons shapes)
    //this loop doubles the process for each set of branches
    for(int outset=0;outset<2;outset++){    
        vec2 ouv = uv;
        ouv *= Rot(float(outset) * PI / float(nfold));
        //this loop divides the circle by nfold# and draws that many branches, evenly spaced
        for (int branch=0;branch<nfold;branch++){
            vec2 buv = ouv;
            buv *= Rot(angle1 * float(branch));
            buv.y -= .08 / float(nfold); // magic numbers
            buv.y += sidelength * float(outset) * 0.41; // magic numbers
            //this loop sets and positions the row being drawn in the current branch
            for (int row=0;row<hexL;row++) { // the hexagonal loop could probably be optimized
                //this loop sets and positions the individual seed being drawn in the current row
                for (int col = row * -1; col <= row; col += 2) {
                    vec2 huv = buv;
                    float camera_shutter = 0.;
                    huv += camera_shutter; // shutter
                    vec2 hexcenter = vec2(float(col)*offX,float(row)*offY) * 0.41;
                    huv += hexcenter; // magic numbers
                    huv.y += offY/PI;
                    float mainhex = drawHex(huv, 0.749); // magic numbers
                    color += mainhex * 0.8;
                    vec3 hexcol = 0.1*vec3(outset*4,branch*1,row+col)*drawHex(huv, 0.76); // magic numbers
                    color -= hexcol;
                }            
            }            
        }
    }
    #elif USE_METHOD == GRID_METHOD

    #endif

    // Output to screen
    fragColor = vec4(color,1.0);
}

