mat2 rot (float a) {return mat2(cos(a),sin(a),-sin(a),cos(a));}
float hash21( vec2 p ) {return fract(sin(dot(p,vec2(23.43,84.21)))*4832.3234);}
float lsp(float begin, float end, float t) {return clamp((t-begin)/(end-begin),0.,1.);}
float eoc(float t) {return (t=t-1.)*t*t+1.;}

// http://mercury.sexy/hg_sdf/
float modPolar(inout vec2 p) {
    const float angle = 2.*PI/3.;
    float a = atan(p.y, p.x) + angle/2.;
    float c = floor(a/angle);
    a = mod(a,angle) - angle/2.;
    p = vec2(cos(a), sin(a))*length(p);
    return (abs(c) >= (3./2.)) ? abs(c) : c;
} 

// The Folds
void tet(inout vec4 p) {
	p = abs(p);
	if (p.x < p.y) p.xy = p.yx; p.x = -p.x;
	if (p.x > p.y) p.xy = p.yx; p.x = -p.x;
	if (p.x < p.z) p.xz = p.zx; p.x = -p.x;
	if (p.x > p.z) p.xz = p.zx; p.x = -p.x;
}
void sep(inout vec4 p){
	if (p.x + p.y<0.) p.xy = -p.yx;
	if (p.x + p.z<0.) p.xz = -p.zx;
	if (p.y + p.z<0.) p.zy = -p.yz;
}
//@iq thanks for the sdf's!
float cap( vec3 p, float h, float r ) {
  vec2 d = abs(vec2(length(p.xz),p.y)) - vec2(h,r);
  return min(max(d.x,d.y),0.) + length(max(d,0.));
}
float sdbox( vec3 p, vec3 b ) {
  vec3 q = abs(p) - b;
  return length(max(q,0.)) + min(max(q.x,max(q.y,q.z)),0.);
}
float sdframe( vec3 p, vec3 b, float e ) {
  p = abs(p  )-b;
  vec3 q = abs(p+e)-e;
  return min(min(
      length(max(vec3(p.x,q.y,q.z),0.))+min(max(p.x,max(q.y,q.z)),0.),
      length(max(vec3(q.x,p.y,q.z),0.))+min(max(q.x,max(p.y,q.z)),0.)),
      length(max(vec3(q.x,q.y,p.z),0.))+min(max(q.x,max(q.y,p.z)),0.));
}

//globals
mat2 tprot,rx,ry,mmt1,mmt2,mmt3,gr;
vec3 hit=vec3(0);vec3 hitPoint=vec3(0);
float tmod,ga1,ga2,ga3,ga4,ga5,tt;

const float size = 35.;
const float hlf = size/2.;

vec2 map(vec3 p) {
    vec2 res =vec2(1e5,0.);

    p.xz*=gr;
    vec4 P = vec4(p.xyz,1.);
    tet(P);

    vec3 q = P.xyz; 
    q.y-=3.;
    q.yz*=mmt1;
    q.x=abs(q.x)-(2.75+(3.*ga3)+ga4);
    q.zx*=mmt2;
    q.z=abs(q.z)-(2.-(2.25*ga1));
    q.yx*=mmt3;

    float mainbox = sdbox(q-vec3(0,1.1,0),vec3(1.5,3,.75));
    float cutbox =  sdbox(q-vec3(.45,1.25,.775),vec3(.85,2.5,1.1));
    mainbox = max(mainbox, -cutbox);

    float frame2 = sdframe(q-vec3(0,1.1,0),vec3(1.6,3.1,.82),.075)-.0125;
    if(frame2<res.x) res = vec2(frame2,4.);
    float frame = sdbox(q-vec3(-.3,1.3,.7),vec3(.445,1.35,.05));
    float tapeB = cap(vec3(q.y,q.z,abs(q.x))-vec3(2.8,.55,.65),.225,.1);
    tapeB = min(  cap(vec3(q.y,q.z,abs(q.x))-vec3(1.95,.5,.65),.100,.1),tapeB);

    vec3 tq1 = q.yzx-vec3(2.9,.25,.55);
    tq1.x=abs(abs(tq1.x+1.65)-1.2)-.6;
    frame = min(cap(tq1+vec3(0,.35,0),.25,.5),frame);
    vec3 pq1 = tq1;
    pq1.xz*=tprot;
    modPolar(pq1.xz);
    float tcbx = sdbox(pq1-vec3(.4,0,0),vec3(.09,.09,.075));

    float tape1 = cap(tq1,.575,.05);
    tape1=max(tape1,-tcbx);
    if(tape1<res.x) res = vec2(tape1,3.);

    vec3 bq = q-vec3(-.9,3,.65);
    bq.y=abs(abs(abs(bq.y)-.4)-.2)-.1;
    bq.x=abs(bq.x)-.2;
    float btn1 = sdbox(bq,vec3(.15,.05,.25))-.0125;
    btn1 = min(sdbox(q-vec3(-.5,1.25,.85),vec3(.45,.3,.06))-.0125,btn1);
    if(btn1<res.x) res = vec2(btn1,2.);

    mainbox = min(mainbox, frame);
    if(mainbox<res.x) res = vec2(mainbox,1.);

    res.x/=P.w;
    return res;
}

vec3 normal(vec3 p, float t, float mindist) {
    t*=mindist;
    float d = map(p).x;
    vec2 e = vec2(t,0);
    vec3 n = d - vec3(
        map(p-e.xyy).x,
        map(p-e.yxy).x,
        map(p-e.yyx).x
    );
    return normalize(n);
}

vec3 render(vec3 p, vec3 rd, vec3 ro, float d, float m, inout vec3 n) 
{

    n = normal(p,d,1.);
    vec3 lpos =  vec3(3,10,-8);
    vec3 l = normalize(lpos);
    float diff = clamp(dot(n,l),0.,1.);

    vec3 h = vec3(.3);

    if(m==1.) {
        vec3 h2 =mix(vec3(.05),vec3(.4),clamp((p.y+1.)*.25,0.,1.));
        h=mix(h2,vec3(.1),clamp((p.z+4.)*.1,0.,1.));
    }

    if(m==2.) {
        vec3 h2=mix(vec3(.596,.110,.690),vec3(.961,.000,.094),clamp((p.x+2.)*.07,0.,1.));
        h=mix(vec3(.600,0.525,0.039),h2,clamp((p.z+2.)*.07,0.,1.));
    }
    if(m==3.) {
        vec3 h2 =mix(vec3(.000,.639,.514),vec3(.698,.608,.165),clamp((p.x+4.)*.1,0.,1.));
        h=mix(h2,vec3(.871,.169,.847),clamp((p.y+3.)*.1,0.,1.));
    }
    if(m==4.) {
        vec3 h2 =mix(vec3(.5),vec3(.1),clamp((p.y+1.)*.25,0.,1.));
        h=mix(h2,vec3(.01),clamp((p.z+4.)*.1,0.,1.));
    }
    return diff*h;
}

void mainImage( out vec4 O, in vec2 F )
{
    // precal
    float time = T;
    tprot=rot(T*3.);
    tmod = mod(time,24.);

    float t1 = lsp(1.,4.,tmod);
    float t2 = lsp(8.,10.,tmod);
    float t3 = lsp(4.,7.,tmod);
    float t4 = lsp(12.,16.,tmod);
    float t5 = lsp(4.,6.,tmod);
    float t6 = lsp(8.,10.,tmod);
    float t7 = lsp(3.,12.,tmod);
    float t8 = lsp(15.,16.,tmod);

    ga1 = eoc(t1-t2);ga1 = ga1*ga1*ga1;
    ga2 = eoc(t3-t4);ga2 = ga2*ga2*ga2;  
    ga3 = eoc(t5-t6);ga3 = ga3*ga3*ga3;  
    ga4 = eoc(t7-t8);ga4 = ga4*ga4*ga4;

    tt = time*.167;
    mmt1=rot(ga1*PI-tt);
    mmt2=rot((ga1+ga2)*PI);
    mmt3=rot(ga3*PI+tt);
    gr=rot(ga1+ga2+ga3+ga4+T*.125);

    // set UV's
    vec2 uv = (2.*F.xy-R.xy)/max(R.x,R.y);
    vec3 C = vec3(.0075);

    // to make thigns fun faster and being
    // isometric and all - clipping the center
    if(uv.x<.65&&uv.x>-.65){

        //orthographic camera
        const float zoom = 17.;
        vec3 ro = vec3(uv*zoom,-zoom);
        vec3 rd = vec3(0,0,1.);

        rx = rot(-.78);
        ry = rot( .78*sin(tt*.5));

        ro.yz *= rx; ro.xz *= ry;
        rd.yz *= rx; rd.xz *= ry;

        vec3  p = ro + rd;
        float atten = 1.;
        float k = 1.;
        float d = 0.;
        float iv = 1.;
        for(int i=0;i<128;i++)
        {
            vec2 ray = map(p);
            vec3 n=vec3(0);
            float m = ray.y;

            d = i<32 ? ray.x*.5 : ray.x;
            p += rd * d *k;

            if (d*d < 1e-7) {

                C+=render(p,rd,ro,d,ray.y,n)*atten;

                atten *= .575;
                p += rd*.025;
                k = sign(map(p).x);

                if(m== 3.) {
                    rd=reflect(-rd,n);
                    p+=n*.01;
                } else {
                    vec3 rf=refract(rd,n,iv > 0. ? .85 : .1);
                    iv *= -1.;
                    if(length(rf) == 0.) rf = reflect(rd,n);
                    rd=rf;
                    p+=-n*.1;
                }
            } 

            if(distance(p,rd)>125.) { break; }
        }

    }

    if(C.r<.008&&C.g<.008&&C.b<.008) C = hash21(uv)>.5 ? C+.005 : C;
    C = pow(C, vec3(.4545));
    O = vec4(C,1.0);
}

#define MAX_STEPS 400
