float sphereSDF(vec3 p, vec3 c, float r)
{
    return length(p - c) - r;
}

float smin(float a, float b, float k) {
    float h = max(k - abs(a - b), 0.) / k;
    return min(a, b) - h * h * h * k * 1. / 6.;
}

float worldSDF(vec3 p)
{
    const float sqrt3d2 = 0.8660254;
    float sphere1 = sphereSDF(p, vec3(0.), 1.);
    float sphere2 = sphereSDF(p, vec3(0., 1.2, 1.), 1.);
    float sphere3 = sphereSDF(p, vec3(sqrt3d2, 1.2, -0.5), 1.);
    float sphere4 = sphereSDF(p, vec3(-sqrt3d2, 1.2, -0.5), 1.);

    float smoothFactor = sin(iTime / 3.) * 2. + 3.;

    float spheres = smin(smin(smin(sphere1, sphere3, smoothFactor), sphere2, smoothFactor), sphere4, smoothFactor);

    float plane = p.y - (-0.5);
    return smin(spheres, plane, smoothFactor);
}

vec3 normal(vec3 p)
{
    const vec2 small_step = vec2(0.001, 0.0);
    float gradient_x = worldSDF(p + small_step.xyy) - worldSDF(p - small_step.xyy);
    float gradient_y = worldSDF(p + small_step.yxy) - worldSDF(p - small_step.yxy);
    float gradient_z = worldSDF(p + small_step.yyx) - worldSDF(p - small_step.yyx);

    return normalize(vec3(gradient_x, gradient_y, gradient_z));
}

vec3 ray_march(vec3 ro, vec3 rd)
{
    float total_distance_traveled = 0.;
    const int NUMBER_OF_STEPS = 100;
    const float MIN_DISTANCE = 0.0001;
    const float MAX_DISTANCE = 1000.;

    for (int i = 0; i < NUMBER_OF_STEPS; i++)
    {
        vec3 current_position = ro + total_distance_traveled * rd;
        float closest_distance = worldSDF(current_position);
        if (closest_distance < MIN_DISTANCE)
        {
          vec3 light_position = vec3(2., -4., 3.);
          vec3 light_direction = normalize(current_position - light_position);
          float diffuse_factor = max(0.0, dot(light_direction, normal(current_position)));
          return vec3(1.) * float(i) / 100.;
        }
        if (closest_distance > MAX_DISTANCE)
        {
          return vec3(1.) * float(i) / 100.;
        }
        total_distance_traveled += closest_distance;
    }

    return vec3(1.);
}

vec3 rotateY(vec3 p, float angle)
{
    mat3 ry = mat3(
      cos(angle), 0., sin(angle),
      0., 1., 0.,
      -sin(angle), 0, cos(angle)
    );

    return ry * p;
}


