float fresnel(float n1, float n2, vec3 normal, vec3 incident, float f0, float f90)
{
        // Schlick aproximation
        float r0 = (n1-n2) / (n1+n2);
        r0 *= r0;
        float cosX = -dot(normal, incident);
        if (n1 > n2)
        {
            float n = n1/n2;
            float sinT2 = n*n*(1.0-cosX*cosX);
            // Total internal reflection
            if (sinT2 > 1.0)
                return f90;
            cosX = sqrt(1.0-sinT2);
        }
        float x = 1.0-cosX;
        float ret = r0+(1.0-r0)*x*x*x*x*x;

        // adjust reflect multiplier for object reflectivity
        return mix(f0, f90, ret);
}

vec3 rot(in vec3 inp, in vec3 rot){
    float s1 = sin(rot.x);
    float c1 = cos(rot.x);
    float s2 = sin(rot.y);
    float c2 = cos(rot.y);
    float s3 = sin(rot.z);
    float c3 = cos(rot.z);

    float a = c2 * c3 * inp.x;
    float b = (s1*s2*c3 - c1*s3)*inp.y;
    float g = (c1*s2*c3 + s1*s3)*inp.z;

    float a1 = c2 * s3 * inp.x;
    float b1 = (s1*s2*s3 + c1*c3)*inp.y;
    float g1 = (c1*s2*s3 - s1*c3)*inp.z;

    float a2 = -s2*inp.x;
    float b2 = s1*c2*inp.y;
    float g2 = c1*c2*inp.z;


    return vec3((a+b+g),(a1+b1+g1),(a2+b2+g2));
}



