    vec4 PlaneNormal = vec4(0.,1.,0.,0.);
    vec2 TorusSettings = vec2(2.,.5);
    vec3 BoxNormal1;
    vec3 BoxNormal2;
    vec3 BoxNormal3;
    vec3 BoxNormal4;
    vec3 BoxNormal5;
    vec3 BoxNormal6;
    vec3 BoxNormal7;
    vec3 BoxNormal8;
    vec3 BoxNormal9;
    vec3 BoxOrigin1 = vec3(0.,13.5,0.);//light
    vec3 BoxOrigin2 = vec3(10.,0.1,10.);//floor
    vec3 BoxOrigin3 = vec3(3.,1.2,-3.);//tiny cube on the right
    vec3 BoxOrigin4 = vec3(-3,8.,6.);//back wall mirror
    vec3 BoxOrigin5 = vec3(7.,10.,0.);//red wall
    vec3 BoxOrigin6 = vec3(-7.,10.,0.);//green wall
    vec3 BoxOrigin7 = vec3(0.,10.,10.);//z wall
    vec3 BoxOrigin8 = vec3(0.,14.,0.);//ceiling
    vec3 BoxOrigin9 = vec3(-4.,2.,-3.);//tiny cube
    vec3 TorOrigin = vec3(-4,4.,0.);
    vec3 R = vec3(0.2,.5,0.2);
    vec3 R1 = vec3(0.,0.2,0.);
    vec3 R2 = vec3(1.5,-0.6,0.);
    vec3 R3 = vec3(0.,-0.2,0.);
    vec3 SphereOrigin = vec3(2.,4.,4.);
    vec3 SphereOrigin2 = vec3(4.,8,2.);
    vec3 BoxIntersect1 = box(pos-BoxOrigin1,ray,BoxSize,BoxNormal1);
    vec3 BoxIntersect2 = box(pos,ray,BoxOrigin2,BoxNormal2);
    vec3 BoxIntersect3 = box(rot(pos-BoxOrigin3,R3),rot(ray,R3),vec3(1.,1.2,1.),BoxNormal3);
    vec3 BoxIntersect4 = box(rot(pos-BoxOrigin4,R),rot(ray,R),vec3(3.,4.,.2),BoxNormal4);
    vec3 BoxIntersect5 = box(pos-BoxOrigin5,ray,vec3(.5,10.,10.),BoxNormal5);
    vec3 BoxIntersect6 = box(pos-BoxOrigin6,ray,vec3(.5,10.,10.),BoxNormal6);
    vec3 BoxIntersect7 = box(pos-BoxOrigin7,ray,vec3(10.,10.,.5),BoxNormal7);
    vec3 BoxIntersect8 = box(pos-BoxOrigin8,ray,vec3(10.,.5,10.),BoxNormal8);
    vec3 BoxIntersect9 = box(rot(pos-BoxOrigin9,R1),rot(ray,R1),vec3(1,2,1),BoxNormal9);
    float TorusIntersect = torus(rot(pos-TorOrigin,R2),rot(ray,R2),TorusSettings);
    vec3 TorusNormals = torNormal(rot(pos+ray*TorusIntersect-TorOrigin,R2),TorusSettings);
    vec4 SphereIntersect = Sphere(pos,ray,SphereOrigin,2.);
    vec4 SphereIntersect2 = Sphere(pos,ray,SphereOrigin2,1.2);

    BoxNormal9 = rot(BoxNormal9,-R1);
    TorusNormals = rot(TorusNormals,-R2);
    BoxNormal3 = rot(BoxNormal3,-R3);

    float SceneDepth = min(min(BoxIntersect1.x,BoxIntersect2.x),TorusIntersect);
    SceneDepth = min(SceneDepth,BoxIntersect3.x);
    SceneDepth = min(min(SceneDepth,BoxIntersect4.x),BoxIntersect5.x);
    SceneDepth = min(SceneDepth, BoxIntersect6.x);
    SceneDepth = min(SceneDepth, SphereIntersect.w);
    SceneDepth = min(min(SceneDepth, SphereIntersect2.w),BoxIntersect7.x);
    SceneDepth = min(SceneDepth,BoxIntersect7.x);
    SceneDepth = min(SceneDepth,BoxIntersect8.x);
    SceneDepth = min(SceneDepth,BoxIntersect9.x);
    //decide all the object parameters
    if (TorusIntersect == SceneDepth){
    mat.albedo = vec3(1.);
    mat.emission = vec3(0.);
    hit.normal = TorusNormals;
    mat.specularity = 1.;
    mat.specularColor = vec3(1.);
    mat.roughness = 0.6;
    }
    if (BoxIntersect1.x == SceneDepth){
    mat = stock();
    mat.albedo = vec3(1.,0.8,0);
    mat.emission = vec3(10.,4.,3.);
    hit.normal = normalize(BoxNormal1);
    mat.specularColor = vec3(1.);
    }
    if (BoxIntersect2.x == SceneDepth){
    mat = stock();
    mat.albedo = vec3(1.);
    mat.emission = vec3(0.);
    hit.normal = normalize(BoxNormal2);
    mat.roughness = 1.;
    mat.specularColor = vec3(1.);
    }
    if (BoxIntersect3.x == SceneDepth){
    mat = stock();
    mat.albedo = vec3(1.,1.,1.);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal3);
    mat.roughness = 0.1;
    mat.specularity = .2;
    mat.specularColor = vec3(1.);
    }
    if (BoxIntersect4.x == SceneDepth){
    mat = stock();
    mat.albedo = vec3(1.,0.3,0.2);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(rot(BoxNormal4,-R));
    mat.roughness = 0.1;
    mat.specularity = 1.;
    mat.specularColor = vec3(1.);
    }
    if (BoxIntersect5.x == SceneDepth){
    mat = stock();
    mat.albedo = vec3(1.,0.3,0.2);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal5);
    mat.roughness = 1.;
    mat.specularity = 0.;
    mat.specularColor = vec3(1.);
    }
    if (BoxIntersect6.x == SceneDepth){
    mat.albedo = vec3(.3,1.,0.2);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal6);
    mat.roughness = 1.;
    mat.specularity = 0.;
    mat.specularColor = vec3(1.);
    }    

    if (SphereIntersect.w == SceneDepth){
    mat.albedo = vec3(1.);
    mat.emission = vec3(0.);
    hit.normal = vec3(SphereIntersect.xyz);
    mat.specularity = 1.;
    mat.roughness = 0.5;
    mat.specularColor = vec3(1.);
    }
    if (SphereIntersect2.w == SceneDepth){
    mat = stock();
    mat.refChance = 1.;
    hit.normal = vec3(SphereIntersect2.xyz);
    mat.IOR = (1.2);
    mat.specularity = 0.1;
    mat.roughness = 0.1;
    mat.refRoughness = 0.1;
    }

    if (BoxIntersect7.x == SceneDepth){
    mat.albedo = vec3(1.,1.,1.);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal7);
    mat.roughness = 1.;
    mat.specularity = 0.;
    mat.specularColor = vec3(1.);
    }  
    if (BoxIntersect8.x == SceneDepth){
    mat.albedo = vec3(1.,1.,1.);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal8);
    mat.roughness = 1.;
    mat.specularity = 0.;
    mat.specularColor = vec3(1.);
    } 
    if (BoxIntersect9.x == SceneDepth){
    mat.albedo = vec3(1.,1.,1.);
    mat.emission = vec3(0.,0.,0.);
    hit.normal = normalize(BoxNormal9);
    mat.roughness = 1.;
    mat.specularity = 0.;
    mat.specularColor = vec3(1.);
    }  
    hit.distance = SceneDepth;
    hit.normal = normalize(hit.normal);
    return hit.normal;
}
vec3 PathTrace(in vec3 pos, in vec3 ray, inout uint rngstate, out HitInfo Hit){
    vec3 StartNormal;
    vec3 T = vec3(1.);
    vec3 R = vec3(0.);

    Material Mat;
    for(int i = 0; i <= 10; i++){

    Mat = stock();
    Hit.distance = BIG;
    Hit.inside = false;
    Scene(pos,ray,Hit,Mat);
    //if(Hit.distance < -1.){
    //    break;
    //}
    //for(int j = 0; j < 20; j++){
    //if(Hit.distance == BIG){

    //R += SRGBToLinear((texture(iChannel1, ray).rgb * T)*0.7);
    //Scene(pos,ray,Hit,Mat);

    //break;

    //}
    //}

    if(Hit.inside){
    T *= exp(-Mat.refColor*Hit.distance);

    }

    float specChance = Mat.specularity;
    float refChance = Mat.refChance;
    float rayProb = 1.;

    if(specChance > 0.){
        specChance = fresnel(
        Hit.inside ? Mat.IOR : 1.0,
        !Hit.inside ? Mat.IOR : 1.0,
        ray, Hit.normal, Mat.specularity, 1.0);  
        float chanceMult = (1.-specChance)/(1.-Mat.specularity);
        refChance *= chanceMult;
    }

    // calculate whether we are going to do a diffuse, specular, or refractive ray
    float doSpec = 0.;
    float doRef = 0.;
    float raySelRoll = RandomFloat01(rngstate);

    if(specChance > 0. && raySelRoll < specChance){
        doSpec = 1.;
        rayProb = specChance;
    }
    else if (refChance > 0. && raySelRoll < specChance + refChance)

    {
        doRef = 1.;
        rayProb = refChance;
    }

    else 

    {
        rayProb = 1.0-(specChance + refChance);
    }

    rayProb = max(rayProb,0.001);

    if(doRef == 1.)

    {
        pos = (pos+ray * Hit.distance) - Hit.normal * 0.01;
    }

    else 

    {
        pos = (pos+ray * Hit.distance) + Hit.normal * 0.01;
    }

    float spec = (RandomFloat01(rngstate) < specChance) ? 1.0f : 0.0f;

    // avoid numerical issues causing a divide by zero
    rayProb = max(rayProb, 0.001f);  



    vec3 dif = normalize(Hit.normal + RUV(rngstate));



    vec3 glos = reflect(ray,Hit.normal);
    glos = normalize(mix(glos, dif, Mat.roughness * Mat.roughness));


    vec3 ref = refract(ray, Hit.normal,Hit.inside ? Mat.IOR : 1. / Mat.IOR );
    ref = normalize(mix(ref,normalize(-Hit.normal+RUV(rngstate)),Mat.refRoughness * Mat.refRoughness));


    ray = mix(dif, glos, doSpec);

    ray = mix(ray, ref, doRef);

    R += Mat.emission * T;


    if(doRef == 0.){

    T *= mix(Mat.albedo,Mat.specularColor,doSpec);

    }



    T /= rayProb;


    //roulette 
    float p = max(T.r, max(T.g, T.b));
    if (RandomFloat01(rngstate) > p)
        break;

    // Add the energy we 'lose' by randomly terminating paths

    T *= 1.0f / p;

    }
    return R;
}
