    vec3 pos = vec3(0.,0.,0.);//camera coord position, change z for distance
    vec3 ray = vec3((fragCoord+jitter-iResolution.xy*.5)/iResolution.x,cameraDistance);//normalize shit
    Rotate(pos,angle);//Rotate position, remove for fps camera
