// License CC0: Stars and galaxy
// Bit of sunday tinkering lead to stars and a galaxy
// Didn't turn out as I envisioned but it turned out to something
// that I liked so sharing it.

// Controls how many layers of stars
#define LAYERS            5.0

#define PI                3.141592654
#define TAU               (2.0*PI)
#define TIME              mod(iTime, 30.0)
#define TTIME             (TAU*TIME)
#define RESOLUTION        iResolution
#define ROT(a)            mat2(cos(a), sin(a), -sin(a), cos(a))

// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
float sRGB(float t) { return mix(1.055*pow(t, 1./2.4) - 0.055, 12.92*t, step(t, 0.0031308)); }
// License: Unknown, author: nmz (twitter: @stormoid), found: https://www.shadertoy.com/view/NdfyRM
vec3 sRGB(in vec3 c) { return vec3 (sRGB(c.x), sRGB(c.y), sRGB(c.z)); }

// License: Unknown, author: Matt Taylor (https://github.com/64), found: https://64.github.io/tonemapping/
vec3 aces_approx(vec3 v) {
  v = max(v, 0.0);
  v *= 0.6f;
  float a = 2.51f;
  float b = 0.03f;
  float c = 2.43f;
  float d = 0.59f;
  float e = 0.14f;
  return clamp((v*(a*v+b))/(v*(c*v+d)+e), 0.0f, 1.0f);
}

// License: Unknown, author: Unknown, found: don't remember
float tanh_approx(float x) {
  //  Found this somewhere on the interwebs
  //  return tanh(x);
  float x2 = x*x;
  return clamp(x*(27.0 + x2)/(27.0+9.0*x2), -1.0, 1.0);
}


// License: WTFPL, author: sam hocevar, found: https://stackoverflow.com/a/17897228/418488
const vec4 hsv2rgb_K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
vec3 hsv2rgb(vec3 c) {
  vec3 p = abs(fract(c.xxx + hsv2rgb_K.xyz) * 6.0 - hsv2rgb_K.www);
  return c.z * mix(hsv2rgb_K.xxx, clamp(p - hsv2rgb_K.xxx, 0.0, 1.0), c.y);
}

// License: MIT OR CC-BY-NC-4.0, author: mercury, found: https://mercury.sexy/hg_sdf/
vec2 mod2(inout vec2 p, vec2 size) {
  vec2 c = floor((p + size*0.5)/size);
  p = mod(p + size*0.5,size) - size*0.5;
  return c;
}

// License: Unknown, author: Unknown, found: don't remember
vec2 hash2(vec2 p) {
  p = vec2(dot (p, vec2 (127.1, 311.7)), dot (p, vec2 (269.5, 183.3)));
  return fract(sin(p)*43758.5453123);
}

vec2 shash2(vec2 p) {
  return -1.0+2.0*hash2(p);
}

vec3 toSpherical(vec3 p) {
  float r   = length(p);
  float t   = acos(p.z/r);
  float ph  = atan(p.y, p.x);
  return vec3(r, t, ph);
}


// License: CC BY-NC-SA 3.0, author: Stephane Cuillerdier - Aiekick/2015 (twitter:@aiekick), found: https://www.shadertoy.com/view/Mt3GW2
vec3 blackbody(float Temp) {
  vec3 col = vec3(255.);
  col.x = 56100000. * pow(Temp,(-3. / 2.)) + 148.;
  col.y = 100.04 * log(Temp) - 623.6;
  if (Temp > 6500.) col.y = 35200000. * pow(Temp,(-3. / 2.)) + 184.;
  col.z = 194.18 * log(Temp) - 1448.6;
  col = clamp(col, 0., 255.)/255.;
  if (Temp < 1000.) col *= Temp/1000.;
  return col;
}


// License: MIT, author: Inigo Quilez, found: https://www.shadertoy.com/view/XslGRr
float noise(vec2 p) {
  // Found at https://www.shadertoy.com/view/sdlXWX
  // Which then redirected to IQ shader
  vec2 i = floor(p);
  vec2 f = fract(p);
  vec2 u = f*f*(3.-2.*f);

  float n =
         mix( mix( dot(shash2(i + vec2(0.,0.) ), f - vec2(0.,0.)), 
                   dot(shash2(i + vec2(1.,0.) ), f - vec2(1.,0.)), u.x),
              mix( dot(shash2(i + vec2(0.,1.) ), f - vec2(0.,1.)), 
                   dot(shash2(i + vec2(1.,1.) ), f - vec2(1.,1.)), u.x), u.y);

  return 2.0*n;              
}

float fbm(vec2 p, float o, float s, int iters) {
  p *= s;
  p += o;

  const float aa = 0.5;
  const mat2 pp = 2.04*ROT(1.0);

  float h = 0.0;
  float a = 1.0;
  float d = 0.0;
  for (int i = 0; i < iters; ++i) {
    d += a;
    h += a*noise(p);
    p += vec2(10.7, 8.3);
    p *= pp;
    a *= aa;
  }
  h /= d;

  return h;
}

float height(vec2 p) {
  float h = fbm(p, 0.0, 5.0, 5);
  h *= 0.3;
  h += 0.0;
  return (h);
}

vec3 stars(vec3 ro, vec3 rd, vec2 sp, float hh) {
  vec3 col = vec3(0.0);

  const float m = LAYERS;
  hh = tanh_approx(20.0*hh);

  for (float i = 0.0; i < m; ++i) {
    vec2 pp = sp+0.5*i;
    float s = i/(m-1.0);
    vec2 dim  = vec2(mix(0.05, 0.003, s)*PI);
    vec2 np = mod2(pp, dim);
    vec2 h = hash2(np+127.0+i);
    vec2 o = -1.0+2.0*h;
    float y = sin(sp.x);
    pp += o*dim*0.5;
    pp.y *= y;
    float l = length(pp);

    float h1 = fract(h.x*1667.0);
    float h2 = fract(h.x*1887.0);
    float h3 = fract(h.x*2997.0);

    vec3 scol = mix(8.0*h2, 0.25*h2*h2, s)*blackbody(mix(3000.0, 22000.0, h1*h1));

    vec3 ccol = col + exp(-(mix(6000.0, 2000.0, hh)/mix(2.0, 0.25, s))*max(l-0.001, 0.0))*scol;
    col = h3 < y ? ccol : col;
  }

  return col;
}

// License: MIT, author: Inigo Quilez, found: https://iquilezles.org/articles/spherefunctions
vec2 raySphere(vec3 ro, vec3 rd, vec4 sph) {
  vec3 oc = ro - sph.xyz;
  float b = dot( oc, rd );
  float c = dot( oc, oc ) - sph.w*sph.w;
  float h = b*b - c;
  if( h<0.0 ) return vec2(-1.0);
  h = sqrt( h );
  return vec2(-b - h, -b + h);
}


vec4 moon(vec3 ro, vec3 rd, vec2 sp, vec3 lp, vec4 md) {
  vec2 mi = raySphere(ro, rd, md);

  vec3 p    = ro + mi.x*rd;
  vec3 n    = normalize(p-md.xyz);
  vec3 r    = reflect(rd, n);
  vec3 ld   = normalize(lp - p);
  float fre = dot(n, rd)+1.0;
  fre = pow(fre, 15.0);
  float dif = max(dot(ld, n), 0.0);
  float spe = pow(max(dot(ld, r), 0.0), 8.0);
  float i = 0.5*tanh_approx(20.0*fre*spe+0.05*dif);
  vec3 col = blackbody(1500.0)*i+hsv2rgb(vec3(0.6, mix(0.6, 0.0, i), i));

  float t = tanh_approx(0.25*(mi.y-mi.x));

  return vec4(vec3(col), t);
}

vec3 sky(vec3 ro, vec3 rd, vec2 sp, vec3 lp, out float cf) {
  float ld = max(dot(normalize(lp-ro), rd),0.0);
  float y = -0.5+sp.x/PI;
  y = max(abs(y)-0.02, 0.0)+0.1*smoothstep(0.5, PI, abs(sp.y));
  vec3 blue = hsv2rgb(vec3(0.6, 0.75, 0.35*exp(-15.0*y)));
  float ci = pow(ld, 10.0)*2.0*exp(-25.0*y); 
  vec3 yellow = blackbody(1500.0)*ci;
  cf = ci;
  return blue+yellow;
}

vec3 galaxy(vec3 ro, vec3 rd, vec2 sp, out float sf) {
  vec2 gp = sp;
  gp *= ROT(0.67);
  gp += vec2(-1.0, 0.5);
  float h1 = height(2.0*sp);
  float gcc = dot(gp, gp);
  float gcx = exp(-(abs(3.0*(gp.x))));
  float gcy = exp(-abs(10.0*(gp.y)));
  float gh = gcy*gcx;
  float cf = smoothstep(0.05, -0.2, -h1);
  vec3 col = vec3(0.0);
  col += blackbody(mix(300.0, 1500.0, gcx*gcy))*gcy*gcx;
  col += hsv2rgb(vec3(0.6, 0.5, 0.00125/gcc));
  col *= mix(mix(0.15, 1.0, gcy*gcx), 1.0, cf);
  sf = gh*cf;
  return col;
}

vec3 grid(vec3 ro, vec3 rd, vec2 sp) {
  const float m = 1.0;

  const vec2 dim = vec2(1.0/8.0*PI);
  vec2 pp = sp;
  vec2 np = mod2(pp, dim);

  vec3 col = vec3(0.0);

  float y = sin(sp.x);
  float d = min(abs(pp.x), abs(pp.y*y));

  float aa = 2.0/RESOLUTION.y;

  col += 2.0*vec3(0.5, 0.5, 1.0)*exp(-2000.0*max(d-0.00025, 0.0));

  return 0.25*tanh(col);
}

vec3 color(vec3 ro, vec3 rd, vec3 lp, vec4 md) {
  vec2 sp = toSpherical(rd.xzy).yz;

  float sf = 0.0;
  float cf = 0.0;
  vec3 col = vec3(0.0);

  vec4 mcol = moon(ro, rd, sp, lp, md);

  col += stars(ro, rd, sp, sf)*(1.0-tanh_approx(2.0*cf));
  col += galaxy(ro, rd, sp, sf);
  col = mix(col, mcol.xyz, mcol.w);
  col += sky(ro, rd, sp, lp, cf);
  col += grid(ro, rd, sp);

  if (rd.y < 0.0)
  {
    col = vec3(0.0);
  }

  return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  vec2 q = fragCoord/iResolution.xy;
  vec2 p = -1.0 + 2.0*q;
  p.x *= RESOLUTION.x/RESOLUTION.y;

  vec3 ro = vec3(0.0, 0.0, 0.0);
  vec3 lp = 500.0*vec3(1.0, -0.25, 0.0);
  vec4 md = 50.0*vec4(vec3(1.0, 1., -0.6), 0.5);
  vec3 la = vec3(1.0, 0.5, 0.0);
  vec3 up = vec3(0.0, 1.0, 0.0);
  la.xz *= ROT(TTIME/60.0-PI/2.0);

  vec3 ww = normalize(la - ro);
  vec3 uu = normalize(cross(up, ww));
  vec3 vv = normalize(cross(ww,uu));
  vec3 rd = normalize(p.x*uu + p.y*vv + 2.0*ww);
  vec3 col= color(ro, rd, lp, md);

  col *= smoothstep(0.0, 4.0, TIME)*smoothstep(30.0, 26.0, TIME);
  col = aces_approx(col);
  col = sRGB(col);

  fragColor = vec4(col,1.0);
}

#define PI 3.14151692

#define rho_offset fract(0.5 + 0.5 * iTime) //dspnote param: 0 - 1
#define theta_offset fract(0.5 + 0.5 * iTime) //dspnote param: 0 - 1

float scale = 6.0/PI;
vec3 diskColor = vec3(0.4, 0.4, 0.3);
vec3 hBarColor = vec3(0.2, 1.0, 0.2);
vec3 vBarColor = vec3(1.0, 0.2, 0.2);

vec2 logPolar(vec2 p) {
	p = vec2(log(length(p)), atan(p.y, p.x));
	return p;
}

float line(float pos, float aaSize) {
	return smoothstep(-1.3*aaSize, -0.5*aaSize, pos) - 
		smoothstep(0.5*aaSize, 1.3*aaSize, pos);
}

float disk(vec2 pos, float aaSize) {
	return 1.0-smoothstep(0.3-aaSize, 0.3+aaSize, length(pos));
}

vec3 polka(vec2 p, float aaSize) {
	p *= scale;
	vec2 diskP = p - vec2(rho_offset, theta_offset)*3.0;
	diskP = fract(diskP) * 2.0 - 1.0;
	vec3 ret = vec3(1.0);
	ret = mix(ret, diskColor, disk(diskP, aaSize));
	ret = mix(ret, hBarColor, line(p.x, aaSize));
	ret = mix(ret, vBarColor, line(p.y, aaSize));
	return ret;
}

vec3 cartesianPolka(vec2 p) {
	p *= 4.0;
	float aaSize = length(1.0/iResolution.xy) * 7.0;
	vec3 ret = polka(p, aaSize);

    // <darken outside>
	if (p.y < -PI || p.y > PI)
		ret *= 0.6;
    // </darken outside>
    // <darken negative area>
	else if (p.x < -0.0)
		ret *= 0.8;
    // </darken negative area>

    return ret;
}

vec3 logPolarPolka(vec2 p) {
	p *= 4.0;
    vec2 lpp = logPolar(p);
	float aaSize = length(lpp - logPolar(p+1.0/iResolution.xy)) * 7.0;
	vec3 ret = polka(lpp, aaSize);

    // <darken outside> (no meaning)
	if (lpp.y < -PI || lpp.y > PI)
		ret *= 0.6;
    // </darken outside>
    // <darken negative area>
	if (lpp.x < -0.0)
		ret *= 0.8;
    // </darken negative area>

    return ret;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = (2.0 * gl_FragCoord.xy - iResolution.xy) / iResolution.y;
	vec2 quarter = vec2(1.0, 0.0);
	vec3 ret;
	if (p.x < 0.0)
		ret = cartesianPolka(p + quarter);
	else
		ret = logPolarPolka(p - quarter);
	fragColor = vec4(ret, 1.0);
}

#define MAX_STEPS 100   //光线前进最大步数
#define MAX_DIST 100.0  //最大距离100
#define SURF_DIST 0.01  //点到物体最近的表面距离小于这个距离即理解为击中表面

//球体sdf,sphere前三个参数是球心位置坐标,最后一个参数是半径
float SphereSDF(vec3 p,vec4 sphere)
{
    return length(p-sphere.xyz)-sphere.w;
}

//胶囊体sdf,a/b是胶囊体两端半球的球心,r是半球的半径
float CapsuleSDF(vec3 p,vec3 a,vec3 b,float r)
{
    vec3 ap = p-a;
    vec3 ab = b-a;

    //ap在ab方向上的投影除以ab的长度,并截止到(0,1)之间,
    //所以点a上面部分是c=a,点b下面的是c=b
    //上面的部分离胶囊体最近距离是ap的距离减半径,下面同理
    float t = dot(ab,ap)/dot(ab,ab);
    t = clamp(t,0.0,1.0);
    vec3 c = a+t*ab;

    return length(p-c)-r;
}

//环面sdf,a表示换面中心点位置,r.x表示大半径,r.y表示小半径
float TorusSDF(vec3 p,vec3 a,vec2 r)
{
    //点p在xz平面内到中心点的距离减去大半径
    float x = length(p.xz-a.xz)-r.x;

    //根据 点p在xz平面内到中心点的距离和y方向的距离 
    //计算p到外环中间的距离,然后减去外环的半径
    //得到点p距物体表面的最小值
    return length(vec2(x,p.y-a.y))-r.y;
}

//box,盒子sdf(立方体或长方体),a表示盒子中心点坐标,size表示长宽高
float BoxSDF(vec3 ap,vec3 size)
{
    return length(max(abs(ap)-size,0.0));
}

//Cylinder,圆柱sdf,类似胶囊体
float CylinderSDF(vec3 p,vec3 a,vec3 b,float r)
{
    vec3 ap = p-a;
    vec3 ab = b-a;

    float t = dot(ap,ab)/dot(ab,ab);   
    vec3 c = a+t*ab;

    float d = length(p-c)-r;
    float y = abs(t-0.5)-0.5;
    y *= length(ab);

    float dist = length(max(vec2(d,y),0.0));

    return dist+min(max(d,y),0.0);
}
//输入角度,返回某个平面的旋转矩阵
mat2 Rot(float t)
{
    float s = sin(t);
    float c = cos(t);
    return mat2(c,-s,s,c);
}

//获得每一步的距离,场景中只有一个平面和一个球
float GetDist(vec3 p)
{   
    //Sphere,球,前三个参数是圆心位置,第四个是半径
    vec4 sphere = vec4(-6,1,10,1);

    //胶囊体参数
    vec3 a = vec3(-3.0,2.0,10.0);//胶囊体上半球的球心
    vec3 b = vec3(-3.0,0.5,10.0);//胶囊体下半球的球心
    float t = 0.5;    //两个球的半径

    //环面参数
    vec3 a1 = vec3(0,1.0,10.0);//环面中心点位置坐标
    vec2 r = vec2(1.0,0.3);//x表示大半径,y表示小半径

    //盒子参数
    vec3 a2 = vec3(3.0,1.0,10.0);//立方体中心坐标
    vec3 a2p = a2-p;
    //a2p.xz *= Rot(iTime);
    //a2p -= vec3(2,-1,0); //平移,translation
    vec3 size = vec3(1);

    //圆柱体参数
    vec3 a3 = vec3(6.0,2.0,10.0);
    vec3 b3 = vec3(6.0,0.0,10.0);
    float r3 = 0.5;

    //点p距离场景中各个物体的距离
    float sphereDist = SphereSDF(p,sphere);//点p到球体的最小距离
    float planeDist = p.y;//点p到屏幕的距离,片面位于y=0
    float capsuleDist = CapsuleSDF(p,a,b,t);
    float torusDist = TorusSDF(p,a1,r);
    float boxDist = BoxSDF(a2p,size);
    float cylinderDist = CylinderSDF(p,a3,b3,r3);

    //选出最小的距离,即每次前进的安全距离
    float d = min(sphereDist,planeDist);
    d = min(d,capsuleDist);
    d = min(d,torusDist);
    d = min(d,boxDist);
    d = min(d,cylinderDist);

    return d;
}
//光线前进,计算每个方向上光线前进多少距离击中表面,即到物体表面的距离
float RayMarching(vec3 ro,vec3 rd)
{
    float d0 = 0.0;

    for(int i=0;i<MAX_STEPS;i++)
    {
        vec3 p = ro+rd*d0;
        float ds = GetDist(p);//获得每一次前进的距离
        d0 += ds;
        //前进总距离大于MAX_DIST认为是该方向击中不了物体,
        //前进的距离小于SURF_DIST认为是击中物体表面
        if(d0>MAX_DIST || ds<SURF_DIST) break;
    }

    return d0;
}
//获得点p的法向量
vec3 GetNormal(vec3 p)
{
    float d = GetDist(p);
    vec2 e = vec2(0.001,0);

    vec3 normal = d-vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx)
    );

    return normalize(normal);
}

float CalcAO(vec3 p,vec3 n)
{
    float res = 0.;
    float scale = 1.;
    for (int i=0;i<5;i++)
    {
        float h = 0.01+0.03*float(i);
        float d = GetDist(p+n*h);
        res += (h-d)*scale;
        scale *= 0.95;
        if (res > 0.35) break;
    }
    return clamp(1.-3.*res,0.,1.);
}

//获得点p的颜色,blinn-phong模型
vec3 GetLight(vec3 p,vec3 ro)
{
    vec3 lightPos = vec3(3,5,5);//光源位置
    vec3 lightCol = vec3(1.0);//光源颜色
    lightPos.xz += vec2(sin(2.0*iTime),cos(2.0*iTime));

    //红宝石材质属性
    vec3 k_a = vec3(0.1745,0.01175,0.01175);//环境反射率
    vec3 k_d = vec3(0.61424,0.041362,0.04136);//漫反射率
    vec3 k_s = vec3(0.727811,0.626959,0.626959);//镜面反射率
    float shininess = 76.8;//高光指数

    vec3 lightDir = normalize(lightPos-p);//光向量
    vec3 normal = GetNormal(p);//获得点p的法向量

    //环境光
    vec3 ambient = k_a*0.5;

    //漫反射
    float dif = clamp(dot(lightDir,normal),0.0,1.0);
    vec3 diffuse = lightCol*k_d*dif;

    //高光
    vec3 viewDir = normalize(ro-p);//视向量
    vec3 halfDir = normalize(viewDir+lightDir);//半程向量
    float spec = pow(max(dot(halfDir,normal),0.0),shininess);//高光系数
    vec3 specular = lightCol*k_s*spec;//高光

    //阴影,将点p沿法线外移两个SURF_DIST的距离,然后在向光源的
    //方向RayMarching计算距离,若距离小于光源点到p的距离
    //那么说明点p到光源之间有物体遮挡,是阴影部位
    vec3 p2 = p + normal*SURF_DIST*2.0;
    float d = RayMarching(p2,lightDir);
    if(d<length(lightPos-p)) return ambient;

    vec3 color = ambient+diffuse+specular;

    //计算ao
    float ao = CalcAO(p,normal);

    return color*ao;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec2 mo = iMouse.xy/iResolution.xy;

    vec3 col = vec3(0.0);

    float time = iTime;

    vec3 ro = vec3(0.0,3.0,1.0);
    //射线原点随鼠标移动距离进行旋转
    ro.yz *= Rot(-mo.y+.4);
    ro.xz *= Rot(-mo.x*6.2831);
    vec3 rd = normalize(vec3(uv.x,uv.y,1.0));//射线方向

    float d = RayMarching(ro,rd);//获得每个rd方向上需要前进的距离
    vec3 p = ro+rd*d;//根据前进距离和rd求得射线击中物体的点

    //blinn-phong光照模型
    col = GetLight(p,ro);    

    fragColor = vec4(col,1.0);
}

float rand(vec2 co){
    return fract(sin(dot(co, vec2(12.9898, 78.233))) * 43758.5453);
}

float getClosest( vec3[12] a, int n, vec2 point )
{
    float distance = length(a[0].xy - point) - a[0].z;
    for(int i = 1; i<n; i++){
        float tmp = length(a[i].xy - point)-a[i].z;
        if(tmp<distance)
        {
            distance = tmp;
        }
    }
    return distance;
}


