{
    float threshhold = 1.0;
    const int maxSteps = 50;
    vec2 center = iMouse.xy;

    vec3 planets[12];
    for(int i = 0; i<planets.length(); i++)
    {
        planets[i] = vec3((i%3+1)*int(iResolution.x)/4,(1+i/3)*int(iResolution.y)/5, 15);
    }

    vec2 diff = center - fragCoord;

    int i = 0;

    vec2 point = fragCoord;
    bool collision = true;

    float progress = 0.0;
    float lightContribution = 1.0;

    for(i = 0; i < maxSteps; i++){

        float dist = length(center - point);

        float close = getClosest(planets, 12, point)*.5*(1.0-.2*rand(fragCoord));

        progress += close;
        lightContribution = min(lightContribution, 10.0*(close)/progress);



        if(length(progress - diff) < threshhold){
            collision = false;
            break;
        }
        point = (diff*close/length(diff)) + point;
        //collision = true;

    }




    float bright = lightContribution;

    if(length(diff) - progress > .1 &&  collision){

        bright = 0.0;

    }

    float d = length(diff);
    d /= 1000.0;
    float light = 1.0/(d*d+1.0);
    bright*=.5*light;
    fragColor = vec4(bright);




    vec4 column = vec4(0.0);
    for(int j = 0; j<planets.length(); j++)
    {
         float add = step(length(fragCoord - planets[j].xy), planets[j].z);
         vec2 normal = ((fragCoord - planets[j].xy)/length(fragCoord - planets[j].xy));
         vec2 normalDiff = diff.xy/length(diff.xy);
         float dotp = normal.x*normalDiff.x+normal.y*normalDiff.y;
         add *= pow(dotp,1.0);
         column+=add;


    }

    fragColor *= 1.0;
    //fragColor+=column*light*.5;


}

