//Complex (C) functions
vec2 cis(float theta) {
    return vec2(cos(theta), sin(theta));
}

vec2 mulComplex(vec2 z, vec2 w) {
    return z.x * w + z.y * vec2(-w.y, w.x);
}

//Quaternion (H) functions
//https://en.wikipedia.org/wiki/Quaternion
vec4 conjQuat(vec4 q) {
    return vec4(q.x, -q.yzw);
}

vec4 mulQuat(vec4 p, vec4 q) {
    //Quaternion multiplication (Order matters)
    return vec4(p.x * q.x - dot(p.yzw, q.yzw), p.x * q.yzw + q.x * p.yzw + cross(p.yzw, q.yzw));
}

vec4 sqrQuat(vec4 q) {
    return vec4(q.x * q.x - dot(q.yzw, q.yzw), (q.x + q.x) * q.yzw);
}

vec4 cubeQuat(vec4 q) {
    float r2 = q.x * q.x;
    float v2 = dot(q.yzw, q.yzw);
    return q * vec4(r2 - 3. * v2, vec3(3. * r2 - v2));
}

vec4 recipQuat(vec4 q) {
    //Quaternion reciprocal
    return vec4(q.x, -q.yzw) / dot(q, q);
}

vec4 sqrtQuat(vec4 q) {
    return vec4(sqrt(0.5 * (length(q) + q.x)), normalize(q.yzw) * sqrt(0.5 * (length(q) - q.x)));
}

vec4 expQuat(vec4 q) {
    float r = length(q.yzw);
    return exp(q.x) * vec4(cos(r), normalize(q.yzw) * sin(r));
}

vec4 logQuat(vec4 q) {
    return vec4(0.5 * log(dot(q, q)), normalize(q.yzw) * acos(q.x / length(q)));
}

vec4 powQuat(vec4 q, float x) {
    float r = length(q);
    float phi = acos(q.x / r);
    return pow(r, x) * vec4(cos(x * phi), normalize(q.yzw) * sin(x * phi));
}

vec4 powerQuat(vec4 p, vec4 q) {
    return expQuat(mulQuat(logQuat(p), q));
}

