// raymarching based from https://www.shadertoy.com/view/wdGGz3
#define USE_MOUSE 0
#define MAX_STEPS 100
#define MAX_DIST 100.
#define SURF_DIST .001
#define Rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
#define antialiasing(n) n/min(iResolution.y,iResolution.x)
#define S(d,b) smoothstep(antialiasing(1.0),b,d)
#define B(p,s) max(abs(p).x-s.x,abs(p).y-s.y)
#define MATERIAL 0
#define MATERIAL1 1

#define ZERO (min(iFrame,0))

vec2 combine(vec2 val1, vec2 val2 ){
    return (val1.x < val2.x)?val1:val2;
}

vec2 hash22(vec2 p) {
    p = p*mat2(127.1,311.7,269.5,183.3);
    p = -1.0 + 2.0 * fract(sin(p)*43758.5453123);
    return sin(p*6.283);
}

float perlin_noise(vec2 p) {
    vec2 pi = floor(p);
    vec2 pf = p - pi;

    // interpolation
    vec2 w = pf * pf * (3.0 - 2.0 * pf);

    float f00 = dot(hash22(pi + vec2(0.0, 0.0)), pf - vec2(0.0, 0.0));
    float f01 = dot(hash22(pi + vec2(0.0, 1.0)), pf - vec2(0.0, 1.0));
    float f10 = dot(hash22(pi + vec2(1.0, 0.0)), pf - vec2(1.0, 0.0));
    float f11 = dot(hash22(pi + vec2(1.0, 1.0)), pf - vec2(1.0, 1.0));

    // mixing top & bottom edges
    float xm1 = mix(f00, f10, w.x);
    float xm2 = mix(f01, f11, w.x);

    // mixing to point
    float ym = mix(xm1, xm2, w.y); 

    return ym;
}

float sdBox( vec3 p, vec3 b )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0);
}

vec2 GetDist(vec3 p) {
    p*=0.8;
    vec3 prevP = p;

    p = prevP;
    p.x+=iTime*2.1;

    float n = perlin_noise(p.xz*0.3)*3.0;

    p = prevP;

    p.y+=n-0.5;
    p.x+=sin(n*0.1)*0.6;
    float d = length(p)-0.5;
    d = sdBox(p,vec3(1.2,0.01,0.9));
    p = prevP;
    float f = (p.y+n)+0.5;
    p = prevP;
    vec2 model = vec2(f*0.6,MATERIAL);
    vec2 model1 = vec2(d*0.6,MATERIAL1);

    return combine(model,model1);
}

vec2 RayMarch(vec3 ro, vec3 rd, float side, int stepnum) {
    vec2 dO = vec2(0.0);

    for(int i=0; i<stepnum; i++) {
        vec3 p = ro + rd*dO.x;
        vec2 dS = GetDist(p);
        dO.x += dS.x*side;
        dO.y = dS.y;

        if(dO.x>MAX_DIST || abs(dS.x)<SURF_DIST) break;
    }

    return dO;
}

vec3 GetNormal(vec3 p) {
    float d = GetDist(p).x;
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy).x,
        GetDist(p-e.yxy).x,
        GetDist(p-e.yyx).x);

    return normalize(n);
}

vec3 R(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = p+f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i-p);
    return d;
}


vec3 diffuseMaterial(vec3 n, vec3 rd, vec3 p, vec3 col) {
    vec3 diffCol = vec3(0.0);
    vec3 lightDir = normalize(vec3(1,10,-10));
    float diff = clamp(dot(n,lightDir),0.0,1.0);
    float shadow = step(RayMarch(p+n*0.3,lightDir,1.0, 15).x,0.9);
    float skyDiff = clamp(0.5+0.5*dot(n,vec3(0,1,0)),0.0,1.0);
    float bounceDiff = clamp(0.5+0.5*dot(n,vec3(0,-1,0)),0.0,1.0);
    diffCol = col*vec3(-0.5)*diff*shadow;
    diffCol += col*vec3(1.0,1.0,0.9)*skyDiff;
    diffCol += col*vec3(0.3,0.3,0.3)*bounceDiff;
    diffCol += col*pow(max(dot(rd, reflect(lightDir, n)), 0.0), 60.); // spec

    return diffCol;
}

float pattern(vec2 p, float rot) {
    p*=Rot(radians(rot));
    float size = 0.1;

    float d = B(p,vec2(size));
    for (int i = 0; i < 3; i++) {
        vec2 q = abs(p);
        q-=0.15;

        d = min(d,  B(q,vec2(size)));
    }
    return abs(d)-0.02;
}

float pattern2(vec2 p, float rot) {
    p*=Rot(radians(rot));
    float size = 0.1;

    p.x*=2.0;
    float d = B(p,vec2(size));
    for (int i = 0; i < 3; i++) {
        vec2 q = abs(p);
        q-=0.15;
        q.x*=2.0;
        d = min(d,  B(q,vec2(size)));
    }
    return abs(d)-0.05;
}

float pattern3(vec2 p, float rot) {
    p*=Rot(radians(rot));
    float size = 0.1;

    float d = length(p)-size;
    for (int i = 0; i < 3; i++) {
        vec2 q = abs(p);
        q-=0.15;
        d = min(d,  length(p)-size);
    }
    return abs(d)-0.02;
}

vec3 magicCarpetTex(vec3 p){
    vec2 uv = p.xz;
    uv.x*=0.85;
    uv.y*=0.8;
    vec2 prevUV = uv;

    vec3 col = vec3(0.5,0.0,0.1);

    uv = abs(uv)-0.15;
    uv = abs(uv)-0.15;
    float d =pattern(uv,35.0);

    float d2 = pattern3(uv,40.0);
    d = min(d,d2);

    uv.x = abs(uv.x)-0.6;
    d2 = pattern2(uv,-30.0);
    d = min(d,d2);


    uv = prevUV;

    col = mix(col,vec3(0.2,0.5,0.8),S(d,0.0));


    d = B(uv,vec2(1.2,0.8));
    d = abs(d)-0.02;
    col = mix(col,vec3(0.9,0.5,0.2),S(d,0.0));

    return col;
}

vec3 materials(int mat, vec3 n, vec3 rd, vec3 p, vec3 col){
    if(mat == MATERIAL){

        col = diffuseMaterial(n,rd,p,vec3(0.9,0.9,0.3));
    } else if(mat == MATERIAL1){
        col = diffuseMaterial(n,rd,p,magicCarpetTex(p));
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 prevUV = uv;
    vec2 m =  iMouse.xy/iResolution.xy;

    vec3 ro = vec3(0, 6, -3.)*.6;
    #if USE_MOUSE == 1
    ro.yz *= Rot(-m.y*3.14+1.);
    ro.y = max(-0.9,ro.y);
    ro.xz *= Rot(-m.x*6.2831);
    #else
    ro.yz *= Rot(radians(-30.0));
    ro.xz *= Rot(radians(-5.0+iTime*20.0));
    #endif

    vec3 rd = R(uv, ro, vec3(0,0.0,0), 1.0);
    vec2 d = RayMarch(ro, rd, 1.,MAX_STEPS);
    vec3 col = vec3(0.6);

    if(d.x<MAX_DIST) {
        vec3 p = ro + rd * d.x;
        vec3 n = GetNormal(p);
