}

float noise(vec2 p)
{
    return fract(634171.9852*sin(dot(p,vec2(394.915,594.337))));
}

vec2 noise2(vec2 p)
{
    float x = fract(619723.6489*sin(dot(p,vec2(467.294,357.517))));
    float y = fract(693118.1384*sin(dot(p,vec2(572.221,492.997))));
    return vec2(x,y);
