float smoothnoise(vec2 p)
{
    vec2 c = floor(p);
    vec2 d = fract(p);
    float n = 0.;
    for (float x=-0.; x<=1.; x++)
    {
        for (float y=-0.; y<=1.; y++)
        {
            vec2 xy = vec2(x,y);
            vec2 v = abs(1.-xy-d);
            n += noise(xy+c) * v.x * v.y;
        }
    }
    n *= 0.5;
    return n;
}

float fractalnoise(vec2 p)
{
    float n = 0.;
    for (float i=0.; i<9.; i++)
    {
        n += smoothnoise(pow(1.5,i)*rot(.1*i)*p)*pow(.8,i);
    }
    n *= 0.65;
    return n;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.x;
    uv *= rot(0.01 * iTime);
    uv *= 16.;

    float n0 = max(0.,fractalnoise(rot(.14)*uv+vec2(7.12,5.31))-0.65);
    float nr = max(0.,fractalnoise(rot(.48)*uv+vec2(5.19,4.56))-0.3);
    float nb = max(0.,fractalnoise(rot(.69)*uv+vec2(3.21,8.67))-0.3);

    float den = 15.*pow(n0,2.3);
    float red = sqrt(1.5*nr);
    float blu = sqrt(1.5*nb);

    vec3 col = vec3(0.);

    col += 0.8*den*vec3(.9*red,red*blu+.08*(red+blu),blu);

    uv *= 8.;
    for (float x=-1.; x<=1.; x++)
    {
        for (float y=-1.; y<=1.; y++)
        {
            vec2 xy = vec2(x,y);
            vec2 id = floor(uv) - xy;
            vec2 p = fract(uv) + xy;
            float d = distance(p, noise2(id));
            vec3 c = vec3(.6);
            c.rb += 0.4*noise2(id + 20.);
            col += c * (0.5 + 0.5*sin(1.5*iTime + 6.283*noise(id-30.))) * (0.4 + 0.6*noise(id + 30.)) * smoothstep(1.,0.,8.*d);
        }
    }

    fragColor = vec4(col,1.0);
}

