float GetDist(vec3 p) {
    p.x += tanh(0.1 * iTime) * cos(0.5 * p.z + iTime);
    float a = 4.;
    float b = 0.;
    float e = exp(-0.5 * (cos(4. * p.x + 0. * iTime) + cos(4. * p.z)));
    float d = -0.1 * p.y * abs(p.x-p.z) + p.y + 0.1 * e * cos(10. * e);
    d += -0.2+ 0.1 * thc(1., pi * mod(floor(2. * p.z/pi), 2.) + iTime)
              + 0.1 * ths(1., pi * mod(floor(2. * p.x/pi), 2.) + iTime);
   // d -= 0. + 0.2 * cos(iTime + 200. * exp(-2. * length(p.xz)));
   d -= -0.2 + 0.05 * length(p);
    return 0.1 * d;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0,5. * tanh(0.1 * iTime),0), 1.);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    vec3 p = ro + rd * d;
    if(d<MAX_DIST) { 
        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        float a = atan(p.x, p.z);
        float t = iTime;
        col = 0.6 + 0.4 * vec3(1) * clamp(dot(n, vec3(0.2,1,0.2)),0.,1.);//0.5 + 0.5 * n;
       // col *= pal(
        //col *= exp(-2.8 * p.y);
        vec3 e = vec3(0.5);

        col *= 3.5 * pal(n.y, e, e,e, 0.32 * vec3(0,1,2)/3.);
        //col *= 0.5 + 0.5 * thc(4., 0.1 * length(p.xz) - iTime);
    }
    vec2 ipos = floor(p.xz + vec2(iTime/pi,0)) + 0.5;
    col *= 0.45 + exp(-20. * p.y);
    //col *= 0.45 + 2. * exp(- 10.5 * length(mod(ipos,2.)));
    float mx = 1. * clamp(exp(-0.1 * length(p)),0.,1.);
    vec3 col2 = 0. * vec3(0.02 + 0.02 * cos(0.01 * p.x + iTime),1,1);
    col = mix(1.-col, col, clamp(4. * mx,0.,1.)) * mx;
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

