#define F1 float
#define F3 vec3

float Srgb1(float c){return(c<0.0031308?c*12.92:1.055*pow(c,0.41666)-0.055);}
vec3 Srgb3(vec3 c){return F3(Srgb1(c.r),Srgb1(c.g),Srgb1(c.b));}


vec2 approximateShape(float a, float b)
{
    float C = sqrt(a*a+b*b);
    a /= C;
    b /= C;

    float a2 = 2.0*a*b;
    float b2 = a*a - b*b;
    float a3 = 3.0*a - 4.0*a*a*a;
    float b3 = -3.0*b + 4.0*b*b*b;    

    // softness_scale 0.0
    // Estimated using https://colab.research.google.com/drive/1qQA3xhBX3iB8FT8558e5QQnmVZ5Jh1Fb?usp=sharing
    return vec2(
        1.63804674 + -0.08431713*a +  0.27624179*b +  0.08737741*a2 +  0.13917503*b2 + 0.03691021*a3 +  0.03060615*b3,
        0.23438507 + 0.05736278*a  +  -0.09674117*b +  0.024447*a2 + 0.00469441*b2 + 0.01253234*a3 + 0.00741058*b3
    );
}

vec3 softSaturate(vec3 x, vec3 a)
{
    a = 1.0+a;
    x = min(x, a);
    vec3 b = (a-1.0)*sqrt(a/(2.0-a));
    return 1.0 - (sqrt((x-a)*(x-a) + b*b) - b)/(sqrt(a*a+b*b)-b);
}

vec3 softClipColorOutsideGamutOnly(vec3 color)
{
    // soft clipping scaled to only have an effect if the color is outside the gamut

    float middle = 0.2;

    vec3 x = color-middle;

    vec3 xsgn = sign(x);
    vec3 xscale = 0.5 + xsgn*(0.5-middle);
    x /= xscale;

    vec3 absX = abs(x);

    float maxX = max(max(1.0,absX.r), max(absX.g, absX.b));

    float softness_scale = 0.5;
    float softness = softness_scale*(maxX-1.0);
    softness = softness/(1.0+softness);

    return middle + xscale*xsgn*softSaturate(absX, vec3(softness));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float h = (fragCoord.x / iResolution.x)* 3.141592 * 2.;
    float L = 3.0*fragCoord.y / iResolution.y;
    L = L - floor(L);
    float C = -0.25*log(0.55+0.45*sin(iTime));
    float a = cos(h);
    float b = sin(h);
    float C_max = find_gamut_intersection(a,b,L,1.0,L);

    vec3 rgb;

    if (fragCoord.y / iResolution.y > 2.0/3.0)
    {   
        // this is an smooth approximation of the sRGB gamut, that is larger than the sRGB gamut everywhere.
        vec2 ST = approximateShape(a, b);
        float C_smooth = (1.0/((ST.x/L) + (ST.y/(1.0-L))));

        // Clip to a smooth gamut larger than the sRGB gamut
        rgb = oklab_to_linear_srgb(vec3(L, a*min(C,C_smooth), b*min(C,C_smooth)));

        // soft clipping all colors, if at least one of them is out of gamut:
        rgb = softClipColorOutsideGamutOnly(rgb);

    }
    else if (fragCoord.y / iResolution.y > 1.0/3.0)
    {
        rgb = oklab_to_linear_srgb(vec3(L, a*min(C,C_max), b*min(C,C_max)));
    }
    else
    {
        rgb = oklab_to_linear_srgb(vec3(L, a*C, b*C));
    }

    //if (C > C_max)
    //    rgb = vec3(0.0);

    rgb = clamp(rgb,0.0,1.0);

    fragColor.rgb = Srgb3(vec3(rgb));
    fragColor.a = 1.;
}

