// Two different distance esimators for the Mandelbrot Set (M).
// The first one (ESTIMATOR 0 below) is the one most people use to
// raymarch quaternionic quadratic sets (like Mandelbrot or Julia
// set), but only works well when very close to M. The second one
// (ESTIMATOR 1) is a better estimator, especially far from M,
// that is basically not much more expensive although does suffer
// form precision when close to M.
//
// More info:
//
// https://iquilezles.org/articles/distancefractals

// List of some other 2D distances: https://www.shadertoy.com/playlist/MXdSRf
//
// and iquilezles.org/articles/distfunctions2d


// 0 : traditional estimator
// 1 : much better estimator
#define ESTIMATOR 0

float sdMandelbrot( vec2 c )
{
    vec2 z = vec2(0.0,0.0);
    vec2 dz = vec2(0.0,0.0);
    bool exterior = false;
    float r2;
    float n = 0.0;
    for( int i = 0; i<256; i++ )
    {
        // dz -> 2·z·dz + 1
        dz = 2.0*vec2(z.x*dz.x - z.y*dz.y, z.x*dz.y + z.y*dz.x) + vec2(1.0,0.0);
        // z -> z² + c
        z = vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y) + c;

        n += 1.0;
        r2 = dot(z,z);
        if( r2>65536.0 )
        {
            exterior = true;
            break;
        }
    }

    #if ESTIMATOR==0
	 float d = 0.5*sqrt(r2/dot(dz,dz))*log(r2);
    #else
     float en = exp2(n);
     float d = 0.5*sqrt(r2/dot(dz,dz))*en*(1.0-pow(r2,-1.0/en));
    #endif
    return (exterior) ? d : 0.0;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // normalized pixel coordinates
    vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y;
    vec2 m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    // animate
    bool zoom = sin(iTime*0.6)<-0.4;

    // transform
    vec3 tx = zoom ? vec3(-0.745,0.186, 0.05 ) : vec3(-0.5,0.0, 1.2 );
    p = tx.xy + p*tx.z;
    m = tx.xy + m*tx.z;
    float px = tx.z*2.0/iResolution.y;

    // if not mouse
    if( iMouse.z<0.001 )
    {
        if( zoom )
        {
            m = vec2(-0.775,0.2);
            m += vec2(0.015,0.02)*vec2(cos(iTime),sin(iTime));
        }
        else
        {
            float time = iTime*0.5;
            m  = vec2(-0.75,0.0);
            m += vec2(1.6,1.3)*vec2(cos(time*1.1),sin(time*1.1));
            m += vec2(0.4,0.4)*vec2(cos(time*3.1),sin(time*3.1));
        }
    }

    // distance
    float d = sdMandelbrot(p);

    // coloring

    vec3 col = vec3(1.0) - sign(d)*vec3(0.1,0.4,0.7);
	col *= 1.0 - exp(-5.0*abs(d/tx.z));
	col *= 0.8 + 0.2*cos(100.0*abs(d/tx.z));
	col = mix( col, vec3(1.0), 1.0-smoothstep(0.0,1.4*px,abs(d)) );

    //if( iMouse.z>0.001 )
    {
    d = sdMandelbrot(m);
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, px, abs(length(p-m)-abs(d))-px));
    col = mix(col, vec3(1.0,1.0,0.0), 1.0-smoothstep(0.0, px, length(p-m)-px*4.0));
    }

	fragColor = vec4(col, 1.0);
}

#define MAX_ITER 256.

vec2 mandelbrotDE(vec2 c) {
    vec2 p = vec2(2.0, 0.0);
    vec2 z = c;
    float k = 0.0;
    vec2 dz = one;
    float r;
    while (k < MAX_ITER) {
        r = length(z);
        if (r > 256.0) break;
        dz = mul(p, mul(powervec2(z, p - one), dz)) + one;
        z = powervec2(z, p) + c;
        k += 1.0;
    }
    return vec2(k, 0.5 * log(r) * r / length(dz));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float zoom = 5.0;
    vec2 uv = zoom * (fragCoord - 0.5 * iResolution.xy) / iResolution.x;

    vec2 n = mandelbrotDE(uv);
    vec3 col;
    if (n.x == MAX_ITER) {
        col = vec3(0.0);
    } else {
        float sinlogdist = sin(log(n.y));
        col = vec3(sinlogdist * sinlogdist);
        //col = vec3(0.1 * sqrt(n.x));
    }

    float thickness = 0.01;
    vec2 mouse = zoom * (iMouse.xy - 0.5 * iResolution.xy) / iResolution.x;
    float d = mandelbrotDE(mouse).y;
    float l = length(uv - mouse);
    if (d - thickness < l && l < d + thickness) {
        fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
        fragColor = vec4(col,1.0);
    }
}

