vec2 sinhyp(in vec2 z) {
    return vec2(sinh(z.x) * cos(z.y), cosh(z.x) * sin(z.y));
}

vec2 coshyp(in vec2 z) {
    return vec2(cosh(z.x) * cos(z.y), sinh(z.x) * sin(z.y));
}

vec2 sub_tanh(in vec2 z) {
    return one - div(two, powexp(z + z) + one);
}

vec2 tanhyp(in vec2 z) {
    if (z.x > 44.) {
        return flip(sub_tanh(flip(z)));
    } else {
        return sub_tanh(z);
    }
}

vec2 sub_coth(in vec2 z) {
    return one + div(two, powexp(z + z) - one);
