struct Ray   //射线结构体
{
    vec3 origin;//射线原点origin
    vec3 direction;//射线方向direction
};

//伪随机,t乘以一个大的数,使得sin值伪随机,再乘以一个大的数,然后取小数部分,得到伪随机的小数部分
float Noise(float t)
{
    return fract(sin(t*3456.0)*6547.0);
}
//输入一个数，返回一个伪随机的vec4
vec4 Noise14(float t)
{
    return fract(sin(t*vec4(123.0,1024.0,3456.0,9821.0))*vec4(6547.0,345.0,9123.0,2431.0));
}

//设置摄像机的三个基向量以及摄像机点到屏幕上每个像素点的方向
Ray SetCamera(vec2 uv,vec3 camPos,vec3 lookat,float zoom)
{
    Ray ray;
    ray.origin = camPos;
    //摄像机的三个基向量
    vec3 forward = normalize(lookat-camPos);
    vec3 right = cross(vec3(0.0,1.0,0.0),forward);
    vec3 up = cross(forward,right);

    vec3 center = ray.origin+forward*zoom;//屏幕中心点坐标
    vec3 i = center+right*uv.x+up*uv.y;//屏幕上的每个点坐标
    ray.direction = normalize(i-ray.origin);//摄像机点到屏幕每个像素点的方向

    return ray;
}

//求点p到射线的距离
float GetDistance(Ray ray,vec3 p)
{
    return length(cross(p-ray.origin,ray.direction));
}

//绘制圆效果,size为圆的大小,blur是模糊程度,百分比
float Bokeh(Ray ray,vec3 p,float size,float blur)
{
    float d = GetDistance(ray,p);//求点p到每条射线(每个像素点-射线原点)的距离
    size *= length(p);//点p距离越近,大小乘以距离,使得圆在靠近摄像机时大小不变
    float c = smoothstep(size,size-blur*size,d);//绘制小圆
    c *= mix(0.6,1.0,smoothstep(0.8*size,size,d));//边缘过度效果(0.8,1.0)的时候

    return c;
}

//绘制一系列圆，组成一个移动的效果
vec3 Light(Ray ray,float t)
{
    float side = step(ray.direction.x,0.0);//在左半边是为1
    ray.direction.x = abs(ray.direction.x);//关于y对称
    float s = 1.0/10.0;//for循环间隔
    float mask = 0.0;
    for(float i=0.0;i<1.0;i+=s)
    {
        float ti = fract(t+i+side*s*0.5);//fract取小数部分的函数,左半边比右半边快一些
        vec3 p = vec3(2.0,2.0,100.0-ti*100.0);//10个深度不同的圆
        mask += Bokeh(ray,p,0.05,0.1)*ti*ti*ti;//乘以t^3减少初始地区圆的数量
    }

    return vec3(1.0,0.7,0.3)*mask;
}

//环境光
vec3 EnvLight(Ray ray,float t)
{
    float side = step(ray.direction.x,0.0);//在左半边是为1
    ray.direction.x = abs(ray.direction.x);//关于y对称
    float s = 1.0/10.0;//for循环间隔
    vec3 col = vec3(0.0);
    for(float i=0.0;i<1.0;i+=s)
    {
        vec4 n = Noise14(i+side*100.0);//+side*100可以使得左右两边颜色不同
        float ti = fract(t+i+side*s*0.5);//fract取小数部分的函数,左半边比右半边快一些
        float fade = ti*ti*ti;//乘以t^3减少初始地区圆的数量

        float occlusion = sin(ti*6.28*10.0*n.x)*0.5+0.5;//结果为(0,1),乘以n.x达到随机某个圆消失
        fade = occlusion;

        float x = mix(2.5,10.0,n.x);
        float y = mix(0.1,1.0,n.y);
        vec3 p = vec3(x,y,50.0-ti*50.0);//10个深度不同的圆

        col += Bokeh(ray,p,0.05,0.1)*fade*n.wzy*0.5;
    }

    return col;
}

//与Light类似,没有对称边,头灯
vec3 HeadLight(Ray ray,float t)
{
    t *= 2.0;
    vec3 w1 = vec3(0.25,0.0,0.0);
    vec3 w2 = w1*1.2;
    float s = 1.0/10.0;//for循环间隔
    float mask = 0.0;
    for(float i=0.0;i<1.0;i+=s)
    {
        float n = Noise(i);
        if(n>0.1) continue;//去掉一些随机值小于0.1的部分，达到一个随机效果

        float ti = fract(t+i);//fract取小数部分的函数,左半边比右半边快一些
        float focus = smoothstep(0.9,1.0,ti);
        float size = mix(0.05,0.04,focus);

        float fade = ti*ti*ti*ti*ti;
        vec3 p = vec3(-1.0,0.15,100.0-ti*100.0);//10个深度不同的圆
        mask += Bokeh(ray,p-w1,size,0.1)*fade;//乘以t^n减少初始地区圆的数量
        mask += Bokeh(ray,p+w1,size,0.1)*fade;

        mask += Bokeh(ray,p-w2,size,0.1)*fade;
        mask += Bokeh(ray,p+w2,size,0.1)*fade;

        float ref = 0.0;//反射,y取相反,然后模糊百分比是1,size大一些
        ref += Bokeh(ray,(p-w2)*vec3(1.0,-1.0,1.0),size*3.0,1.0)*fade;
        ref += Bokeh(ray,(p+w2)*vec3(1.0,-1.0,1.0),size*3.0,1.0)*fade;

        mask += ref*focus;
    }

    return vec3(0.9,0.9,1.0)*mask;
}

//尾灯
vec3 TailLight(Ray ray,float t)
{
    t *= 0.25;
    vec3 w1 = vec3(0.25,0.0,0.0);
    vec3 w2 = w1*1.2;
    float s = 1.0/15.0;//for循环间隔
    float mask = 0.0;
    for(float i=0.0;i<1.0;i+=s)
    {
        float n = Noise(i);
        if(n>0.5) continue;//去掉一些随机值小于0.1的部分，达到一个随机效果

        float lane = step(0.25,n)+0.4;//用于红色偏移

        float ti = fract(t+i);//fract取小数部分的函数,左半边比右半边快一些
        float focus = smoothstep(0.8,1.0,ti);
        float size = mix(0.05,0.04,focus);

        float laneShift = smoothstep(1.0,0.96,ti);//部分红色转弯

        float fade = ti*ti*ti*ti*ti;
        float z = 100.0-ti*100.0;
        vec3 p = vec3(1.5-lane*laneShift,0.15,z);//10个深度不同的圆
        mask += Bokeh(ray,p-w1,size,0.1)*fade;//乘以t^n减少初始地区圆的数量
        mask += Bokeh(ray,p+w1,size,0.1)*fade;

        float blink = step(0.,sin(1000.0*t))*7.0*step(0.25,n)*step(0.96,ti);//乘以step(0.96,ti)让红色转弯时才闪烁
        mask += Bokeh(ray,p-w2,size,0.1)*fade;
        mask += Bokeh(ray,p+w2,size,0.1)*fade*(1.0+blink);

        float ref = 0.0;//反射,y取相反,然后模糊百分比是1,size大一些
        ref += Bokeh(ray,(p-w2)*vec3(1.0,-1.0,1.0),size*3.0,1.0)*fade;
        ref += Bokeh(ray,(p+w2)*vec3(1.0,-1.0,1.0),size*3.0,1.0)*fade*(1.0+blink*0.1);

        mask += ref*focus;
    }

    return vec3(1.0,0.1,0.03)*mask;
}

//rain雨,在挡风玻璃前的雨
vec2 Rain(vec2 uv,float t)
{
    t *= 40.0;
    vec2 a = vec2(3.0,1.0);//分出多行多列
    vec2 st = uv*a;

    vec2 id = floor(st);//获得每个雨滴是第几行第几列
    st.y += t*0.22;   //网格下降,和雨滴上升抵消,达到雨滴先下落后暂停的循环

    float n = fract(sin(id.x*716.34)*768.34);//根据不同的列获得不同的随机数
    //对y进行偏移,使得不同列起始位置不同
    st.y += n;
    uv.y += n;

    id = floor(st);//根据偏移后的坐标重新计算行数和列数
    st = fract(st)-0.5;//st范围变为(-0.5,-0.5)-(0.5,0.5)

    //根据不同行,不同列队t进行偏移,使得不同雨滴下落时间不同
    t += fract(sin(id.x*76.34+id.y*1453.7)*768.34)*6.283;
    float offsetY = -sin(t+sin(t+sin(t)*0.5))*0.43;
    vec2 offset = vec2(0.0,offsetY);//大雨滴的滑落偏移

    vec2 o1 = (st-offset)/a;//除以横纵比,不再是椭圆
    float d = length(o1);
    float m1 = smoothstep(0.07,0.0,d);

    //大雨滴后面的小雨滴
    vec2 o2 = (fract(uv*a.x*vec2(1.0,2.0))-0.5)/vec2(1.0,2.0);
    d = length(o2);//除以横纵比,将x,y方向间距相同

    float mask = smoothstep(-0.1,0.1,st.y-offset.y);//去除大雨滴下面的小雨滴

    //模拟大雨滴划过之后留在原地的小雨滴
    float m2 = smoothstep(0.2*(0.6-st.y),0.0,d)*mask;//距离大雨滴越远的水滴越小

    //if(st.x>0.46 || st.y>0.49) m1=1.0;//边界线

    return vec2(m1*o1*5.0+m2*o2*5.0);
}

