// This shader shows when variables start losing precision as
// they grow to be in the order of 11, 12, ... 21 bits large.
//
// In an ideal world, the blue line would be a perfect ramp.
// But when you start seeing the staircase (lose of precision)
// at some point that depends on your GPU and screen resolution.
// Anything between 14 and 16 bits is normal.
//
// The yellow signal is the difference between the ramp we get
// and the ramp we would ideally get.
//
// If the blue signal was time, you could safely expect about one
// hour of smooth animation. So, make sure you compute time always
// in some 64 bit type and that you only convert it to event-relative
// float32 (for sound synthesis or animation) AFTER subtracting the 
// 64 bit global time from the event's trigger time which is also in
// 64 bit precision.


const int kBitsA = 11;
const int kBitsB = 22;

float printDigit( in int n, in vec2 p )
{		
    // digit bitmap by P_Malin (https://www.shadertoy.com/view/4sf3RN)
    const int lut[10] = int[10](480599,139810,476951,476999,71028,464711,464727,476228,481111,481095);
    // extract bit
	ivec2 q = ivec2(p*vec2(4,5));
    if( q.x<0 || q.x>3 || q.y<0 || q.y>4 ) return 0.0;
	int id = 4*q.y + q.x;
    return float( (lut[n]>>id) & 1 );
}

#define ZERO(v) (min(iTime*v,0.0))

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // coords
    vec2 p = vec2(fragCoord)/iResolution.xy;
    float px = 2.0/iResolution.y;

    // number of bits 
    float wct  = float(kBitsB-kBitsA)*p.x;
    float wcf  = fract(wct);
    int   bits = kBitsA + int(floor(wct));

    // offset value
    float t = p.x + exp2(float(bits));

    const float sep = 0.12;

    // background 
    vec3 col = vec3( 0.15 );

    // plot t
    {
        float y = t - exp2(float(bits)+ZERO(1.0));
        y = sep + (1.0-sep)*(0.1+0.8*y);
        float dy = (1.0-sep)*0.8;
        float d  = abs(p.y-y)/sqrt(1.0+dy*dy);
        col = mix( col, vec3(0.0,0.8,1.0), smoothstep(px,0.0,d) );
    }
    // plot error
    {
        float v1  = t - exp2(float(bits)+ZERO(2.0));
        float v2 = p.x;
        float y = v1-v2;
        y = sep + (1.0-sep)*(0.1+0.8*y);
        float dy = (1.0-sep)*0.8;
        float d  = abs(p.y-y)/sqrt(1.0+dy*dy);
        col = mix( col, vec3(0.9,0.6,0.2), smoothstep(px,0.0,d) );
    }
    // plot vertical lines
    {
        col += smoothstep(1.0-px*6.0,1.0,2.0*abs(wcf-0.5));
    }
    // plot horizontal lines
    {
        float d = min( 2.0*abs(p.y-sep), abs(abs(p.y-0.5)-0.5) );
        col += smoothstep(px,0.0,d);
    }
    // plot digits
    {
        float wi = 1.0/float(kBitsB-kBitsA);
        vec2 p = vec2(wcf,float(kBitsB-kBitsA)*fragCoord.y/iResolution.x);
        p.x -= 3.0*wi;
        p.y -= 3.0*wi;
        col += printDigit( bits/10, 5.0*p );
        p.x -= 4.0*wi;
        col += printDigit( bits%10, 5.0*p );
    }

    // output color
    fragColor = vec4( col, 1.0 );
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)

float seg(in vec2 p, in vec2 a, in vec2 b) {
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

vec2 pnt(float n, float spd, float t) {      
    //float t = iTime; //floor(spd * time) / spd;
    float f = fract(t);//fract(spd * time);

    vec2 p = vec2(cos(t), sin(t));
    t += n/spd;
    vec2 p2 = vec2(cos(t), sin(t));
    t -= n/spd;
    vec2 p3 = vec2(cos(t), sin(t));

    f = 1. - pow(1.-f,4.);
    f = smoothstep(0.,1.,f);

    float m = clamp(2. * f, 0., 1.);
    float m2 = clamp(2. * f, 1., 2.) - 1.;
    return 0.45 * mix(mix(p,p2,m), p3, m2);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float spd = 4.;

    float s = 0.;

    float k = 1. / iResolution.y;
    float n = spd * 4.;
    for (float i = 0.; i < n; i++) {
        float io = 2. * pi * i / n;
        vec2 p = pnt(1., spd, 0.5 * iTime + io);
        p *= (0.75 + 0.25 * thc(4., 4. * io + 0.5 * iTime));
        float o2 = 1./n;
        vec2 p2 = pnt(1., spd, 0.5 * iTime + io + o2);       
        p2 *= (0.75 + 0.25 * thc(4., 0.5 * iTime));

        float d = seg(uv, p, p2);// length(uv - p);
        s = max(s - 0.5 * s * cos(i * pi / n + iTime), smoothstep(-k, k, -d + 0.015));

    }

    vec3 col = vec3(s);
    col += 0.22 * exp(-1. * (uv.y + 0.5)); 
    fragColor = vec4(col,1.0);
}

#define MAX_ITER 10
#define MAX_STEPS 100
#define MAX_DIST 10.
#define MIN_DIST .001
#define EPSILON .0001

vec2 cis(float theta) {
    return vec2(cos(theta), sin(theta));
}

vec2 mul(vec2 z, vec2 w) {
    return z.x * w + z.y * vec2(-w.y, w.x);
}

vec3 rotate(vec3 p, vec3 rot) {
    vec2 v = mul(p.yz, cis(rot.x));
    vec3 p_prime = vec3(p.x, v);
    v = mul(p_prime.xz, cis(rot.y));
    p_prime = vec3(v.x, p_prime.y, v.y);
    v = mul(p_prime.xy, cis(rot.z));
    p_prime = vec3(v, p_prime.z);
    return p_prime;
}

float DE(vec3 c) {
    vec3 z = c;
    float power = 8.0;
    float bailout = 2.0;
    float dr = 1.0;
    float r = 0.0;
    for (int k = 0; k < MAX_ITER; k++) {
        r = length(z);
		if (r > bailout) break;

		float theta = power * acos(z.z/r);
		float phi = power * atan(z.y, z.x);
		dr =  pow(r, power-1.0) * power * dr + 1.0;		
		r = pow(r,power);

		z = r * vec3(sin(theta) * cos(phi), sin(phi) * sin(theta), cos(theta)) + c;
    }
    return 0.5*log(r)*r/dr;
}

float RayMarch(vec3 ro, vec3 rd) {
	float dO=0.;

    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = DE(p);
        dO += dS;
        if(dO>MAX_DIST || dS<MIN_DIST) break;
    }

    return dO;
}

vec3 normalVector(vec3 p) {
	float d = DE(p);
    vec2 e = vec2(EPSILON, 0);

    vec3 n = d - vec3(
        DE(p-e.xyy),
        DE(p-e.yxy),
        DE(p-e.yyx));

    return normalize(n);
}

float lighting(vec3 p) {
    vec3 lightPos = rotate(vec3(0.0, -5.0, 5.0), vec3(-0.5, 0., iTime / 5.));
    vec3 l = normalize(lightPos - p);
    vec3 n = normalVector(p);

    float dif = clamp(dot(n, l), 0.0, 1.0);
    float d = RayMarch(p + n * MIN_DIST * 2.0, l);
    if(d < length(lightPos - p)) dif *= .1;

    return dif;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = 2.0;
    vec2 uv = zoom * (fragCoord - 0.5 * iResolution.xy) / iResolution.x;

    vec3 rot = vec3(-0.5, 0.0, iTime / 5.);
    vec3 ro = rotate(vec3(0.0, -3.0, 0.0), rot);
    vec3 rd = normalize(rotate(vec3(uv.x, 1.0, uv.y), rot));

    float d = RayMarch(ro, rd);

    vec3 p = ro + rd * d;

    float dif = lighting(p);
    vec3 col = vec3(dif);

    col = pow(col, vec3(0.45));

    fragColor = vec4(col, 1.0);
}

