vec2 getOrbitPos(float v, float dist)
{
    return vec2(cos(v), sin(v)) * dist;
}
float getRings(vec2 uv, float r)
{
    float Speed = iTime * .2;
    float P = 0.;
    for(float i=0.; i <= RIPPLE_COUNT; i++)
    {
        float per = i / RIPPLE_COUNT;
        float stage = fract(per + Speed);
        float radius = r * stage;

        P += smoothstep(0.015,0., abs(length(uv)-radius)) * pow((1. - stage),3.5) * .5;
    }
    return P;
}
float getBall(vec2 uv, float scale, float orbitSpeed)
{

    float P = smoothstep(0.1*scale, 0.01 * scale, abs(length(uv)-scale));    
    float orbitScale =  scale  * .5;    
    for(float i=0.; i <= 1.; i++)    
        P += smoothstep(orbitScale, -0.6 * scale, length(uv - getOrbitPos(orbitSpeed + radians(180. * i), scale))) * 2.;        
    orbitScale *= .5;    

    return P;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float T = iTime;
    vec2 uv = (fragCoord.xy -.5 * iResolution.xy) /iResolution.y;        
    uv *= Rot(dot(uv.x, uv.y) + T );
    uv += vec2(sin(T * 0.1), 0);
    uv *= TILE_COUNT + (TILE_COUNT * 0.1 * sin(T * 0.5));        

    vec2 gv = fract(uv) - .5;    
    vec2 ID = floor(uv);          
    vec3 col = vec3(0);                
    float BG1 = pow(mod(fract(abs(gv.x) + T), abs(gv.y)), 1.8);
    float BG2 = pow(abs(dot(sin(uv.x * 1.), cos(gv.y * 6.) * length(uv) * .075) ), 1.7);
    float BG = mix(BG1, BG2, abs(sin(T * .2)));
    float BGHue = pow(BG, .5) + (T * .1);
    vec3 bgCol = hue(BGHue) * BG;  
    col = mix(vec3(BG), bgCol, 0.6);  //  Desaturate        
    float lines = 0.;
    float orbitSpeed = T * 5.;
    float P = 0.;
    vec3 ballCol = vec3(0);
    float w = 0.;
    float waveSpeed = T *  34.;
    for (int x = -1; x <= 1; x++)
    {
        for (int y = -1; y <= 1; y++)
        {
            vec2 offs = vec2(x,y);
            vec2 oID = ID + offs;
            float nID = hash12(oID); 

            vec2 pos = gv -offs;
            float Size = (mod(oID.x + oID.y, 2.) == 0.) ? .2 : 0.075;
            float orbSpeed = (mod(floor(nID * 10000.), 2.) == 0.) ? orbitSpeed : -orbitSpeed;

            float nP = getBall(pos, Size, orbSpeed);

            ballCol += hue(nID) * nP;
            P += nP;            
            w += getWaves(pos, Size, waveSpeed);                                   
            ballCol += hue(nID + 0.1) * getRings(pos, Size * 4.5);
            lines += line(gv + 1., pos + 1.5  , vec2(1.), 0.03);



        }
    }

    col = mix(col , hue(BGHue + .001), lines * .1);
    col = mix(col, vec3(1.), min(1. , max(0. , P- 1.)) );
    col += ballCol;
    col += (hue(length(gv)) * w);


    fragColor = vec4(col,1.0);
}

#define pi 3.14159

#define thc(a,b) tanh(a*cos(b))/tanh(a)

vec3 pal(in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d) {
    return a + b*cos( 6.28318*(c*t+d) );
}

float h21(vec2 a) {
    return fract(sin(dot(a.xy, vec2(12.9898, 78.233))) * 43758.5453123);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float sc = iResolution.y / 3.;

    float v = abs(fract(sc * (uv.x + uv.y))-0.5) 
            + abs(fract(sc * (uv.x - uv.y))-0.5);

    vec3 e = vec3(0.5);

    float a = atan(uv.x, uv.y);
    float r = length(uv);

    float val = exp(-0.5 * thc(4., a + 4. * log(r)) * cos(4. * log(r) + iTime));
    float hatch = 0.2 * h21(vec2(v,0.01 * floor(7. * iTime)));

    vec3 col = pal(0.12 + hatch + v + val, e, e, e, 0.5 * vec3(0,1,2)/3.);
    fragColor = vec4(col,1.0);
}

