const float PI = 3.14159265;
const int MAX_STEPS = 64;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float EPSILON = 1e-6;


//----------------------------- Pyramid ------------------------------
//fonction barry crée les "faces" triangles multicolores + gamma
vec3 bary(vec3 a, vec3 b, vec3 c, vec2 p) {
    vec2 v0 = b.xy - a.xy, v1 = c.xy - a.xy, v2 = p - a.xy;
    float inv_denom = 1.0 / (v0.x * v1.y - v1.x * v0.y);
    float v = (v2.x * v1.y - v1.x * v2.y) * inv_denom;
    float w = (v0.x * v2.y - v2.x * v0.y) * inv_denom;
    float u = 1.0 - v - w;
    vec3 bc = abs(vec3(u,v,w));
    return bc * clamp(1.1 - floor(bc.x + bc.y + bc.z - 0.0001), 0.0, 1.0);
}



//----------------------------- Daugnut & glow ------------------------------

//https://www.shadertoy.com/view/3s3GDn
float getGlow(float dist, float radius, float intensity){
	return pow(radius / max(dist, EPSILON), intensity);	
}

//----------------------------- Camera ------------------------------

vec3 rayDirection(float fieldOfView, vec2 fragCoord, float yAddition, float size) {
    vec2 xy = (fragCoord - iResolution.xy / 2.0);
    xy.y = xy.y + yAddition;
    float z = (size * iResolution.y) / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

//https://www.geertarien.com/blog/2017/07/30/breakdown-of-the-lookAt-function-in-OpenGL/
mat3 lookAt(vec3 camera, vec3 targetDir, vec3 up){
    vec3 zaxis = normalize(targetDir);    
    vec3 xaxis = normalize(cross(zaxis, up));
    vec3 yaxis = cross(xaxis, zaxis);

    return mat3(xaxis, yaxis, -zaxis);
}


//-------------------------- SDF and scene ---------------------------
//Donut rotation fonction
vec3 rotate(vec3 p, vec4 q){
  return (2.0 * cross(q.xyz, p * q.w + cross(q.xyz, p)) + p + 1.0-abs(cos(iTime)/2.0));
}

//https://iquilezles.org/articles/distfunctions
float torusSDF( vec3 p, vec2 t ){
  vec2 q = vec2(length(p.xz) - t.x, p.y);
  return length(q) - t.y;
}

float getSDF(vec3 position) {
	//rotation avec iTime | set l'angle du donut pour le faire "tourner" - ca change l'angle du cercle de base sur leques les glow sont posés
   	float angle = iTime;
    vec3 axis = normalize(vec3(1.0, 1.0, 1.0));
    position = rotate(position, vec4(axis * sin(-angle*0.5), cos(-angle*0.5))); 
    return torusSDF(position, vec2(0.4, 0.1));

}


//---------------------------- Raymarching ----------------------------

// Glow variable is passed in by reference using the keyword inout. The result written in this
// function can be read afterwards from where it was called.
float distanceToScene(vec3 cameraPos, vec3 rayDir, float start, float end, inout float glow, int steps, float glowIntensity) {

    // Start at a predefined distance from the camera in the ray direction
    float depth = start;

    // Variable that tracks the distance to the scene at the current ray endpoint
    float dist;

    // For a set number of steps
    for (int i = 0; i < steps; i++) {

        // Get the SDF value at the ray endpoint, giving the maximum 
        // safe distance we can travel in any direction without hitting a surface
        dist = getSDF(cameraPos + depth * rayDir);

        // Calculate the glow at the current distance using the distance based glow function
        // Accumulate this value over the whole view ray
        // The smaller the step size, the smoother the final result
        glow += getGlow(dist, 1e-3, glowIntensity);

        // If the distance is small enough, we have hit a surface
        // Return the depth that the ray travelled through the scene
        if(dist < EPSILON){
            return depth;
        }

        // Else, march the ray by the sdf value
        depth += dist;

        // Test if we have left the scene
        if(depth >= end){
            return end;
        }
    }

    // Return max value if we hit nothing but remain in the scene after max steps
    return end;
}


//----------------------- Tonemapping and render ------------------------

//https://knarkowicz.wordpress.com/2016/01/06/aces-filmic-tone-mapping-curve/
vec3 ACESFilm(vec3 x){
    return clamp((x * (2.51 * x + 0.03)) / (x * (2.43 * x + 0.59) + 0.14), 0.0, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    // Get the default direction of the ray (along the negative Z direction)
    vec3 rayDir = rayDirection(60.0, fragCoord, 50.0, 0.5);
    vec3 rayDir2 = rayDirection(60.0, fragCoord, 15.0, 2.5);

    //----------------- Define a camera -----------------

    vec3 cameraPos = vec3(2.0);

    vec3 target = -normalize(cameraPos);
    vec3 up = vec3(0.0, 1.0, 0.0);

    // Get the view matrix from the camera orientation
    mat3 viewMatrix = lookAt(cameraPos, target, up);

    //---------------------------------------------------

    // Transform the ray to point in the correct direction
    rayDir = viewMatrix * rayDir;
    rayDir2 = viewMatrix * rayDir2;

    // Initialize glow to 0
    float glow = 0.00;

    // Find the distance to where the ray stops, pass in the glow variable to be accumulated
    float dist = distanceToScene(cameraPos, rayDir, MIN_DIST, MAX_DIST, glow, 64, 0.555);
    float dist2 = distanceToScene(cameraPos, rayDir2, MIN_DIST, MAX_DIST, glow, 2, 1.0);

    // Dist can now be used to render surfaces in the scene. We will only render the glow

    vec3 glowColour = vec3(0.9, 0.1, 0.2);

    // Glow now holds the value from the ray marching
    vec3 col = glow * glowColour;

    // Tonemapping
    col = ACESFilm(col);

    // Gamma
    col = pow(col, vec3(0.4545));



    vec2 uv = fragCoord.xy / iResolution.xy;
    //auto rotate pyramid with sin to right/left mouvment
    float rotateX = 3.5 * cos(iTime/2.0) - iResolution.x;

    //Define pyramid points, moving with the obj.x val
    vec3 p1 = vec3(0.5 + sin(rotateX)       *0.25, 0.2 + cos(rotateX+PI)    *0.1, 0.5 + sin(rotateX));
    vec3 p3 = vec3(0.5 + sin(rotateX+PI)    *0.25, 0.2 + cos(rotateX)       *0.1, 0.5 + sin(rotateX+PI));
    vec3 p2 = vec3(0.5 + sin(rotateX+PI/2.0)*0.25, 0.2 + cos(rotateX-0.5*PI)*0.1, 0.5 + sin(rotateX+PI/2.0)*0.2);
    vec3 p4 = vec3(0.5 + sin(rotateX-PI/2.0)*0.25, 0.2 + cos(rotateX+0.5*PI)*0.1, 0.5 + sin(rotateX-PI/2.0)*0.2);
    vec3 p5 = vec3(0.5 , 0.8+abs((sin(iTime)/8.0)), 0.0);

    //creating "faces" with color & gamma betweeb*n points
    vec3 bc1 = bary(p1, p2, p5, uv);
    vec3 bc2 = bary(p2, p3, p5, uv);
    vec3 bc3 = bary(p3, p4, p5, uv);
    vec3 bc4 = bary(p4, p1, p5, uv);
    //bc5 & bc6 makes the base with 2 triangles
    vec3 bc5 = bary(p1, p2, p3, uv);
    vec3 bc6 = bary(p1, p4, p3, uv);

    //adding everything in fragColor to implement this into shadertoy
	fragColor = vec4(bc1 + bc2 + bc3 + bc4 + bc5 + bc6 + col, 1.0);
}

struct Ray   //射线结构体
{
    vec3 origin;//射线原点origin
    vec3 direction;//射线方向direction
};
//设置摄像机的三个基向量并返回射线
Ray GetRay(vec2 uv,vec3 camPos,vec3 lookat,float zoom)
{
    Ray ray;
    ray.origin = camPos;
    //摄像机的三个基向量
    vec3 forward = normalize(lookat-camPos);
    vec3 right = cross(vec3(0.0,1.0,0.0),forward);
    vec3 up = cross(forward,right);

    vec3 center = ray.origin+forward*zoom;
    vec3 i = center+right*uv.x+up*uv.y;
    ray.direction = normalize(i-ray.origin);

    return ray;
}
//获得点p到射线ray上最近的点,通过射线原点加上射线方向乘以(p-ro和rd的点乘)
//点乘表示中的rd是归一化的所以点乘结果就是点p在rd方向上的投影长度
vec3 GetClosestPoint(Ray ray,vec3 p)
{
    return ray.origin+max(0.0,dot(p-ray.origin,ray.direction))*ray.direction;
}
//求最近到到p的距离
float GetDistance(Ray ray,vec3 p)
{
    return length(p-GetClosestPoint(ray,p));
}
//绘制背景虚化效果
float Bokeh(Ray ray,vec3 p,float size,float blur)
{
    float d = GetDistance(ray,p);
    float c = smoothstep(size,size-blur*size,d);
    c *= mix(0.6,1.0,smoothstep(0.8*size,size,d));

    return c;
}
//获取一个伪随机的(0,1)的数
float Noise(float t)
{
    return fract(sin(t*564.0)*3389.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    //虚拟摄像机
    vec3 camPos = vec3(0.0,0.0,0.0);
    vec3 lookat = vec3(0.0,0.0,1.0);
    float zoom = 2.0;
    Ray ray = GetRay(uv,camPos,lookat,zoom);

    float z = 50.0-fract(iTime/2.0)*50.0;
    vec3 p0 = vec3(1.0,1.0,1.2*z);//中间

    float d = Bokeh(ray,p0,0.3,0.1);
    float i = Noise(floor(iTime/2.0));
    for(;i<20.0;i+=1.0)
    {
        vec3 offset = vec3(Noise(i)*2.0-1.0,Noise(i+0.5)*2.0-1.0,0.0);
        vec3 p = p0-offset*10.0;
        p.z *= i/10.0;
        d += Bokeh(ray,p,0.3,0.1);
    }

    vec3 col = vec3(1.0,0.7,0.3)*d;

    fragColor = vec4(col,1.0);
}

