    return drawBar(x,x0,0.01);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 uv = fragCoord.xy / iResolution.xy;

    #ifdef DEBUG
    fragColor = abs(texture(iChannel0, uv).rgba / debugRange);
    return;
    #endif

    float v = texture(iChannel0, uv).r;
    fragColor.rgb = vec3(0.5, 0.5, 0.5);

    vec4 bufA = texture(iChannel3, uv);
    if (bufA.a > 0.0) {
        fragColor.rgb = bufA.rgb;
    }

    float g = v / glowOuterRadius;
    g = clamp(g, 0., 1.);
    if (g > 0.)
        fragColor.rgb = mix(glowOuterColor, fragColor.rgb, g);


    // Normalized pixel coordinates (from 0 to 1)
    uv = (2.*fragCoord - iResolution.xy)/iResolution.y;
    uv *= 1.333;
    vec3 col = vec3(0.);
    float b = v/200.;
    bool inside=false;
    if (b<0.)inside=true;

    float draw=1.;
    float drawcol = 0.;
    float movespeed = -0.05*SPEED;
    for (float i=0.0;i<1.;i+=0.1){
        draw *= drawBar(i,b);
    }
    b += mod(iTime*movespeed,0.1);
    draw = smoothstep(0.0,0.02*length(uv),nsin((b)*20.*PI-0.5*PI));
    col = vec3(draw);
    vec3 neon = 1.-col;

    float grad = 1.-mod(b,0.1)*10.;
    float timestep = floor(-iTime*10.*SPEED);
    float band = floor(b*10.+timestep)*0.13;
    // col += band;
    col *= hsv2rgb_smooth(vec3(band,1.,1.));
    // col += grad*0.3;
    // col /= .8+grad;
    // col *= 1.2-grad;
    // col = vec3(grad*grad+(1.-grad));

    // deboog
    // if (uv.x<sin(iTime*0.33)) col = vec3(b);

    if (BUZZ) {
        col *= 0.3;
        col -= grad*grad * (1.-grad) * 3.;
        neon *= hsv2rgb_smooth(vec3(band,1.,1.));
        neon *= neon;
        col += neon*5.;
        float filament;
        for (int i=1;i<3;i++){
            filament = smoothstep(0.0,0.003*length(uv),nsin((b)*20.*PI-0.5*PI));
            col += vec3(1.-filament)*float(i)/2.;
            b += (noise(vec3(float(i)+uv*20.*float(i+1),iTime))*0.02-0.01) * (0.2+length(uv));
        }
    }

    if (inside) col*=0.;
    if (inside) col=texture(iChannel3,fragCoord/iResolution.xy).rgb;

    if (!inside) col *= nclamp(iTime*0.25);
    // Output to screen
    fragColor = vec4(col,1.0);
}

