// Improved iterative Signed Distance Field encoder based on V1:
//  https://www.shadertoy.com/view/XlfBDl
//
// It's a really simple approach at doing this and most likely
//  can be done much faster by splitting horizontal and vertical
//  passes and by using loops for testing for distances. There is
//  also a UV channel stored within the blue and alpha of the
//  result, allowing higher precision if necessary.
//
// Output definition:
// .r  = Positive Distance
// .g  = Negative Distance
// .ba = UV of nearest edge

// Parameters
const float threshold = 0.1;

#define POSSIBLY_INFINITE 18446744073709551616.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (iFrame == 0) {
        fragColor = vec4(POSSIBLY_INFINITE);
        return;
    }

    vec2 uvStep = 1.0 / iResolution.xy;
    vec3 uvStep0 = vec3(uvStep.x, uvStep.y, 0.);
    vec2 uv = fragCoord / iResolution.xy;

    bool inside = false;

    // Test if we are in- or outside of the shape.
    float alpha = texture(iChannel1, uv).a;
    if (alpha > threshold) {
        inside = true;
        fragColor.r = 0.0;
        fragColor.ba = uv;
    } else {
        inside = false;
        fragColor.g = 0.0;
        fragColor.ba = uv;
    }

    if (inside) { // Negative Values
        vec3 test = vec3(POSSIBLY_INFINITE, POSSIBLY_INFINITE, POSSIBLY_INFINITE);
        vec3 iU, iD, iL, iR;
        iU = texture(iChannel0, uv - uvStep0.zy).gba;
        iD = texture(iChannel0, uv + uvStep0.zy).gba;
        iL = texture(iChannel0, uv - uvStep0.xz).gba;
        iR = texture(iChannel0, uv + uvStep0.xz).gba;        
    	if (iU.r < test.r) { test = iU; }
    	if (iD.r < test.r) { test = iD; }
    	if (iL.r < test.r) { test = iL; }
    	if (iR.r < test.r) { test = iR; }

        vec3 m = vec3(0.4142135623730950488016887242097, 0, 0);
        iU = texture(iChannel0, uv - uvStep0.xy).gba;
        iD = texture(iChannel0, uv - vec2(-uvStep0.x, uvStep0.y)).gba;
        iL = texture(iChannel0, uv + uvStep0.xy).gba;
        iR = texture(iChannel0, uv + vec2(uvStep0.x, -uvStep0.y)).gba;
    	if (iU.r < test.r) { test = iU + m; }
    	if (iD.r < test.r) { test = iD + m; }
    	if (iL.r < test.r) { test = iL + m; }
    	if (iR.r < test.r) { test = iR + m; }

        if (test.r < POSSIBLY_INFINITE) {
            fragColor.g = test.r + 1.;//distance(fragCoord, test.gb / uvStep) + 0.5;
            fragColor.ba = test.gb;
        }
    } else { // Positive Values
        vec3 test = vec3(POSSIBLY_INFINITE, POSSIBLY_INFINITE, POSSIBLY_INFINITE);
        vec3 iU, iD, iL, iR;
        iU = texture(iChannel0, uv - uvStep0.zy).rba;
        iD = texture(iChannel0, uv + uvStep0.zy).rba;
        iL = texture(iChannel0, uv - uvStep0.xz).rba;
        iR = texture(iChannel0, uv + uvStep0.xz).rba;

    	if (iU.r < test.r) { test = iU; }
    	if (iD.r < test.r) { test = iD; }
    	if (iL.r < test.r) { test = iL; }
    	if (iR.r < test.r) { test = iR; }

        vec3 m = vec3(0.4142135623730950488016887242097, 0, 0);
        iU = texture(iChannel0, uv - uvStep0.xy).rba;
        iD = texture(iChannel0, uv - vec2(-uvStep0.x, uvStep0.y)).rba;
        iL = texture(iChannel0, uv + uvStep0.xy).rba;
        iR = texture(iChannel0, uv + vec2(uvStep0.x, -uvStep0.y)).rba;
    	if (iU.r < test.r) { test = iU + m; }
    	if (iD.r < test.r) { test = iD + m; }
    	if (iL.r < test.r) { test = iL + m; }
    	if (iR.r < test.r) { test = iR + m; }

        if (test.r < POSSIBLY_INFINITE) {
            fragColor.r = test.r + 1.;//distance(fragCoord, test.gb / uvStep) + 0.5;
            fragColor.ba = test.gb;
        }
    }
}

