        for(int y=-1; y <= 1; y++)
        {
            vec2 offs = vec2(x,y);
            vec2 cID = hash22(ID + offs);
            float nID = cID.x + cID.y * 0.5;
            float nVariant = nID * 10000.;
            float erratic = abs(sin(iTime * 0.25 + nVariant));
            float movSpeed = iTime + (PI *erratic);
            vec2 mov = getCosSin(nVariant + movSpeed);
            vec2 pos = gv - offs -  mov * .5;


            float orbitPhase =  100. * (nID);


            float orbitSpeed = iTime * 3.0;
            if (nID > 0.5)
                orbitSpeed *= -1.;
            vec3 h = hue(cID.x + cID.y);
            float erraticScale = 0.1 + (0.9 * erratic);
            float orbitScale = 0.1 * erraticScale;



            float p = 0.;
            float orbitDist = 0.2 * erraticScale;
            p += getBall(pos + getOrbitOffset(orbitSpeed, orbitPhase, orbitDist), orbitScale, 0.);                                    
            p += getBall(pos + getOrbitOffset(orbitSpeed, orbitPhase + radians(180.), orbitDist), orbitScale, 0.);                                    
            p += getBall(pos + getOrbitOffset(-orbitSpeed, orbitPhase, 0.2*erraticScale), orbitScale,0.);                                    
            p += getBall(pos + getOrbitOffset(-orbitSpeed, orbitPhase + radians(180.), orbitDist), orbitScale,0.);                                                
            p += getBall(pos + getOrbitOffset(orbitSpeed * 2., orbitPhase + radians(0.), orbitDist), orbitScale*.75, 0.);                                    
            p += getBall(pos + getOrbitOffset(-orbitSpeed * 2., orbitPhase + radians(180.), orbitDist), orbitScale*.75, 0.);                                    
            p += getBall(pos + getOrbitOffset(orbitSpeed * 4., orbitPhase + radians(0.), orbitDist), orbitScale*.5, 0.);                                    
            p += getBall(pos + getOrbitOffset(-orbitSpeed * 4., orbitPhase + radians(180.), orbitDist), orbitScale*.5, 0.);                                    


            float centerPulse = smoothstep(0.2, 0., length(sin((iTime * (1.0 + (2.0 *nID)) ) + nVariant)));
            centerPulse += 0.3 * abs(sin(iTime*4.));

            p += getBall(pos , 0.2 * erraticScale, centerPulse);
            col += h * p;
            col = mix(col, vec3(1.), min(max(0., p - 1.), 1.0)) * pow(erraticScale,0.05);

            float dist = pow(length(pos), erraticScale );

            if(dist < nDist)
            {
                nDist = dist;
            }
        }
    }


    col += hue(pow(nDist  , 3.)+ (iTime * 0.5)) * pow(nDist, 8.) * 0.25;


    fragColor = vec4(col,1.0);
}

// Texturing
// Zinedine Zidane

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
vec3 c;
float s;
};

// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : point
// c : center 
// r : radius
V Sphere(vec3 p, vec3 c,float r,int index)
{
  return V(length(p-c)-r,index);
}

// Cube 
// p : point
// c : center 
// r : radius
V Cube(vec3 p,vec3 c,float r,int index)
{
   vec3 q = abs(p-c) - vec3(r);
  return V(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0),index);
}

// Plane 
// p : point
// n : Normal of plane
// o : Point on plane
V Plane(vec3 p, vec3 n, vec3 o,int index)
{
    return V(dot((p-o),n),index);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

// Dice 
// p : point
// c : center 
// r : radius
V Dice(vec3 p,vec3 c,float r,int index)
{
  return Intersection(Cube(p,c,r*0.75,index),Sphere(p,c,r,index));
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V u = Plane(p,normalize(vec3(0.0,0.0,1.0)),vec3(0.0,0.0,-4.0),0);

  u=Union(u,Dice(p,vec3( 0.0, 0.0, 2.0),4.0,1));
  u=Union(u,Dice(p,vec3( -10.0, 0.0, 2.0),4.0,2));
  u=Union(u,Dice(p,vec3( 10.0, 0.0, 2.0),4.0,3));

  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.65,0.75,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


float Turbulance(in vec3 p, in int i, in float puiss, in float res, in float nb)
{
    mat3 M = Rz(0.0);
    float som = 0.0; 
    for(int k=0; k < i; k++)
        {
        res = res * pow(puiss, float(-k));
        nb = nb * pow(puiss, float(-k));
        M = matrixCompMult(M, M);

        som = som + res * Noise((M*p)/nb);
        }

    return som; 
}

vec3 Noise3D(vec3 p)
{
 	return vec3(Noise(p), Noise(p + vec3(165.0,126.0,127.0)), Noise(p + vec3(34.0,242.0,342.0)));
}

float decaler(float d)
{
    if(d<0.0){
    return d = d + -1.0;
    }
    else{
    return d;
    }
}

int damier  ( float x , float y , float z ) {
x= decaler(x);
y=decaler(y);
z=decaler(z);
return ( int ( x ) + int ( y ) + int ( z ) ) % 2 ;
}

T Bois(in vec3 p, in vec3 c1, vec3 c2, float t, int octaves, float contrast)
{
  vec3 pt = p - (Turbulance(p, octaves, 2., 4.8, 8.8) * (1. / t));  
  c1 = mix(contrast*c1, 
           (1.-contrast)*c1,
           Turbulance(vec3(25.0*pt.x,300.0*pt.y,1.0*pt.z),4, 2., .8, 2.5)*(1.-.7*Noise(200.*pt)));
  c2 = mix(contrast*c2, 
           (1.-contrast)*c2,
           Turbulance(vec3(25.0*pt.x,300.0*pt.y,1.0*pt.z),4, 2., .8, 2.5)*(1.-.7*Noise(200.*pt)));
  float check = ((cos(3.1415927 *sqrt(pt.x*pt.x + pt.y*pt.y))) + 1.) / 2.; // stripes

  return T((1.-check)*c1+ check*c2, 0.1);
}

float Ligne(vec3 p)
{
 	vec3 q=p+1.0*Noise3D(p)+0.5*Noise3D(p/0.5);
    float z=abs(mod(q.z,0.5)-0.5)*3.0*Noise(p);
    return smoothstep(z,0.0,0.02);
}

vec3 Zebre(vec3 p,vec3 c1,vec3 c2)
{
    float l=Ligne(p);
    return mix(c1,c2,l);
}

vec3 lerp(vec3 colorone, vec3 colortwo, float value)
{
	return (colorone + value*(colortwo-colorone));
}

T marbre(vec3 p,float i)
{

    p = p + 1.0*Noise3D(p/1.0);
    float d = length(p.z);
    float l = Ligne(p);
    l = min(l, Ligne(p.zxy));

  p = lerp(vec3(0.8,0.8,0.8),vec3(1,1,1),l);
  return T(p,i);
}


// Shading and lighting ---------------------------------------------------------------------------

T Noir(in vec3 p)
{
  return T(vec3(0.0,0.0,0.0),1.2);   
}

T Blanc(in vec3 p)
{
  return T(vec3(1.0,1.0,1.0),1.0);   
}

T Rouge(in vec3 p)
{
  return T(vec3(1.0,0.0,0.0),1.0);   
}

T Vert(in vec3 p)
{
  return T(vec3(0.0,1.0,0.0),1.0);   
}

T Bleu(in vec3 p)
{
  return T(vec3(0.0,0.0,1.0),1.0);   
}

T Damier(in vec3 p)
{

    return T(vec3(damier (p.x, p.y, p.z),damier (p.x, p.y, p.z),damier (p.x, p.y, p.z)),0.25); 
}

T Bois(in vec3 p)
{
    return Bois(p,vec3(0.49, 0.29, 0.13), vec3(0.49, 0.29, 0.13)*1.7, 0.1,3,0.8);
}

T Marbre(in vec3 p)
{
    return marbre(p,1.0);
}



// Compute texture 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= object(p);
    if (vp.i==0){

        return Blanc(p);
    }
    else if (vp.i==1)
    {
        return Marbre(p);
        //return Marbre(p);
    }
    else if (vp.i==2)
    {
        return Bois(p);
    }
    else if (vp.i==3)
    {
        return Damier(p);
    }
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p,l,100.0,h,s);
    if (!h)
    {
     return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    T tex=Color(p,n);

    // Ambient color
    vec3 ambient = 0.5*tex.c;

    // Shadow computation
    float s = Shadow(p+0.01*n,n,l);

// Phong diffuse
    vec3 diffuse = 0.5  * clamp(dot(n, l),0.0,1.0) * tex.c;

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = tex.s * pow(clamp(dot(r,l),0.0,1.0),28.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + s * (diffuse + specular);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

