vec2 uvFromXy(in vec2 xy) {
  return xy/iResolution.xy;
}

float getVal(in vec2 xy) {
  return dot(texture(iChannel0, uvFromXy(xy)).rgb, vec3(1.0));
}

vec2 getGrad(in vec2 xy) {
  const float h = 3.0;
  float f = getVal(xy);
  return vec2(getVal(xy + vec2(h, 0.0)) - f,
              getVal(xy + vec2(0.0, h)) - f) / h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float stepSize = 0.2;
    const float lineWidth = 3.0;
    float f = getVal(fragCoord);
    vec2 grad = getGrad(fragCoord);
    float gradMag = length(grad);

    float nearest = floor((f + 0.5 * stepSize)/stepSize) * stepSize;

    float pixToLine = abs(f - nearest)/gradMag;

    float lineNess = 1.0 - max(0.0, min(1.0, pixToLine - lineWidth * 0.5));

    // lineNess *= min(1.0, max(3.0 - f, 0.0) / 3.0);
    lineNess *= step(0.0125, gradMag);

    vec3 col = vec3(1.0 - lineNess);

    vec3 rgbCol = texture(iChannel0, fragCoord/iResolution.xy).rgb;
    rgbCol = rgbCol + vec3(0.2) - mod(rgbCol, vec3(0.2));

