vec3 saturate(vec3 c)
{
    return 1.3*pow(c, vec3(1.2));
}

#define SHARPEN 2.0
#define LOWSAMPLE_BLUR 1.
vec4 sample_adaptive(sampler2D ch, vec2 uv)
{
    vec3 dx = vec3(0.5/vec2(textureSize(ch, 0)),0.);
    const float k = SHARPEN;
    vec4 c = texture(ch, uv);

    vec4 u = texture(ch, uv + dx.zy);
    vec4 d = texture(ch, uv - dx.zy);
    vec4 r = texture(ch, uv + dx.xz);
    vec4 l = texture(ch, uv - dx.xz);
    vec4 u1 = texture(ch, uv + dx.xy);
    vec4 d1 = texture(ch, uv - vec2(1, -1)*dx.xy);
    vec4 r1 = texture(ch, uv - dx.xy);
    vec4 l1 = texture(ch, uv + vec2(1, -1)*dx.xy);
    return (1.+k)*c - k*(u+d+r+l+1.5*(u1+d1+r1+l1)/4.0)/(4.0 +1.5); 
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec4 acc = sample_adaptive(iChannel0, fragCoord/iResolution.xy);
    fragColor = vec4(saturate(acc.xyz/acc.w), 1.0);
}

