sdfRet sdf(vec3 point)
{
    //body
    sdfRet belly = sdfRet(sphere(point, vec3(.0, -.95, 1.1), .9), white_id);
    sdfRet head = sdfRet(sphere(point, vec3(.0), 1.0), green_id);
    float back = sphere(point, vec3(.0, -.1, -.9), .7);

    sdfRet allParts = paintUnionSDF(head, belly);
    allParts = sdfRet(smin(allParts.dist, back, .7), allParts.id);

    vec3 mirroredX = mirrorX(point); 

    //face
    sdfRet eyes = sdfRet(sphere(mirroredX, vec3(.4, .3, .68), .3), brown_id);
    sdfRet mouth = sdfRet(capsule(bend(point, 2.0), vec3(-.22, -.15, 1.0), vec3(.22, -.15, 1.0), .1), brown_id);
    sdfRet blush = sdfRet(sphere(mirroredX, vec3(.68, -.03, .7), .15), pink_id);

    allParts = minSDF(allParts, eyes);
    allParts = minSDF(allParts, mouth);
    allParts = minSDF(allParts, blush);

    //back legs
    float backThighs = sphere(mirroredX, vec3(.6, -.1, -.6), .7);
    float backFeet = capsule(mirroredX, vec3(.9, -.6, -.6), vec3(1.0, -.6, -.2), .3);
    sdfRet backLegs = sdfRet(smin(backThighs, backFeet, .1), allParts.id);
    allParts = minSDF(allParts, backLegs);

    //front legs
    float frontThighs = sphere(mirroredX, vec3(.8, -.6, .4), .2);
    float frontFeet = capsule(mirroredX, vec3(.8, -.7, .4), vec3(.5, -.8, .6), .2);
    sdfRet frontLegs = sdfRet(smin(frontFeet, frontThighs, .1), green_id);
    allParts = minSDF(allParts, frontLegs);

    return allParts;
}


//from iq: https://iquilezles.org/articles/normalsSDF
vec3 calcNormal(in vec3 p)
{
    const vec2 k = vec2(1,-1);
    return normalize( k.xyy*sdf( p + k.xyy*CLOSE_ENOUGH).dist + 
                      k.yyx*sdf( p + k.yyx*CLOSE_ENOUGH).dist + 
                      k.yxy*sdf( p + k.yxy*CLOSE_ENOUGH).dist + 
                      k.xxx*sdf( p + k.xxx*CLOSE_ENOUGH).dist );
}

//from: https://www.shadertoy.com/view/lllBDM
float subsurface(vec3 p, vec3 v, vec3 n){
    //vec3 d = normalize(mix(v, -n, 0.5));
    // suggested by Shane
    vec3 d = refract(v, n, 1.0/1.5);
    vec3 o = p;
    float a = 0.0;

    const float max_scatter = 1.5;
    for(float i = 0.1; i < max_scatter; i += 0.2)
    {
        o += i*d;
        float t = sdf(o).dist;
        a += t;
    }
    float thickness = max(0.0, -a);
    const float scatter_strength = 16.0;
	return scatter_strength*pow(max_scatter*0.5, 3.0)/thickness;
}

vec3 light(vec3 point, float id, vec3 viewDir)
{
    vec3 norm = calcNormal(point);
    vec3 baseColor = idToColor(id);
    vec3 lightDir = normalize(vec3(1.0,1.0,.0));
    vec3 reflectDir = reflect(lightDir, norm);
    float lambert = lambertian(norm, lightDir);
    //from: https://www.shadertoy.com/view/lllBDM
    float subsurface_scattering = pow(subsurface(point, viewDir, norm), 0.6);
    lambert = mix(lambert, 1.5*smoothstep(0.1, 6.0, subsurface_scattering), 0.5);

    vec3 shading = vec3(1.0,.9,.8)*lambert + pow(max(dot(viewDir, reflectDir), 0.0), 2.0);
    return baseColor * shading;
}

vec3 Image(vec2 uv)
{
    float mousex = 9.0*iMouse.x/iResolution.x;
    ray currentRay = cameraRay(mousex, uv);

    vec3 col = sky(currentRay.direction);

    float currentDistance = .0;

    for(int i = 0; i < ITERATIONS; i++)
    {
        vec3 samplePoint = currentRay.origin + currentRay.direction*currentDistance;
        sdfRet ret = sdf(samplePoint);
        if(ret.dist < CLOSE_ENOUGH)
        {
            col = light(samplePoint, ret.id, currentRay.direction);
            break;
        }
        currentDistance += ret.dist;
    }

    return col;
}


//------------ boilerplate

vec4 colorCorrect(vec3 color)
{
    vec3 x = max(vec3(.0), color-.004);
    vec3 retColor = (x*(6.2*x+.5))/(x*(6.2*x+1.7)+0.06);
    return vec4(min(retColor, 1.0), 1.0);
}

//AA trick from Fabrice Neyret: https://shadertoyunofficial.wordpress.com/2021/03/09/advanced-tricks/                     
void mainImage0(out vec4 frogColor, in vec2 fragCoord)
{                                                     
    vec2 uv = fragCoord/iResolution.xy - vec2(.5);    
    uv.x *= iResolution.x/iResolution.y;              
    frogColor = colorCorrect(Image(uv));           
}                                                     

void mainImage(out vec4 frogColor, in vec2 fragCoord)  
{                                                     
    mainImage0(frogColor,fragCoord);                  
    if ( fwidth(length(frogColor)) > .01 )            
    {                                                 
        vec4 col;                                     
        for (int k=0; k < 9; k+= k==3?2:1 )           
        {                                             
            vec2 offset = vec2(k%3-1,k/3-1)/3.;       
            mainImage0(col,fragCoord+offset);         
            frogColor += col;
        }                                             
        frogColor /= 9.;
    }                                                 
}
