#define CLOSE_ENOUGH 0.0001
#define ITERATIONS 500
#define CAMERA_DISTANCE 6.0
#define CAMERA_HEIGHT .5

#define COLOR(name, id, r,g,b) const vec3 name = vec3(r,g,b)/255.0; const float id = float(__LINE__);
#define RETURN_COLOR(name, col_id) if(id <= col_id) return name;

struct ray
{
	vec3 origin;
    vec3 direction;
};

struct sdfRet
{
    float dist;
    float id;
};


//------------ all from Inigo Quilez: https://iquilezles.org/articles/distfunctions
float capsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}

float sphere(vec3 point, vec3 origin, float radius)
{
	return distance(point,origin)-radius;
}

float smin(float d1, float d2, float k) 
{
    float h = clamp( 0.5 + 0.5*(d2-d1)/k, 0.0, 1.0 );
    return mix( d2, d1, h ) - k*h*(1.0-h); 
}

vec3 bend(vec3 point, float amount)
{
    float c = cos(amount*point.x);
    float s = sin(amount*point.x);
    mat2  m = mat2(c,-s,s,c);
    vec3  q = vec3(m*point.xy,point.z);
    return q;
}

//------------ end of iq code


vec3 mirrorX(vec3 point)
{
    return vec3(abs(point.x), point.y, point.z);
}

sdfRet minSDF(sdfRet a, sdfRet b)
{
    if(a.dist < b.dist)
    {
        return a;
    }
    return b;
}

sdfRet paintUnionSDF(sdfRet a, sdfRet b)
{
    sdfRet bPrime = sdfRet(max(a.dist, b.dist), b.id);
    return minSDF(a, bPrime);
}


float lambertian(vec3 n, vec3 l)
{
	return dot(n,l);
} 

vec3 sky(vec3 direction)
{
    return vec3(.2,.4,.9)-vec3(.4,.4,.9)*direction.y;
}


ray cameraRay(float mousex, vec2 uv)
{
    vec3 target = vec3(.0,.0,.0);
    vec3 rayOrigin = target + vec3(CAMERA_DISTANCE*sin(mousex),CAMERA_HEIGHT,CAMERA_DISTANCE*cos(mousex));
    vec3 front = normalize(target - rayOrigin);
    vec3 right = normalize(cross(front, vec3(0.,1.,0.)));
    vec3 up = normalize(cross(right, front));
    return ray(rayOrigin, normalize(uv.x*right+uv.y*up+1.0*front));
}

