#define SPP 1

//Note, no time nor polar angle is used here. 
//A wormhole does not have gravity and is spherically symmetric thus such simplifications are applicable

const float max_distance = 50.0f;
const float eps = 0.005;
const vec2 dq = vec2(0, eps);
float dt = 0.1;


float sqr(float x)
{
    return x*x;
}


float EllisR(float l)
{
    const float rho = 1.0; //radius
    const float a = 6.0; //tunnel length 
    const float M = 0.5; //"mass"

    float x = 2.0*(abs(l) - a)/(PI*M);
    return (abs(l) > a)?(rho + M*(x*atan(x) - 0.5*log(1.0 + x*x))):rho;
}

vec2 DiagG(vec2 q)
{
    return vec2(1.0, sqr(EllisR(q.x)));
}

vec2 DiagGinv(vec2 q)
{
    return vec2(1.0, 1.0/sqr(EllisR(q.x)));
}

float L(vec2 q, vec2 qt)
{
    return dot(DiagG(q), qt*qt);
}

float H(vec2 q, vec2 p)
{
    return dot(DiagGinv(q), p*p);
}

vec2 ToMomentum(vec2 q, vec2 qt)
{
    return DiagG(q)*qt; 
}

vec2 FromMomentum(vec2 q, vec2 p)
{
    return DiagGinv(q)*p; 
}

void GeodesicStep(inout vec2 q, inout vec2 p)
{  
    vec2 qt = FromMomentum(q, p);

    vec2 dHdq = (vec2(L(q+dq.yx,qt),L(q+dq.xy, qt))-L(q, qt))/eps; 

    float r = EllisR(q.x);
    dt = mix(0.3, 0.6, clamp(r / 10.0, 0.0, 1.0)); 

    p += dHdq*dt;
    q += 2.0*qt*dt;
}

void CameraToGeodesic(vec3 ro, vec3 rd, out vec2 q, out vec2 qt, out vec3 ax1, out vec3 ax2)
{
    //get spherical coordinate axis
    ax1 = normalize(ro); // X
    vec3 ax0 = normalize(cross(rd, ax1)); // Z
    ax2 = normalize(cross(ax1, ax0)); // Y

    q = vec2(length(ro)-25.0, 0);
    qt = vec2(dot(ax1, rd), dot(ax2, rd)/EllisR(q.x)); 
}

void GeodesicToCamera(vec2 q, vec2 qt, vec3 ax1, vec3 ax2, out vec3 ro, out vec3 rd)
{
    //polar coordinates
    vec2 s1 = vec2(cos(q.y), sin(q.y));
    vec2 s2 = vec2(-sin(q.y), cos(q.y));


    vec2 s = s1*qt.x + s2*qt.y*EllisR(q.x);

    rd = s.x*ax1 + s.y*ax2;
    ro = abs(q.x)*(s1.x*ax1 + s1.y*ax2);
}


void TraceGeodesic(inout vec3 ro, inout vec3 rd, inout float l)
{
    float t0 = sphIntersect(ro, rd, vec4(0,0,0,max_distance));
    if(t0 > 0.0 && t0 < 1e10) ro += rd*t0;

    vec3 ax1, ax2;
    vec2 q, qt;

    CameraToGeodesic(ro, rd, q, qt, ax1, ax2);

    vec2 p = ToMomentum(q, qt);

    for(int i = 0; i <128*64; i++)
    {
        GeodesicStep(q, p);
        if(abs(q.x) > max_distance) break;
    }

    qt = FromMomentum(q, p);

    GeodesicToCamera(q, qt, ax1, ax2, ro, rd);

    l = q.x;
}


const float aperture_size = 0.0;
vec2 aperture()
{
    vec2 r = rand2();
    return vec2(sin(TWO_PI*r.x), cos(TWO_PI*r.x))*sqrt(r.y);
}

bool getRay(vec2 uv, out vec3 ro, out vec3 rd)
{
    mat3 cam = getCam(get(CamA));

    vec2 apert_cent = -0.*uv;

    vec2 ap = aperture();

    if(!(distance(ap, apert_cent) < 1.0)) return false;

    float apd = length(ap);

    vec3 daperture = ap.x*cam[0] + ap.y*cam[1];

    ro = get(CamP).xyz + aperture_size*daperture;

    float focus =2.5 + 0.8*pow(apd,5.0);


    rd = normalize(focus*(cam*vec3(FOV*uv, 1.0)) - aperture_size*daperture);

    return true;
}

vec3 ToHDR(vec4 color)
{
    return 2.0*pow(length(color.xyz),2.0)*normalize(color.xyz+1e-4);
}

vec4 render(vec2 fragCoord)
{
    fragCoord += nrand2(1.0, vec2(0.));
    vec2 uv = (fragCoord - 0.5*iResolution.xy)/iResolution.y;

    vec3 cp, rd;
    if(!getRay(uv, cp, rd)) return vec4(0,0,0,1);

    vec3 ro = cp;
    vec3 fcol = vec3(0.0);
    float l;

    TraceGeodesic(ro, rd, l);

    if(abs(l) > 5.0)
    {
        if(l > 0.0)
            fcol = ToHDR(texture(iChannel1, rd));
        else
            fcol = ToHDR(texture(iChannel3, rd));
    }

    return vec4(fcol, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    rng_initialize(fragCoord, iFrame);

    if(get(CamP).w == 0.0)
        fragColor = texture(iChannel0, fragCoord/iResolution.xy);
    else
        fragColor = texture(iChannel0, fragCoord/iResolution.xy)*0.1;

   for(int i = 0; i < SPP; i++)
       fragColor += render(fragCoord);
}

