#define MAX_STEPS 400
#define MAX_DIST 100.
#define SURF_DIST .001

#define FK(k) floatBitsToInt(k*k/7.)^floatBitsToInt(k)
float hash(float a, float b) {
    int x = FK(a), y = FK(b);
    return float((x*x+y)*(y*y-x)-x)/2.14e9;
}

vec3 erot(vec3 p, vec3 ax, float ro) {
  return mix(dot(ax, p)*ax, p, cos(ro)) + cross(ax,p)*sin(ro);
}

vec3 face(vec3 p) {
     vec3 a = abs(p);
     return step(a.yzx, a.xyz)*step(a.zxy, a.xyz)*sign(p);
}

float sdBox(vec3 p, vec3 s) {
    p = abs(p)-s;
	return length(max(p, 0.))+min(max(p.x, max(p.y, p.z)), 0.);
}

vec3 getRo() {
    vec2 m = iMouse.xy/iResolution.xy;

    vec3 ro = vec3(3, 1.5, 3);
   // ro.yz *= Rot(-m.y*3.14+1.);
    //ro.xz *= Rot(-m.x*6.2831);
    return ro;
}


float GetDist(vec3 p) {
    float m = 1.;
    float d1 = 10000.;
    // p.y -= 1.;
    p.y += 0.05 * (p.x + p.z);
    for (float i = 0.; i < 5.; i++) {
        p.xz = abs(p.xz) - m;
        //p.y += 0.1 * (5.-i) * mlength(p.xz);
        p.y += 0.05 * cos(10. * p.x) * cos(2. * i * pi / 5. + 24. * length(p.xz) / pi + iTime);

        m *= 0.55;

        float r1 = 1.25 * m;
        d1 = -min(d1, length(p.xz) - r1);
    }

    float d3 = length(p) - m * 2.;
    d1 = -min(d1, -d3);

    return 0.5 * d1;
}

float RayMarch(vec3 ro, vec3 rd, float z) {

    float dO=0.;
    float s = sign(z);
    for(int i=0; i<MAX_STEPS; i++) {
    	vec3 p = ro + rd*dO;
        float dS = GetDist(p);
        if (s != sign(dS)) { z *= 0.5; s = sign(dS); }
        if(abs(dS)<SURF_DIST || dO>MAX_DIST) break;
        dO += dS*z; 
    }

    return min(dO, MAX_DIST);
}

vec3 GetNormal(vec3 p) {
	float d = GetDist(p);
    vec2 e = vec2(.001, 0);

    vec3 n = d - vec3(
        GetDist(p-e.xyy),
        GetDist(p-e.yxy),
        GetDist(p-e.yyx));

    return normalize(n);
}

vec3 GetRayDir(vec2 uv, vec3 p, vec3 l, float z) {
    vec3 f = normalize(l-p),
        r = normalize(cross(vec3(0,1,0), f)),
        u = cross(f,r),
        c = f*z,
        i = c + uv.x*r + uv.y*u,
        d = normalize(i);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

    vec3 ro = getRo();

    vec3 rd = GetRayDir(uv, ro, vec3(0), 3.5);
    vec3 col = vec3(0);

    float d = RayMarch(ro, rd, 1.);

    float IOR = 1.05;
    vec3 p = ro + rd * d;
    if(d<MAX_DIST) {

        vec3 n = GetNormal(p);
        vec3 r = reflect(rd, n);

        vec3 pIn = p - 4. * SURF_DIST * n;
        vec3 rdIn = refract(rd, n, 1./IOR);
        float dIn = RayMarch(pIn, rdIn, -1.);

        vec3 pExit = pIn + dIn * rdIn;
        vec3 nExit = -GetNormal(pExit); // *-1.; ?

        float dif = dot(n, normalize(vec3(1,2,3)))*.5+.5;
        col = vec3(1);vec3(dif);

        col *= 0.5 + 0.5 * n;

        float fres = pow(1.+dot(rd, n), 2.); 
        fres = clamp(fres, 0., 1.);
        col += fres;

        vec3 e = vec3(0.5);
        col *= pal(mod(floor(0.25 * p.z + 0.25 * iTime), 2.) + -0. * iTime + 0. * h21(floor(1. * p.xz - 0.1 * iTime)) + mlength(p.xz), e, e, e, 0.5 * vec3(0,1,2)/3.);
        //col *= col + 0.5;

    }
    col = mix(vec3(0.95), col, clamp(4. * exp(-4. * mlength(p.xz)),0.,1.));
    col = pow(col, vec3(.4545));	// gamma correction

    fragColor = vec4(col,1.0);
}

