#define Pi 3.14159263
#define DIV 500
#define PER 25
vec4 load(float x, float y){return texture(iChannel0,vec2(x+.5,y+.5)/rez.xy);}
vec4 cmap(float a){return abs(vec4(sin(a),sin(a+.4),sin(a+1.4),0.));}
float tube(vec2 pa, vec2 ba){return length(pa-ba*clamp(dot(pa,ba)/dot(ba,ba),0.0,1.0));}
void mainImage(out vec4 O, in vec2 U){
  O=texture(iChannel0,U/rez.xy);
  U=floor(U);
  vec4 L=load(0.,0.);
  int mf=DIV*2,f=(PER*iFrame) % mf;
  float s=floor(float(iFrame*PER)/float(mf)),q=(s+1.)/float(DIV);
  if(f==0){//setup new doodles
    if(iFrame==0)O=vec4(0);
    if(U.x+U.y==0.){
      if(iFrame==0)O=vec4(-15.,0.,15.,0.);
      if(length(O.xy)>20.)L.xy=O.xy=vec2(-15.,0.);else L.y=O.y-=.25;
      if(length(O.zw)>20.)L.zw=O.zw=vec2(15.,0.);else L.z=O.z-=.25;
    }
  }
  vec2 p1=L.xy,p2=L.zw,nv,u=20.*(2.*U-rez.xy)/rez.y;
  float a,d1=1.,d2=1.,q2=(s+1.)/float(DIV+2);
  for(int i=0;i<PER;i++){
    float n=float(f+i+1);
    a=2.*Pi*fract((n-1.)*fract(q*(n-1./5.)));
    nv=vec2(cos(a),sin(a));
    d1=min(d1,tube(u-p1,nv));
    p1+=nv;
    a=2.*Pi*fract((n-1.)*fract(q2*(n-2./3.)));
    nv=vec2(cos(a),sin(a));
    d2=min(d2,tube(u-p2,nv));
    p2+=nv;
  }
  if(U.x+U.y==0.){O=vec4(p1,p2);}//logic pixel
  else {//draw
    O=mix(O,vec4(-.25,0,.25,0),0.005);
    d1=smoothstep(40./rez.y,0.,d1);d2=smoothstep(40./rez.y,0.,d2);
    O=mix(O,cmap(s),d1);
    O=mix(O,cmap(s+.7),d2);
  }
}

