      if(length(O.xy)>20.)L.xy=O.xy=vec2(-15.,0.);else L.y=O.y-=.25;
      if(length(O.zw)>20.)L.zw=O.zw=vec2(15.,0.);else L.z=O.z-=.25;
    }
  }
  vec2 p1=L.xy,p2=L.zw,nv,u=20.*(2.*U-rez.xy)/rez.y;
  float a,d1=1.,d2=1.,q2=(s+1.)/float(DIV+2);
  for(int i=0;i<PER;i++){
    float n=float(f+i+1);
    a=Pi*q*.5+2.*Pi*fract((n-1.)*fract(q*(n-.5)));
    nv=vec2(dSin(a),dCos(a));
    d1=min(d1,tube(u-p1,nv));
    p1+=nv;
    a=Pi*q2*.5+2.*Pi*fract((n-1.)*fract(q2*(n-.5)));
    nv=vec2(dSin(a),dCos(a));
    d2=min(d2,tube(u-p2,nv));
    p2+=nv;
  }
  if(U.x+U.y==0.){O=vec4(p1,p2);}//logic pixel
  else {//draw
    O*=.995;
    //O=mix(O,vec4(-.25,0,.25,0),0.005);
    d1=smoothstep(40./rez.y,0.,d1);d2=smoothstep(40./rez.y,0.,d2);
    O=mix(O,cmap(s),d1);
    O=mix(O,cmap(s+.7),d2);
  }
}

