int N[8]=int[8](0,3,5,7,5,2,3,0);
float scale(float note){//throws out dissonant tones 
 float n2=mod(note,12.); 
 if((n2==1.)||(n2==4.)||(n2==6.)||(n2==8.)||(n2==11.))note=-100.;//mixolydian +minor 3rd 
 return note; 
}
// note number to frequency  from https://www.shadertoy.com/view/ldfSW2 
float ntof(int n0){
 float n=scale(float(n0));
 return (n>0.)?440.0 * pow(2.0, (float(n) - 67.0) / 12.0):0.0;
} 
vec2 rot(vec2 v, float a) {return cos(a)*v+sin(a)*vec2(v.y,-v.x);}
vec2 I(int n, float t, vec3 p, vec4 e, vec4 d){
  float bt=t,t2,t3,t4,f=ntof(n);
  if(f==0.)return vec2(0);
  t-=t*d.y*sin((d.w*pi+t)*pi*d.z);t3=t*d.x;
  t2=fract(t*p.y*f);t4=fract(t3*p.y*f);
  t=fract(t*f);t3=fract(t3*f);
  float amp=abs(1.+sin(bt*e.z*pi)*e.w)*exp(-bt*e.y)*(1.0-exp(-bt*e.x))*min(1.,100.-bt*100.);
  return amp*rot(vec2(sin(pow(t,p.x)*pi*2.+pi*2.*p.z*sin(pow(t2,p.x)*pi*2.)),
    sin(pow(t3,p.x)*pi*2.+pi*2.*p.z*sin(pow(t4,p.x)*pi*2.))),float(n)+bt*pi*2.);
}
vec2 Sound(float time, float bps){
//return vec2(sin(time*500.));
  float tim=mod(time*bps,256.);
  float m=pow(fract(fract(tim+.5)*1.125),20.);tim+=sin(m);
  float bt=floor(tim),t=fract(tim);
  int n1=N[int(bt/64.)];
  int n0=n1+N[int(bt/8.)%8],n=n0+N[int(bt)%8],n2=n0+N[int(bt/2.)%8],n4=n0+N[int(bt/4.)%8];
  vec2 v=I(n+24,t,vec3(1.-t*.5,1.,t),vec4(10.,1.0,12.,t*.3),vec4(1.014,0.005,1.,.5))*.25;
  t=fract(tim/2.);
  float fo=pow(1.-t,2.0);
  v+=I(n2+36,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,4.0,24.,t*.8),vec4(1.014,0.005,1.,.5));

  t=fract(tim/4.);
  fo=pow(1.-t,2.0);
  v+=I(n4+48,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,4.0,36.,t*.8),vec4(1.014,0.005,1.,.5))*.75;
  v+=I(n4+57,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,4.0,36.,t*.8),vec4(1.014,0.005,1.,.5))*.5;

  t=fract(tim/8.);
  fo=pow(1.-t,2.0);
  v+=I(n0+36,t,vec3(1.-fo*.5,1.5,fo),vec4(10.,2.0,0.,t*.8),vec4(1014.014,0.005,1.,.5));
  return v*.1;
}
vec2 mainSound(int samp, float time){
