                spe *= f0 + (1.0-f0)*pow(fre,1.0);
                spe *= 6.0;

                //vec3 lcol = vec3(0.7, 0.8, 1.1);
                vec3 lcol = vec3(1.);

                col += 0.05*lcol*dif*occ;
                col += ks*lcol*spe*dif;

            }

            // bottom light
            {
                float dif = clamp( 0.5-0.5*nor.y, 0.0, 1.0 );
                col += mate*dif*occ*2.*vec3(3.);//*vec3(0.7, 0.8, 1.1);
            }

        }

        // vignette
        col *= 1.0 - 0.2*dot(p,p);

        // gamma        
	    tot += pow(col,vec3(0.35) );
    #if AA>1
    }
    tot /= float(AA*AA);
    #endif

    // S-curve
    tot = clamp(tot, 0.0, 1.0);
    //tot = tot*tot*(3.0-2.0*tot); // same as below
    tot = smoothstep(0.0,1.0,tot); // make it more contrasty

    // dither
    tot += (1.0/512.0)*sin(fragCoord.x*111.0)*sin(fragCoord.y*321.5423);

    // show banding
    //tot = floor(tot*255.)/255.;
    //tot = (abs(dFdy(tot))+abs(dFdx(tot)))*200.0;

    fragColor = vec4( tot, 1.0 );
}

//Daily Shader Challenge Day 6
//Ive learned a lot from this one, there are still some issues with the lighting messing with 
//the rotation of the cd but its in a good enough state for now.
//huge thanks to Alan Zucconi for the CD shader effect (https://www.alanzucconi.com/2017/07/15/the-nature-of-light/)
//lopea (https://github.com/lopea)

#define CD 0.
#define BACK -1.
#define COVER 1.

//#define ALT_COVER


#define time iTime

//////////////////////////////////////////////////////////////////////////////
//a lazy copy-paste from a previous shader i made lmaoooo
//https://www.shadertoy.com/view/flXGW7
//////////////////////////////////////////////////////////////////////////////

//IQ's Gradient noise algorithm
/////////////////////////////////////////////////////////////////////////////
vec2 random(vec2 st)
{
    st = vec2( dot(st,vec2(127.1,331.7)),
              dot(st,vec2(269.5,183.3)) );
    return -1.0 + 2.0*fract(sin(st)*43758.5453123);
}

float noise(vec2 uv)
{
    vec2 i = floor(uv);
    vec2 f = fract(uv);

    vec2 a  = random(i);
    vec2 b = random(i + vec2(1,0));
    vec2 c = random(i + vec2(0, 1));
    vec2 d = random(i + vec2(1, 1));

    vec2 u = f * f * f *(f *(f*6. - 15.)+10.);

    return mix(mix ( dot( a, f), dot(b, f - vec2(1, 0)), u.x),
        mix ( dot( c, f-vec2(0,1)), dot(d, f - vec2(1, 1)), u.x), u.y);

}
/////////////////////////////////////////////////////////////////////////////

//fractal brownian motion
float fbm(vec2 uv)
{
    //store the result of the noise texture
    float result = 0.;

    //store the current amplitude value
    float amplitude = 1.;

    //iterate a few times to give noise a more detailed look
    for(int i = 0; i < 8; i++)
    {
        //add to the result ( with a few modifications
        result += noise(uv + vec2( time - float(i)  +10., -time/25.- float(i)/2.)) * amplitude;

        //shrink the noise texture for the next iteration
        uv *= 2.;

        //make next noise iteration less potent 
        amplitude *= 0.5;
    }


    return result;
}

vec4 dith(vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord;

    //set the threshold for color value, will be compared to screen space.
    float threshold = fbm(vec2(fbm(uv * 10.), fbm(uv * 10. + 3.)));

    //create the dithering effect by warping the screen coordinates
    float coord = sin(uv.x * 1000.) * sin(uv.y * 1000.) * .25;

    //compare warped screen coords to the threshold and create the color value
    vec3 color = mix(vec3(0, .04, .1), vec3(1,0,.5) , vec3(step(coord, threshold)));

    //create an outline effect by subracting same algorithm but with a smaller threshold
    color -= step(clamp(coord, 0., 1.), threshold - .1) * vec3(1);

    //set the color to the pixel
    return vec4(color , 1);

}

