//////////////////////////////////////////////////////////////////////////////
//CD - reflectance equations
//////////////////////////////////////////////////////////////////////////////


//color bump equation
float bump(float x, float a, float o)
{
    return -a * (x-o) * (x-o) + 1.;
}

vec3 spectral(float x)
{
    return vec3(
    bump(x,16., .75),
    bump(x,8., .5),
    bump(x,16., .25)
    );
}


//////////////////////////////////////////////////////////////////////////////
//Shape helper functions (thanks iq)
//////////////////////////////////////////////////////////////////////////////
float cyl(vec3 p,float r , float h)
{
    vec2 v = abs(vec2(length(p.xy), p.z)) - vec2(r,h);
    return length(max(v, 0.)) + min(max(v.x,v.y),0.);
}

float cd(vec3 p)
{
    return max(-cyl(p, .1, 0.1), cyl(p, 1., 0.001)) - .01; 
}
float sbx(vec2 p , vec2 o, float r)
{
    vec2 v = abs(p) - o;

    return length(max(v,0.)) + min(max(v.x,v.y),0.) - r;
}

float six(vec2 p)
{
    p.x -= .019;
    float ii = min(sbx(p+vec2(-.11,-0.00), vec2(.016, .02), .002),sbx(p+vec2(-.06,0), vec2(.005, .02), .002));
    float oo = min(sbx(p+vec2(-.11,-0.03), vec2(.004, .03), .003), min(sbx(p+vec2(-.125,0.045), vec2(.007, .03), .003),
    sbx(p+vec2(-.095,0.045), vec2(.007, .03), .003)));
    return abs(max(-oo,ii) * 2.) - 0.001;
}

float cov(vec3 p)
{
    p.z -= 0.02;
    return max(cyl(p, .95, .001), -cyl(p, .2, 0.01)); 
}

//////////////////////////////////////////////////////////////////////////////
//Rendering functions
//////////////////////////////////////////////////////////////////////////////

mat2 rot(float a)
{
    float ca = cos(a), sa = sin(a);
    return mat2(ca, -sa,sa,ca);
}
vec2 ss(vec3 p)
{
    p.xz *=  rot(time);
    p.yz *= rot(radians(35.));

    //create background 
    vec2 res = vec2(abs(length(p)-50.) -.5, BACK);

    //add cd
    float cc = cd(p);
    res.x = min(cc, res.x);

    if(cc == res.x) res.y = CD;

    //add cover
    float cv = cov(p);

    res.x = min(res.x, cv);
    if(res.x == cv)
        res.y = COVER;

    return res;
}

//raytrace
vec2 rt(vec3 ro, vec3 rd, out vec3 p)
{
    vec2 dl = vec2(0);
    float ll = 0.;
    for(int i = 0 ; i< 255; i ++)
    {
        p = ro + rd * ll;
        dl = ss(p);
        ll += dl.x;

        if(dl.x < 0.001 || ll > 100.)
            break;
    }

    return dl;
}

//normal
vec3 nn(vec3 p)
{
    vec3 n = vec3(0);
    for(int i = min(iFrame, 0); i < 4; i ++)
    {
        vec3 e = 0.5773* (2.0*vec3((((i+3)>>1)&1),((i>>1)&1),(i&1))-1.0);
        n += e*ss(p +e*0.0001).x;
    }
    return normalize(n);
}

mat3 cam(vec3 p, vec3 la)
{
    vec3 ww = normalize(la - p);
    vec3 uu = normalize(cross(ww, vec3(0,1.,0)));
    vec3 vv = cross(uu,ww);
    return mat3(uu,vv,ww);

}

vec4 gc(vec2 u)
{
    //create ray info
    vec3 ro = vec3(.3, 0, -2.5), rd = normalize(vec3(u, 2.));
    vec3 rod = ro;

    rd *= cam(ro, vec3(0,0,0));

    //ro.yz *= rot(radians(35.));
    vec3 p;
    vec3 n = nn(p);
    //get ray march data
    vec2 rm = rt(ro,rd, p);
    vec3 lp = vec3 (.31,1,-6), ld = normalize(lp-p);
    float spec = pow(max(dot(reflect(ld, n), rd),0.),200.); 


               //get relative uv coords
#ifdef ALT_COVER
        vec2 uv = vec2(atan(p.y,p.x)/radians(360.), p.y/.95 + 0.5);
        uv = uv.xx;
#else
        vec3 vp = p;
            //rotate back
        vp.yz *= rot(-radians(35.));
        vp.xz *= -rot(time);
        //apply uv based on dithering
        vec2 uv = vec2(vp.xy) + .5;
        uv /= length(vp - ro) * 2.;
#endif

    //get color based on id
    if(rm.y == CD)
    {
        //get tangent
       vec3 tt = (cross(n,normalize(p.xyz)));
       vec3 col = vec3(0);

       //get colors relative to tangent
       float cthl = dot(ld, tt);
       float cthv = dot(-rd, tt);
       float u = abs(cthl - cthv);

            if(u == 0.)
                return vec4(col,0);
       //start mimicing wave length effect
       for(int i = 1; i <= 1; i ++)
       {
         float ww = u * length(p- ro) * 6./ float(i);
         col += spectral(ww);
       }
       col/= 8.;
       col = clamp(col, 0.,1.);
       vec3 off = texture(iChannel1, uv).rgb;
       return  vec4(0.93) * texture(iChannel0, reflect(rd + off * .044, n)) + vec4(col, 1) ;
    }
    else if (rm.y == COVER)
    {
        //create cover with noise effect.
        return mix(dith(uv), vec4(.2,.5,.9,1) , step(six(uv), 0.001)) * max(dot(-ld, n), 0.) + spec;
    }
    //background
    return vec4(mix(vec4(0.1), vec4(.02),abs(u.y)));
}
void mainImage(out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = (2. * f - r)/r.y;

    c = gc(u);
}

