#define freqStart -1.0
#define freqInterval 0.1
#define sampleSize 0.02           // How accurately to sample spectrum, must be a factor of 1.0


const int rectangles = 10;


const float PHI = (1.0 + sqrt(5.0)) / 2.0; // Φ = Golden Ratio 

float gold_noise(vec2 xy, float seed)
{
    return fract(tan(distance(xy*PHI, xy)*seed)*xy.x);
}


void mainImage(out vec4 fragColor, vec2 fragCoord) 
{
    float intensity = 0.0;
	for(float s = 0.0; s < freqInterval; s += freqInterval * sampleSize) {
		intensity += texture(iChannel0, vec2(freqStart + s, 0.0)).r;
	}
    intensity = abs(intensity);
    intensity = pow((intensity*sampleSize),3.0)*4.0;

    vec4 invertedSquares;
    vec4 squares;
    vec4 blueOutlines;
    vec4 pinkOutlines;
    vec2 res = iResolution.xy;

    for (int i = 0; i < rectangles; i++) {
        vec4 square;
        vec2 uv = abs((fragCoord - vec2(res.x / 3.0 + float((i % 2) * 2 - 1) * iTime * 10.0 + (res.x / 4.0 * gold_noise(vec2(i, i), float(i))), res.y / 3.0 + res.y / 4.0 * gold_noise(vec2(i, i), float(i)))) / res.y * mat2(sin(iTime / (10.0 * gold_noise(vec2(i, i), float(i))) + radians(float((i % 2) * 2 - 1) * 90.0) * vec4(3, 0, 0, 1))));
        square = vec4(1.0 * smoothstep(1.0 / res.y, 0.0,  max(uv.x / PHI, uv.y) - 0.1));
        blueOutlines += vec4(1.0 * smoothstep(2.0 / res.y, 0.0,  max(uv.x / (PHI+0.05*intensity), uv.y) - 0.1));
        pinkOutlines += vec4(1.0 * smoothstep(2.0 / res.y, 0.0,  max(uv.x / PHI, uv.y / (1.0+0.05*intensity)) - 0.1));

        squares += square;
        invertedSquares = abs(invertedSquares-square);
    }

    blueOutlines -= squares;
    pinkOutlines -= squares;
    blueOutlines.r = 0.0;
    pinkOutlines.g = 0.0;
    vec4 outlines = pinkOutlines+blueOutlines;
    fragColor = mix(invertedSquares, outlines, outlines.a);
    //fragColor = outlines;

}

