////////////////////////////////////////////////////////////////////////////////
//
// Cyclides, Matthew Arcus, 2022.
//
// A Dupin cyclide is essentially an inverted torus and like a torus
// has an implicit quartic equation. By moving the projection point
// to be orthogonal to the projection direction, the cubic term in the
// quartic disappears and we can directly use Descartes method for
// solving a depressed quartic. As usual this requires solving an
// auxiliary cubic and we do this with Kahan's semi iterative method -
// this has the great advantage that we avoid use of potentially
// inaccurate trigonometry functions.
// 
// Mouse rotates. <up>/<down> to zoom.
//
// Click & drag red blob in crosshairs to change cyclide parameters.
//
// 'z' hides controls
// 't' texturing
// 'r' controls autorotation
// 'h' 'horn' cycle (parameter d = c = a²-b²)
// 's' 'spindle' cyclide (parameter d = 1.0).
// 'b' show where biquadratic solver is used
//
////////////////////////////////////////////////////////////////////////////////

float sgn(float x) {
  return x < 0.0? -1.0: 1.0; // Return 1 for x == 0
}

int quadratic(float A, float B, float C, out vec2 res) {
  float x1,x2;
  float b = -0.5*B;
  float q = b*b - A*C;
  if (q < 0.0) return 0;
  float r = b + sgn(b)*sqrt(q);
  x1 = C/r;
  x2 = r/A;
  res = vec2(x1,x2);
  return 2;
}

// The Kahan algorithm, for explanation see:
// https://people.eecs.berkeley.edu/~wkahan/Math128/Cubic.pdf

void eval(float X, float B, float C, float D,
          out float Q, out float DQ, out float B1,out float C2) {
  float q0 = X;
  B1 = q0+B;
  C2 = B1*X+C;
  DQ = (q0+B1)*X + C2;
  Q = C2*X + D;
}

float qcubic(float B, float C, float D) {
  float X,b1,c2;
  X = -B/3.0;
  float t,r,s,q,dq,x0;
  eval(X,B,C,D,q,dq,b1,c2);
  t = q; r = pow(abs(t),1.0/3.0); s = sgn(t);
  t = -dq; if (t > 0.0) r = 1.324718*max(r,sqrt(t));
  x0 = X - s*r;
  if (x0 != X) {
    X = x0;
    for (int i = 0; i < 4; i++) {
      eval(X,B,C,D,q,dq,b1,c2);
      if (dq == 0.0) break;
      X -= q/dq;
    }
    if (X*X > abs(D/X)) {
      c2 = -D/X; b1 = (c2 - C)/X;
    }
  }
  vec2 res;
  if (quadratic(1.0,b1,c2,res) == 0) return X;
  X = max(X,res[0]);
  X = max(X,res[1]);
  return X;
}

int biquadratic(float p, float r, out vec4 res) {
  if (key(CHAR_B)) assert(false);
  // Solve x^4 + px^2 + r = 0 (ie. a quadratic equation in x^2)
  vec2 t;
  if (quadratic(1.0,p,r,t) == 0) return 0;
  int n = 0;
  for (int i = 0; i < 2; i++) {
    if (t[i] >= 0.0) {
      float x = sqrt(t[i]);
      res[n++] = -x;
      res[n++] = x;
    }
  }
  return n;
}

// Descartes solver for depressed quartics
// ie. x⁴ + px² + qx + r = 0
int dquartic(float p, float q, float r, out vec4 res) {
  if (abs(q) < 0.001) return biquadratic(p,r,res);
  float A = 2.0*p;
  float B = p*p-4.0*r;
  float C = -q*q;
  float U = qcubic(A,B,C);
  assert(U >= 0.0);
  float u = sqrt(U);
  float s = -u;
  float t = 0.5*(p+U+q/u);
  float v = 0.5*(p+U-q/u);
  int n1 = quadratic(1.0,s,t,res.xy);
  int n2 = quadratic(1.0,u,v,res.zw);
  if (n1 == 0) res.xy = res.zw;
  return n1+n2;
}

int cyclide(vec4 params, vec3 q, vec3 r, out vec4 roots) {
  // Cartesian equation for p = (x,y,z), a,b,c,d params:
  // (p.p+b²-d²)² = 4((ax-cd)²+b²y²)
  // Solve for p = q+tr, with r.r = 1.0, q.r = 0.0
  float a = params.x, b = params.y, c = params.z, d = params.w;
  float K = (b+d)*(b-d); float L = c*d;
  // t0 = (q+tr).(q+tr) + K = q.q + K + t^2
  // t0*t0 = (q.q + K + t^2)(q.q + K + t^2) = (q.q + K)^2 + 2t^2(q.q + K) + t^4
  // (a(q.x+tr.x)-L)² = ((aq.x-L)+tr.x)² = t²(r.x)²+2tr.x(aq.x-L)+(aq.x-L)²
  float qq = dot(q,q)+K; // Roll K into qq
  float arx = a*r.x, aqx = a*q.x-L; // Roll L into aqx
  float bry = b*r.y, bqy = b*q.y;
  float C = 2.0*qq - 4.0*arx*arx - 4.0*bry*bry;
  float D = -8.0*(aqx*arx + bqy*bry);
  float E = qq*qq - 4.0*aqx*aqx - 4.0*bqy*bqy;
  return dquartic(C,D,E,roots);
}

vec3 cyclidegrad(vec4 params, vec3 p) {
  float a = params.x, b = params.y, c = params.z, d = params.w;
  float x = p.x, y = p.y;
  float K = (b+d)*(b-d), L = c*d;
  float t = dot(p,p) + K;
  float u = a*x-L, v = b*y;
  return 2.0*(2.0*t*p - 4.0*vec3(u*a,v*b,0));
}

// Lighting
vec3 light = vec3(1,1,-1);
float ambient = 0.4;
float diffuse = 0.6;
float specular = 0.8;
float specularpow = 10.0;
vec3 specularcolor = vec3(1);

vec3 applylighting(vec3 baseColor, vec3 p, vec3 n, vec3 r) {
  vec3 c = baseColor*ambient;
  c += baseColor*diffuse*(max(0.0,dot(light,n)));
  float s = pow(max(0.0,dot(reflect(light,n),r)),specularpow);
  c += specular*s*specularcolor;
  return c;
}

vec3 compute(vec3 color, vec3 q, vec3 r, float tmin) {
  vec4 res;
  // a = b: torus
  // b = 0: tangent spheres
  float a = 1.0; float b = 0.5;
  float d = 2.0*sin(iTime);
  vec2 sel0 = getselection(0);
  b = min(1.0,abs(1.1*sel0.y));
  float c = sqrt(a*a-b*b);
  d = key(CHAR_H) ? c : key (CHAR_S) ? 1.0 : sel0.x;
  vec4 params = vec4(a,b,c,d);
  int n = cyclide(params,q,r,res);
  // Sort results in decreasing order
  if (n > 1) {
    if (res.x < res.y) res.xy = res.yx;
  }
  if (n > 2) {
    if (res.y < res.z) res.yz = res.zy;
    if (res.x < res.y) res.xy = res.yx;
  }
  if (n > 3) {
    if (res.z < res.w) res.zw = res.wz;
    if (res.y < res.z) res.yz = res.zy;
    if (res.x < res.y) res.xy = res.yx;
  }
  // Process from furthest
  for (int i = 0; i < n; i++) {
    float t = res[i];
    //if (t > tmax) continue; // Not needed here, surface is bounded
    if (t < tmin) break;
    vec3 p = q+t*r;
    float phi = atan(p.y,p.x);
    float theta = atan(p.z,sqrt(p.x*p.x+p.y*p.y));
    vec2 uv = 4.0*vec2(phi,theta)/PI;
    vec3 n = cyclidegrad(params,p);
    if (dot(r,n) > 0.0) n = -n; // Face forwards
    n = normalize(n);
    vec3 basecolor = pow(abs(n),vec3(1.0));
    if (!key(CHAR_T)) basecolor *= texture(iChannel0,uv).xyz;
    if (min(abs(p.x),min(abs(p.y),abs(p.z))) < 0.01) basecolor *= vec3(0.5);
    vec3 c = applylighting(basecolor,p,n,r);
    color = mix(c,color,0.5);
  }
  return color;
}

vec2 rotate(in vec2 p, in float t) {
  return p * cos(t) + vec2(p.y, -p.x) * sin(t);
}

vec3 transform(in vec3 p) {
  vec2 s = defaultselection();
  {
    float theta = s.y*PI;
    float phi = s.x*PI;
    p.yz = rotate(p.yz,theta);
    p.zx = rotate(p.zx,-phi);
  }
  if (!key(CHAR_R)) {
    p.yz = rotate(p.yz, 0.1*iTime);
    p.zx = rotate(p.zx, 0.222*iTime);
  }
  return p;
}

void mainImage(out vec4 fragColor, vec2 fragCoord) {
  vec2 uv = map(fragCoord);
  float camera = 4.0;
  camera *= exp(0.1*float(keycount(KEY_DOWN)-keycount(KEY_UP)));
  vec3 p = vec3(0,0,-camera);
  vec3 r = vec3(uv,2);

  light = transform(light);
  light = normalize(light);
  p = transform(p);
  r = transform(r);
  r = normalize(r);
  float t = -dot(p,r);
  vec3 color = vec3(0);
  if (!key(CHAR_Z)) {
    float px = fwidth(uv.x);
    vec2 d = abs(uv-getselection(0));
    color = mix(vec3(1),color,0.95+0.05*smoothstep(-px,px,min(d.x,d.y)-0.005));
    color = mix(vec3(1,0,0),color,0.6+0.4*smoothstep(-px,px,length(d)-0.02));
  }
  vec3 c = compute(color,p+t*r,r,-t);
  c = pow(c,vec3(0.4545));
  if (alert) c.r = 1.0;
  fragColor = vec4(c,1);
}

