float box(vec2 v, vec2 b)
{
    vec2 q = abs(v) - b;
    return length(max(q,0.)) + min(max(q.x, q.y),0.);
}
mat2 rot(float r)
{
    float cr = cos(r), sr = sin(r);
    return mat2(cr, -sr, sr, cr);
}
float cinco(vec2 p)
{
    //offset
    p.y-= -0.25;

    //get bounds of the v
    float bb = box(p, vec2(1));

    //get 
    p.x = abs(p.x);
    p *= rot(radians(25.));
    float res = max(box(p - vec2(0.,0), vec2(0.1,2)), bb);
    return abs(res);
}
float ss(vec3 p) 
{
    p.z = mod(p.z + 7.,14.) - 7.;
    //get 5
    float cc = cinco(p.xy);

    ///extrude it
    vec2 w = vec2( cc, abs(p.z - .6));
    return min(max(w.x, w.y), 0.) + length(max(w,0.));
}

vec3 rt(vec3 ro, vec3 rd, out vec3 p)
{
    vec3 dl = vec3(0, 0, 1000.);

    for(int i = 0; i < 200; i ++)
    {
        p = ro + rd * dl.y;
        dl.x = ss(p);
        dl.z = min(dl.x, dl.z);
        dl.y += dl.x;
        if(dl.x < 0.001 || dl.y > 100.)
            break;
    }
    return dl;
}

vec4 gc(vec3 ro, vec3 rd)
{
    vec3 p;
    vec3 r = rt(ro, rd, p);
    float d = r.z;

    p.z = (mod(p.z + 7.,14.) - 7.);

    float atten =  1. + d*d * 1.4 + d * 5.;
    return vec4(1./atten)+ max(vec4(asin(p.x + p.y +p.z)) * 0.2, 0.); 
}
void mainImage(out vec4 c, in vec2 f)
{
    vec2 r = iResolution.xy, u = (2. * f - r)/r.y;
    vec3 ro = vec3(0,0,iTime), rd = normalize(vec3(u, 4.)), p;
    c = gc(ro,rd);
}

