}
float wop(float t,float tm,float bps) {
    t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),13.);
    return t;
}
float wop1(float t,float tm,float bps) {
    t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),5.);
    return t;
}
float bong(float t,float tm,float bps) {
    t*=exp(-4.0*fract(tm*bps));
    //t *= pow(exp(-4.*abs(fract(-tm*bps-0.15)-0.5)),13.);
    //t*=exp(-24.0*fract(-tm*bps+0.7));
