    //t *= 1.-pow(1.-tc,2.);
    //t *= 22.;
    //t *= 0.;
    return t;
}

float shep(int ste,float time) {
    float f = 40.;
    float s = 0.;
    float bps = 0.5;
    for (int i=0;i<ste;i++) {
        //bps *= 2.;
        s += tone(time,f);
        s += tone(time,f+bps);
        bps *= 1.05+sin(time);
        f *= 1.+fract(time*0.01+float(i*90)/13.);
        //n += time*0.1;
        //n = fract(n*12.)/12.;
        //s += bong(tone(time,scale(n)),time,bps)*am;
        //am *= 0.88;
